INSERT INTO `tp_message` (`id`, `title`, `time`, `content`, `author`, `cid`, `hit`, `top_img_id`, `collect`) VALUES
(81, '沙溪镇龙头环&quot;三月三&quot;', '2017-09-19', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505780595140395.jpg" title="1505780595140395.jpg" alt="20150320115530857.jpg"/></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">沙溪镇龙头环村民每年农历三月初三会在北帝庙前举行隆重仪式进行拜祭，为庙内菩萨重贴金箔并且设坛奠土，当地村民称之为&quot;打醮&quot;。村民信奉北帝，道光年间就在村里建了座北帝庙，奠土一甲子谓之&quot;罗天大醮&quot;，北帝出巡，村民会组织各种艺术表演酬谢神灵并烧纸钱祭奠孤魂，仪式从早上6 点到晚上11 点结束。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">在珠三角的众多乡村至今都保留着&quot;三月三，北帝诞&quot;的风俗，尤其是在隆都地区，但各个村之间习俗有细小差别，在云汉村的&quot;三月三&quot;，村子就会呈现一片祥和瑞气和歌舞升平的景象，唱戏活动等连续举行七天七夜，甚是热闹，村民称之为&quot;七昼连宵&quot;。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">&quot;二月二，龙抬头；三月三，生轩辕&quot;，三月三这个日子，真是生得巧妙，它是轩辕黄帝的诞辰日，是王母娘娘开蟠桃会的日子，也是道教真武大帝(玄武大帝又称北帝)的寿诞。在沙溪隆都地区，村民在这一天拜的就是后者玄武这位道教的神仙。 清晨六点，薄雾轻洒乡间，龙头环的村民们已经开始忙碌了起来。村内的主干道上，由村民自己组织的艺术表演巡游队伍在初春的寒风中瑟瑟发抖，队伍延伸的尽头就是拥有近200 年历史、道光辛巳年间建成的北帝庙。 手持&quot;肃静&quot;、&quot;回避&quot;等牌匾的对襟小伙，把巡游的队伍瞬间拉回到百年前。行走期间，若不是道路旁现代住宅提醒着时代的进程，在现场会有时空回溯之感。</span></p>', 'admin', 2, 5, '86', 0),
(60, '陕西 杨家沟村', '2017-09-18', '<p><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">杨家沟革命旧址</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">杨家沟村地处陕西榆林市米脂县城东南的二十五公里处，清朝乾隆年间马氏家族迁居于此，清同治年间始营造扶风寨，私宅为窑洞式四合院。扶风寨有寨墙、寨门，寨内有大量私宅，均以窑洞为主、依山而建。杨家沟革命旧址为陕西省爱国主义教育基地和影视拍摄基地。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">现存杨家沟革命旧址，毛泽东、周恩来旧居、十二月会议旧址、高级军事会议旧址，有杨家沟革命纪念馆。当年毛主席在杨家沟村主要召开了“十二月会议”。当时主席在这个会议上做了题目为“目前形势和我们的任务”这个报告。当时主席在会议上向全党发出了伟大的号召，曙光就在前面，我们应当努力。这个会议在打到蒋介石反革命统治集团，在建立新民主主义中国时期内，在政治、军事、经济都带有纲领性的。后来周恩来曾在书里写过，主席在世界上最小的司令部里指挥了世界上最大的人民解放战争，就指的是这样的小会议室。</span></p><p><br/></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">当年部分党的领导人住过马氏庄园院子大门.</span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170918/1505740981917437.jpg" title="1505740981917437.jpg" alt="20150410025432692.jpg"/></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">当年部分领导人旧居。</span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170918/1505741000985427.jpg" title="1505741000985427.jpg" alt="20150410025432472.jpg"/></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">俯瞰杨家沟村。</span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170918/1505741031607006.jpg" title="1505741031607006.jpg" alt="20150410025434565.jpg"/></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">纪念馆。</span></p>', 'add', 1, 2, '63', 0),
(8, '赏石潭油菜花梦幻之旅游船、徒步两日游', '2017-09-18', '<p><strong></strong></p><p><strong>石潭</strong><br/><br/>石潭春天是最美的季节，山不在高，有云则仙，每年都有大量的摄影爱好者来此创作。<br/><br/>活动行程:<br/><br/>第一天<br/>7：00屯溪集合上车向雄村进发 游览被誉为“新安第一岛，<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">徽州</a>最雄村”。这里的桃花、梨花、油菜花以及巍峨壮观的 “四世一品坊”、“大中丞坊”、竹山书院、慈光庵、桃花坝等古迹沉淀着雄村古老深厚的文化内涵在这个春天里美得让人心醉。<br/>11:00向美丽的新安江山水画廊进发。<br/>12：00到达深渡码头中饭后<br/>13:00准时坐上游船，沿新安江而下,沿江边风光秀丽，放眼望去，两岸布满了金黄的油菜花，沿途将穿过多个徽派古村落，体验新安江畔的风土人情,感受新安江十里画廊的美景<br/>16；30可以游览昌溪风景区（门票自理）<br/>18：30到达石潭下汰徽毛驴<a href="http://www.goodzhen.com/html/product/kezhan/" target="_blank" class="keylink">客栈</a>安排住宿吃饭,然后大家可以在村里自由活动<br/>晚上,晚餐后杀人的杀人，也可以搞个小型的篝火烧烤晚会。<br/>第二天爱拍照的可以再早点起来，到山头上拍日出，日出的时候会有云海的喔<br/>6：00在领队带领走石潭最美的油菜花路线了（下汰--湖山--北山--石潭村）<br/>12：00一路走走逛逛，回到歙县吃午饭<br/>13：:00 可以游览歙县<a href="http://www.goodzhen.com" target="_blank" class="keylink">古城</a>风景区<br/>或者回屯溪逛逛老街。</p>', 'admin', 4, 8, '9', 0),
(9, '抚顺新宾清永陵', '2017-09-18', '<p><img src="/ueditor/php/upload/image/20170919/1505828759652447.jpg" title="1505828759652447.jpg" alt="u=2727622290,1582660757&amp;fm=27&amp;gp=0.jpg"/></p><p>清永陵位于辽宁省抚顺市新宾满族自治县永陵镇西北起连山脚下，是努尔哈赤六世祖、曾祖、祖父、父亲、伯父、叔父的陵园，可称的上是清代皇家的祖坟。辈分位居关外三陵之首（另外二陵是座落在沈阳的福陵和昭陵）。</p><p><br/></p><p>清永陵位于辽宁省抚顺市新宾满族自治县永陵镇西北起连山脚下，是努尔哈赤六世祖、曾祖、祖父、父亲、伯父、叔父的陵园，可称的上是清代皇家的祖坟。辈分位居关外三陵之首（另外二陵是座落在沈阳的福陵和昭陵）。永陵始建于1598年，清天聪8 年（1634年）称兴京陵，顺治16年（1659年）尊为永陵。清永陵不仅以神奇的风水，丰富的内涵，重要的价值闻名于世，而且在建筑形制、布局、造型、工艺上都有自己的建筑特点和艺术特色。<br/><br/> 清永陵北依启运山，南临苏子河，对恃烟筒山，堪舆家称之为神奇的风水宝地。清永陵二○○四年与沈阳的清代建筑故宫、福陵和昭陵（称为一宫三陵）一起被列入世界文化遗产名录。<br/><!--内容关联投票-->						 </p><div class="text-c" id="pages"></div><p style="margin-bottom:10px"><br/></p><p><br/></p>', 'admin', 1, 14, '10', 0),
(10, '春游黄山、皖南古村、婺源攻略', '2017-09-18', '<p><strong></strong></p><p><strong>活动行程：</strong><br/><br/>第一天：屯溪老街<br/><br/>夜游“活动的清明上河图”屯溪宋城老街。<br/><br/>第二天：屯溪-60KM-黟县-西递-40KM-卢村（农家乐或客栈标间）<br/>早餐后驱车前往桃花源里人家，一路上的徽州马头墙，遍野的油菜花，多么的惬意，游览西递古村，感受几百年的徽州文化传承。下午前往梯田式的油菜花地卢村狂拍，欣赏徽州最精美的木雕，宿汤口。<br/>关键词：徽州、文化、油菜花，古村<br/>D3黄山精华景区-20km—汤口—60KM-市区住宿（住市区）<br/>早起前往五岳归来不看山的黄山，赏黄山春色。游赏黄山最精华的景区，下午下山后前往市区住宿，游览屯溪老街，品地道的徽州名菜，吃当地特色小吃。<br/>关键词：黄山，奇松，怪石，宋城老街，小吃，徽菜<br/>D4黄山-96KM-婺源离开<br/>早起，行车1小时到达婺源，行摄月亮湾（沙洲如宁静在春水里的一弯新月，加上渔舟的点缀，更有一番江南柔美的风味【月亮湾摄影基地】（约1小时）；李坑景区，游览小桥流水人家1.5小时，【汪口】（游览约1小时）古山林、古埠头、古商业街、古巷、古祠堂和散落在十八条古巷中，可以在晓起用中餐，晓起（以画里乡村见称；特别是去上晓起的石板路上拍摄花海里的村落）；江岭（暮色中花海无边,淡淡的炊烟让您不能释怀）。游览万亩梯田油菜花江岭景区2小时后，\n 结束愉快旅程。返回温馨的家。在欢声笑语中结束您的传奇之旅。<br/>关键词：最美的乡村 油菜花<br/><br/>活动参考费用：758 元/人<br/><br/>门票：西递104元 卢村木雕楼50元 婺源210元<br/>住宿：推荐黟县和宏村周边同等档次的特色客栈，如果住市区可观赏黄山徽韵演出；<br/><br/>黄山物价比较高，山顶餐建议自带。<br/><br/>黄山<br/><br/>黄山位于<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">安徽</a>省南部黄山市境内，原名黟山，因峰岩青黑，遥望苍黛而名。后因传轩辕黄帝曾在此炼丹成仙，唐玄宗信奉道教，故于天宝六年改为“黄山”。明朝旅行家、地理学家徐霞客两次登临黄山，赞叹说：薄海内外，无如徽之黄山。登黄山，天下无山，观止矣！后人引申为“五岳归来不看山，黄山归来不看岳”。有人评黄山有“泰岱之雄伟、华山之险峻、衡岳之烟云、匡庐之飞瀑、雁荡之巧石、峨嵋之清秀”。并被世人誉为“天下第一奇山”。黄山1982年入选第一批国家重点风景名胜区；1986年黄山被评选为中国十大风景名胜之一，且是中国十大风景名胜中唯一的山岳风景区；1990年12月黄山风景名胜区作为一项文化与自然双重遗产被联合国教科文组织列入世界遗产名录；2004年2月入选世界地质公园。<br/><br/>婺源<br/><br/>婺源的自然风光将会向你诠释什么才是“乌托邦”式的理想家园：被淡淡炊烟笼罩的马头墙、山间梯田抽象的线条、暮色中骑在牛背上的村童、幽静的孤舟野渡、倒映在清澈山涧里的金黄的油菜花……让人陡然间心静如水，恍若隔世。<br/><br/><br/>提示：<br/><br/>1、穿着适合户外，穿登山鞋或者徒步鞋，最好带换洗衣物。带2瓶水，干粮，零食，有相机、手台的带上。<br/>2、出游活动，可以让整个心胸都释放，但要牢记安全第一！<br/>3、沿途不要丢不可降解的垃圾。<br/><br/>皖南看花注意事项：<br/>1：雨水较多，注意防寒防潮。<br/><br/>2：旅游旺季吃住行费用上涨，最好提前预定。<br/><br/>3：以上的部份景点交通不便，注意不要误了归程车次。<br/><br/>4：风景太美，相机内存卡和胶卷、电池要带足。<br/><br/>5：自驾注意行车安全、道路条件、燃油的补充。<br/><br/>6：徒步者最好找老乡带带路，不用担心迷路，还可听听乡民诉说的风景，最重要的是安安全全。<br/><br/>7：户外旅行带上应急用品如：药品等。<br/><br/>8：一定要早起可以拍上两张炊烟袅袅的山村人家的好照片。</p>', 'admin', 4, 18, '11', 0),
(11, '三月到宁静典雅的徽州古村落南屏', '2017-09-18', '<p><strong></strong></p><p><strong>南屏春色<br/><br/>一、特色：</strong><br/>黄山市黟县南屏景区：古朴、纯正的徽州古村落，带你远离城市的喧嚣，找到了世外挑源。在这文化底蕴浓厚的村庄里：<br/>1、感受古徽州火桶取暖<br/>2、徽州“锡格子茶”待客、红纸包赐元宝长寿面<br/>3、体验农家菜地采摘<br/>4、品尝地道农家有机菜<br/>5、体会古代运输工具“独轮车”<br/>途径路线是古徽商出去经商必经之道，有历史的痕迹。<br/><strong>二、行程时间</strong>：一天<br/>早上抵南屏村<br/><br/>锡格子茶”品味徽州年味<br/>这里有热情的南屏村导游和村民的迎接带领，去往老叶家（菊豆饭店）。感受徽州待客礼仪“锡格子茶”，尝徽州独特茶点，品地道毛峰茶，稍作歇息后然后开始准备自己新春异地风情的第一顿本土特色美食！<br/><strong>农活体验</strong><br/>内容：<br/>农家菜园采摘食材 （采摘、溪边清洗、分类）<br/>院外劈材备灶火 （农夫劈材堆灶前）<br/>体验灶台烧材火 （配合辅助农家炒菜）<br/>巷口三元井提水 （打水入缸食用、清洗食材）<br/>清洗 （清洗后上锅）<br/>动手做饭：<br/>可以自己亲自体验材火灶、菜籽油炒出来的有机菜<br/> 徽州过年重头戏、大团圆的聚餐，在天井下、在老房子里感受浓浓年味<br/>菜品：南屏草鸡养生汤、竹海冬笋煲土鸭、有机青菜烩冬菇、徽州腌鲜鳜鱼美、拜年萝卜南湖肚、腊八豆腐炒西芹、皖南花猪豆角包、农家香肠蒸双白、生煎古筑毛豆腐、关麓农家一品锅、黟县宏村丰收乐等丰盛特色菜肴，真正体验一次舌尖上的徽州美味！<br/>深度品味厚重恬静的南屏，迷一般的南屏等着您解读。建议穿上红棉袄，在巷子中穿梭，拍的照片很穿越。<br/>由南屏景区导游带领大家参观南屏景区，给客人做讲解介绍。<br/>南屏是一座有千年历史、规模宏大的徽商聚居古村落。完整保存着八座古祠堂、近三百幢明清古建筑，有气势恢宏、庞大的祠堂结构；风格各异、端庄典雅的各类古私塾；村内七十二条古巷，纵横交错于高墙深院之间，如同迷宫，充满神秘色彩；三十六眼古井或圆或方，井水清澈晶莹，村口“万松林”古树参天、古桥静卧。<br/> 体验活动推独轮车活动开始，活动讲究平衡技巧、分组比速度。<br/>在“万松林”边上还准备了古徽州运输的交通工具“独轮车”，观赏古徽州文化的同时，体会厚重的古代生活。<br/><br/><strong> 南屏出发上梓路寺，梓路寺祈福</strong><br/><br/>位于宏村西南方向3公里处的梓岭山间，背靠象鼻峰，前倚奇墅湖三千亩水面，山明水秀，风光如画，元气融结。始建于唐会昌3年（公元843年），2003年盛世重建，新建梓路寺保留唐风徽韵，粉墙黛瓦，古朴恢宏，蕴华独具。寺庙分为宗教生活区、禅修区和学苑区三大部分；共有山门殿、天王殿、钟楼、鼓楼、大雄宝殿、接引殿、药师殿、方丈室、大寮、云水寮等，建筑面积11780㎡。梓路寺面向社会大众，以简单、直接、实在的禅修方法引导一切有情众生，净化心灵烦恼、提升内在品质、体悟生命实相、安享清凉本来，从而成就人间的佛国净土，为大众营造一方清凉的心灵家园<br/>到奇墅中坤酒店入住<br/>18:00 晚餐，奇墅中坤酒店过年宴<br/><br/><strong>三：价格：</strong><br/>南屏门票30一人<br/>奇墅中坤酒店别墅楼标间360元/间<br/>锡格子茶：20元/人/次<br/>农家土菜：120元/人（含茶水、茶叶蛋等）<br/>独轮车：150元/辆<br/>\n				\n				        </p>', 'admin', 4, 1, '12', 0),
(12, '春日黄山脚下观花两日游', '2017-09-18', '<p></p><p>初春，染指流年，听一地花开。一袭千娇百媚、袅袅婷婷 任由洒落一地的思念。扬起幸福欢愉的容颜 伫立 等待 凝望 眷恋......只为你守候：那一朵、那一束，那一片——花海无边。<br/><br/>这个三月的油菜花……恋爱了。<br/><br/><br/>理由1：西递最好的季节是油菜花季，带上长枪短炮不要偷懒，待到早起炊烟升起时一定能拍个好照片。徽派古镇主要看白墙黑瓦、水墨意境，没有江南小镇的流水荡舟意境悠远，但自有其宁静安详之处。走在石板路上，随沟渠的流水而行，两边白墙耸立，置身其中不用去想世俗的烦忧。<br/><br/>理由2：卢村古树林立，春天油菜花海，雨后云雾澎湃，而晴朗日子里的傍晚红霞满天，与鳞次栉比的古村落相 辉映，气势宏伟。<br/><br/>理由3：木坑以竹海而著名，地处桃源山村东大门，为黄山入黟第一寨。木坑竹海又称“滴翠谷”，<br/><br/><br/>理由4：清晨，山岚弥漫乡野，丛树遮掩的村子被抹上一层白晕，村后紫黛色的峰峦，隐约可辨，仿佛一幅泼墨图卷。傍晚，袅袅炊烟依偎着村子，牧童骑着老牛、打着唿哨、披一身灿烂的霞辉、悠悠然走入画中来。飘洒几分自然朴素的田园诗意。<br/><br/>理由5：屏山带您感受江南水乡“小桥流水人家”特有的风韵。<br/><br/>理由6：探徽州底蕴、观宏村阿菊。<br/><br/><br/>行程线路<br/><br/>第一天<br/><br/>上午：西递景区<br/>观：徽州教育文化 寻：桃花源里人家 鉴：徽州建筑群落 品：民风民俗天下<br/>下午：卢村景区、屏山景区<br/>漫步花海丛中，追寻徽州梦境，找、一片安逸、寻、一段清雅幽静 叹为观止的木雕工艺，传承千年不朽的徽派神话<br/>晚上：宏村阿菊（大型水幕盛宴）<br/>梦幻的色彩带您走进神秘的古徽州、扣人心弦的故事陪你穿越那四季的春秋。<br/>第二天<br/>上午：塔川景区 木坑竹海景区<br/>塔川的花海、木坑的竹，身在其中，拾落心情，留下最美的你，带走最亮的景，这个三月，我们期待的是——油菜花的爱情<br/><br/><br/>线路难度：★★<br/>路线强度：★★<br/>风景指数：★★★★★<br/><br/><br/>各景区介绍<br/>西递<br/><br/>西递是徽州最具代表性的古民居旅游景点，坐落于黄山南麓，距屯溪54公里,距黄山风景区仅40公里，距黟县县城8公里。该村东西长700米，南北宽300米，居民三百余户，人口一千多。因村边有水西流，又因古有递送邮件的驿站，故而得名“西递”，素有“桃花源里人家”之称。<br/>西递位于黄山市黟县东南部的西递镇中心，村落面积12.96公顷，乃取村中三条溪水东向西流之意；又因位于徽州府之西，曾设“铺递所”，故改名西递。它始建于北宋\n \n皇佑年间，发展于明朝景泰中叶，鼎盛于清朝初期，至今已近960余年历史。据史料记载，西递始祖为唐昭宗李晔之子，因遭变乱，逃匿民间，改为胡姓，繁衍生息，形成聚居村落。故自古文风昌盛，到明\n \n清年间，一部分读书人弃儒从贾，他们经商成功，大兴土木，建房、修祠、铺路、架桥，将故里建设得非常舒适、气派、堂皇。历经数百年社会的动荡，风雨的侵袭，虽半数以上的古民居、祠堂、书院、牌坊已毁，但仍保留下数百幢古民居，从整体上保留下明清村落的基本面貌和特征。至今尚保存古朴典雅的明清民居中124幢，大量的砖、木、石雕等艺术佳作点缀其间。目前，整理开放有凌云阁、胡文光刺史牌坊、瑞玉庭、桃李园、东园、西园、大夫\n 第、敬爱堂、履福堂、青云轩、膺福堂、笃敬堂、仰高堂、尚德堂、枕石小筑、仁堂、追慕堂等民居古建筑。<br/><br/>卢村<br/><br/>卢村.木雕楼位于黟县北部，距宏村仅二公里，是以卢姓为主聚居的古村落。卢村以规模宏大、雕刻精美的木雕楼群而著称，木雕楼享有中国木雕第一楼之誉。<br/>　卢村靠山临水，村西小溪名下门溪，村东小溪称前街溪，至村南汇合而成丰栈河。村东民宅依溪而建，临水一侧多挑出，建有敞廊，别有一番情趣。一级级青石抬阶，一座座小木桥，使人感觉身处山村，却又似在水乡。村口有石桥，名驷车桥，始建于宋。桥的命名，是由于宋代卢村出了一位显赫的人物卢臣忠。古人认为，驷马高车，非显贵者不得乘坐。卢村人以“驷车”命名桥是为了显示卢氏家族的高贵。村中有一水圳，是人工修筑的沟渠，它从村西的下门溪横穿村中，在村中呈弯曲蜿蜒状，使水圳碧水淙淙，更显活泼，由村东头出，接上前街溪。这是徽州古村落注重水的活用的实际例证。<br/>卢村最具典型、最有特色的是木雕楼，它体现出徽派民居的精华。卢村木雕楼是由七家里民居组成的木雕楼群，主要包括志诚堂、思济堂、思成堂、玻璃厅等宅院。<br/><br/>木坑竹海<br/><br/>木坑竹海位于塔川东南1.5公里，距黟城北面15公里，距宏村4公里。漫山的竹林，郁郁葱葱，且前往游玩的游客不多，非常地安静，让人心旷神怡。在气候条件好的情况下，还可见到类似于黄山云海般的景致。\n \n是卧虎藏龙竹林打斗地的拍摄地。木坑以竹海而著名，地处桃源山村东大门，为黄山入黟第一寨。木坑竹海又称“滴翠谷”，此谷纵深约６公里，四面环竹，郁郁葱葱，形如金斗，聚财不漏，风水甚佳。雨后观景，翠竹深处，白云人家；晴观日出，有如黄山云海，翠浪波涛；谷中赏月，仿佛天上人间。<br/><br/>塔川<br/><br/><br/>塔川隶属于黟县际联镇的塔川村，又名塔上。它位于黟县桃花源著名旅游景点--宏村到木坑竹海景点途中，距宏村仅2公里，是黟县小桃源众多美丽富饶的自然村落中一个独具魅力的山间村落。<br/>塔川，背倚高耸云端的黄山西南余脉黄堆山，遥对碧波荡漾的十里奇墅湖。粉墙黛瓦，飞檐翘角的二、三十幢古民居依山而建，层层迭迭，错落有致，远远望去，就好像一座巨型宝塔，藏身在山谷之间，掩映在浓荫丛中。<br/>塔川，高庵与低庵两峰之间（因峰之巅，原有西林寺禅院而得名），一条清溪破土而去，依山潺潺流下，穿村中而过，直奔风我秀丽的奇墅湖。村庄因有“川”过而富有无穷灵气，清溪也因穿“塔”而更显活力。塔川，即以此而旬名。<br/><br/>屏山<br/><br/><br/>屏山位于<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">安徽</a>省黄山市黟县县城东4公里的青阳山下，因村北向有山状如屏风，屏山因而得名，又因古时建创曾属黟县九都，故又名九都舒村。<br/>屏山是以舒姓聚族而居的村落。明清鼎盛时期，村内有12条街、60条巷、24口井、18幢祠堂、16座牌楼、400多幢民居，号称“八百烟灶、三千丁男、五里长街”的第一大村。由于种种历史原因，破坏比较严重，但村中至今仍保存有光裕堂、成道堂等7座祠堂，其中舒庆余祠堂是中国皖南规模最大的明代宗族祠堂，存有古民居200余幢，水井多口。村中的吉阳水穿村而过，八座古桥连接着一装幢幢古明居，构成一幅绝妙的“小桥、流水、人家”的风水古村落图画。屏山是著名人民表演艺术家舒绣文的故乡，因成功主演《一江春水向东流》等影片受到周恩来总理的接见。<br/><br/>阿菊<br/><br/>《宏村·阿菊》以古<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">徽州</a>文化为前景，艺术再现<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">徽州</a>女人贤惠、勤劳、持家、教子、耕耘劳作以及忠贞如一、保卫家园的新形象、新身份。全剧融入多种全新的剧场艺术元素，气势恢宏，新锐震撼。高空特技、高台跳水、时尚跑酷、水上摩托艇等多种新奇表演纷呈并现，电影特技、水火特效场景、3D立体成像技术等现代科技手段变幻迭出，营造出强烈的视觉效果和艺术冲击力。</p>', 'admin', 4, 0, '13', 0),
(13, '浙江郭洞镇', '2017-09-18', '<p></p><p>初到郭洞，见到村口水口边的桥下有几个村妇字溪边认真地敲打着衣物，让您享受到的这里就是乡土生活气息和乡土生活美景。郭洞边上的龙山不高，是郭洞村的制高点，但是爬起来有点危险，可能这里就是原始森林吧。村内的狗很多，但每一只都很温顺，不会莫名的乱叫。村附近有一个水库，叫做大湾湖，湖很小，可以自己划竹筏，周围的景色是典型的中国水墨画，静谧而充满情趣，颇有一些世外桃源的意境。这里的农家餐馆里的竹筒饭、土鸡和玉米都是非常的美味。</p><p><img src="/ueditor/php/upload/image/20170918/1505739205107119.jpg" title="1505739205107119.jpg" alt="20120904022435466.jpg"/></p><p><br/></p><p>古镇第一井</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739216468314.jpg" title="1505739216468314.jpg" alt="20120905053932763.jpg"/></p><p>住在郭洞村</p><p>郭洞村内有一些条件比较好的小客栈，能融郭洞山水，能品郭洞风光，还有好吃的农家菜，住上一两晚，更能体味郭洞的古意幽情。</p><p><br/></p><p style="text-align:center"><img src="/ueditor/php/upload/image/20170918/1505739231920406.jpg" title="1505739231920406.jpg" alt="20120905114241894.jpg"/></p><p>推荐客栈：</p><p>郭洞山水人家饭店 地址：郭洞村大湾湖畔 电话：0579-87736777</p><p>宝泉山庄 地址：郭洞老村内 电话：0579-87736468 简介：这是有老祠堂改建的客栈。</p><p><br/></p><p>吃在郭洞</p><p>在郭洞，竹筒饭是当地最特别的美食，每筒三两，价格五到十元左右。土鸡煲35至50元一份，不过烧起来需要一些时间，建议进景区时先和饭店联系好，出景区时就能吃到。另外，郭洞景区的美食还有武义宣莲羹、武义巷子豆腐等。</p><p><br/></p><p>郭洞购物</p><p><br/></p><p>郭洞和武义的名特产有中国三大名莲之一的宣莲，还有金华火腿、有机茶叶和莹石雕工艺品等，在景区或县城内的超市都能买得到。</p><p><br/></p><p>玩在郭洞</p><p>郭洞节庆活动：元宵灯会，农历正月初十至十六，是当地最具特色的民间艺术娱乐活动，人们通过迎灯以示吉利除瘟，祛灾祈福，以求新的一年里五谷丰登、人畜平安。</p><p><br/></p><p>武义温泉浴吧</p><p><br/></p>', 'admin', 1, 11, '14', 2),
(14, '行游山水间--龙脊梯田与八角寨', '2017-09-18', '<p><strong></strong></p><p><strong>资源八角寨</strong><br/><br/>龙胜的龙脊梯田是这片土地上的一则寓言，桂林山水的美与和谐来自大自然的馈赠和匠心独运，于是山如碧莲水若罗带，水绕山色，山居水上，水和山都出落得倾国倾城。<br/>然而，龙脊梯田打破了这一最为经典的山水组合方式，这里的水和山，如同水乳交融般交错组合，梯田一级级直攀上云雾中的顶峰。日光雪色之下，山与水的合唱如同天籁之音，山的雄灵秀水的温婉在这重重叠叠让人惊叹的清晨梯田上得到了另一种形式的完美体现。<br/>三江得名于境内的三条大江，即榕江、浔江，苗江大小七十四条河流纵横交错，滋养着万物生长也孕育着侗家文化。举世闻名的程阳风雨桥是侗族人的骄傲，向世人展示了侗族人的聪明智慧及精湛工艺，穿过风雨桥，走在侗族村落中，宛蜿蜒曲折的石板小路上，一面是吊脚楼吊脚楼木楼鳞次栉比，一面是稻田生机盎然，八个村寨紧密相连，一路贯通，好一派原汁原味的侗家田园风光！<br/><br/>折返回龙胜，再奔赴资源，中间的山路九曲十八弯，特别是在最后的几十公里，竟然绕出几百个弯道，几乎就看不见直线。据说这是因为当年修路的时候为了不侵占当地珍贵的耕地人们硬是在山腰上凿出了一条路来。<br/><br/>资江是资源的灵魂，这条充满灵气的江流吸引了众多游客前来漂流，正是：水是眼波横，山是眉峰聚，欲问行人去那边？眉眼盈盈处。农历7月12至14日，当地就会举办传统节日“七月半”河灯歌节，以唱歌、放河灯的形式寄托缅怀先人、消灾避祸的情思，人们自发携灯，沿河漂放。资源城的滨江道，成为观赏美景的最好的去处。夜晚，空中焰火似锦,江面万盏河灯齐发，场面蔚为壮观，伴以江面上飘荡的苗族姑娘的山歌成就了一个梦幻般的世界。<br/><br/>此行可以以新安为终点。这里是湖南的母亲河--湘江的发源地，另一条著名的河流--漓江也从此流过。公元前214年，秦始皇挥师50万攻打岭南百越，苦于远离中土，粮草难运，于是令史禄主持修建了中国最早的运河--灵渠.它连通了湘江与漓江，为北方的军事物资运往岭南地区提供了便利。此后，携交通之便利，兴安成为商贾文教重地，水街繁盛一时，漓江书院也因此而立。清代著名诗人苏宗经曾经这样形容水街：“径缘桥底人，舟向市中穿；浆棹挥波易，蓬窗买酒便。”如今，修复过的心安水街保留了大量明清时期的石雕石刻，浓缩了桂北两千多年的历史文化，展现了的秦汉建筑文化的精华。与城市融为一体的水街，杨柳依依，碧水潺潺，成为游人和兴安人休闲游玩的好去处。</p><p><img src="/ueditor/php/upload/image/20170918/1505739286435739.jpg" title="1505739286435739.jpg" alt="20131203101514218.jpg"/></p><p><strong>龙脊梯田</strong><br/><br/>四天时间，漫游桂北四地<br/><br/>第一天<br/><br/>到达龙胜，转车去龙脊梯田，游览大寨或平安寨。<br/><br/>第二天<br/><br/>早晨看日出后回龙胜县城，转车去看程阳风雨桥，逛程阳八寨。晚上再返回龙胜住宿，这一天转车频繁，但好在班次较多，也有私人面包车随时可以出发。<br/><br/>第三天<br/><br/>乘车到资源，资源以八角寨闻名，可乘坐县城至景区的旅游专线车，将近两个小时到达景区门口。攀登八角寨需要一定的体力，好在道路简单，一条上，一条下，主要景点都分布在沿途路边。<br/><br/>第四天<br/><br/>转战兴安，把大部分时间留给乐满地乐园，下午再去灵渠，灵渠公园后门处的那条水街颇有市井风情，也值得一逛。<br/><br/><br/>如果从黔东南前往，前三天可以这样安排：<br/>第一天，从肇兴前往三江，中午到达程阳，游览风雨桥。下午逛程阳八寨，细细品味侗族田园之美，夜宿村里的吊脚木楼。<br/><br/>第二天一早回到三江，乘坐三江到龙胜的班车，每半小时一趟，车程约一个半小时，到龙胜后再转车到龙脊梯田的大寨或平安寨，游览龙脊梯田。<br/><br/>第三天，早晨看日出后回龙胜县城，再转车到资源游览八角寨。</p><p><br/></p><p><strong>龙脊梯田</strong><br/><br/>以下预算以四天时间为标准。<br/><br/>门票大约310元。<br/><br/>交通：区内交通费约200元。<br/><br/>住宿：住宿普通宾馆标准间，按照每天60元计算，四天总计240元。<br/><br/>餐饮：以当地普通饭馆为主，按照每天50元计算，四天总计200元。<br/><br/>行程费用总计约950元。<br/><br/>行程贴士：在龙脊梯田，持学生证购买门票可以享受八折优惠，资源的所有景点和船票价格都可以打折，不过需要找旅行社，一般折扣在八到九折，学生还有优惠。<br/></p>', 'admin', 4, 6, '15', 0),
(15, '浙江前童镇', '2017-09-18', '<p></p><p>前童古镇位于浙东宁海以南14公里的梁皇山下,34省道（甬临线）和同三高速公路由北向南横卧全镇，前童两千多户人家全为“童”姓，给人一种“住的全是亲，来的全是客”的祥和之感。它和大多数江南古镇不同，一条以八卦图形的水渠引白溪之水穿过全镇，家家门前的过溪石板犹如小桥一般，所以前童又有“千桥之镇”的美誉。</p><p><img src="/ueditor/php/upload/image/20170918/1505739311178151.jpg" title="1505739311178151.jpg" alt="20121123050419111.jpg"/></p><p>这里地面铺的是细细碎碎的鹅卵石，特别是镇上主要的房屋都是石头结构的民居建筑，按“回”字九宫八卦式布局而建。古镇随处都可入画，尤其是这里的“静”，能给人一种回到家的感觉，而这种感觉又有点陌生。前童以明清时期的古建筑最多，青藤白墙黑瓦，石头镂花窗户，雕梁画栋门楼，苍凉中显现出曾经的繁华。明代方孝儒在此讲学，前童村民形成了一种“挺身而出”的性格，今人又称之为“前童精神”。电影《理发师》的故事就发生在这些昔日华丽的沧桑老宅之中。前童赋予我们的是它那古老而憔悴的美丽，它也是崇儒重义的别样江南。</p><p><img src="/ueditor/php/upload/image/20170918/1505739315534712.jpg" title="1505739315534712.jpg" alt="20121123054430942.jpg"/></p><p><br/></p><p><br/></p><p><br/></p><p>前童豆制品很出名，常被称作“前童三宝”，其实“前童三宝”只是前童豆制品的三种，即豆腐、空心腐、香干。前童豆制品的品种远不止这三种，常见的还有豆腐皮、烟熏豆腐等，不少于几十种。在前童单用豆制品就能做出一桌色香味俱全的豆腐宴。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739318340811.jpg" title="1505739318340811.jpg" alt="20140306014133789.jpg"/></p><p><br/></p><p>前童豆腐:</p><p><br/></p><p>   白豆腐，一是口感细腻绵滑、营养倍加丰富；二是细若凝脂，洁白如玉，清鲜柔嫩；三是托于手中晃动而不散塌，掷于汤中久煮而不沉碎。其味在清淡中藏着鲜美，吃起来适口清爽生津，可荤可素，历史上曾作为贡品。</p><p><br/></p><p>前童芯腐:</p><p></p><p>   空心豆腐，长圆鼓形、中空，炸成金黄，趁热咬上一口，薄薄的、酥酥的、香香的，撒上椒盐更好吃。</p><p><br/></p><p>前童香干:</p><p><br/></p><p>   香干是豆腐的再加工制品，硬中带韧，咸香爽口；香干在制作过程中会添加食盐、茴香、花椒、大料、干姜等调料，既香又鲜，久吃不厌。可制作多种菜肴，可冷拌，可热炒，可油炸，可烤制，做成后俱鲜香可口，价廉物美，营养丰富，受到人们的喜爱。</p><p><br/></p><p><br/></p><p>前童是未开发的古村落，时间节奏极其缓慢。清明前后镇后是一大片灿烂的油菜花。前童村外兴隆寺、塔山家庙旁是一派安居乐业、疏朗俊秀的田园风光。村外开辟了一大片稻菜腴田，田埂边水车吱呀，一年四季或油菜花黄，或稻谷飘香。尤其在深秋，荷塘月色，蛙声四起，桂影婆娑，周围被清凉的空气包围。古镇的特色是老房子、地上的鹿图案、红砂岩的石花窗及卵石铺成的窄巷，很容易让人亲近。这里家家门前临水、依水、面水。四合院是宁静的，您可以随意推开门进去，若逢主人，打个招呼就可以了。在前童，最好能登上鹿山顶，可观前童全貌。一年中除了夏季台风来袭不太适合游览外，其他时节都可游览。</p><p><br/></p><p><br/></p><p>前童住宿：</p><p>镇上有客栈，也可住村民家，条件尚可，价格30至120元不等。前童距宁海城区较近，约十四公里，那里的宾馆、酒店条件很好。</p><p>前童酒家，地址：前童新镇，电话：057465380191，标准间50-70元左右，推窗可见鹿山秀色。</p><p>吃在前童：</p><p>前童人主要待客点心有鸡子茶、桂圆茶、炒面、豆腐皮、鱼干、咸肉等浇头的汤面和糯米圆、肉麦饼、麦糊头等。</p><p>特色食品有前童豆腐、空心菜、汤包、麦糊头，还有麻糍、麦饼、年糕、椒盐小溪鱼等美味。前童桶饼，可称当地第一美味。</p><p>前童购物：</p><p>前童香干：韧性强，略带甜味。</p><p>十二生肖童鞋、童帽：儿童穿的布鞋、戴的帽子，绣有老虎、白兔等十二生肖。</p><p>笋壳箬扇：用笋壳箬精编而成。扇面有山水花鸟等图案，精致玲珑。</p><p>香袋：用碎布头、笋壳箬、麦秆或茧壳缝制成各种形状的外袋，并采拔野生香草，晒干装进袋内，香气宜人，美观大方。</p><p>方竹紫竹盆景：方竹紫竹为竹族珍品。方竹棱角分明，呈正四边形。紫竹，竹竿乌黑，均为当地特产。</p><p>玩在前童：</p><p>二月：前童元宵鼓亭抬阁行会文化旅游节。</p><p>五月：中国徐霞客开游节。</p><p>九到十月：中国国际梁祝婚俗节。</p><p><br/></p><p><br/></p><p>自驾游指南：</p><p>线路：从北方前往前童，车行杭甬高速公路后往宁波，在宁波东站再转甬台温高速公路南行，到“宁海”出口下，转34省道南行，左转到前童镇。</p><p>从西南方向往前童，车到金华后继续前行转甬金高速公路往东，过奉化后再转甬台温高速公路南行，到“宁海”出口下。</p><p>从南方往前童，沿着甬台高速公路北行往宁波方向，经吴岙枢纽后注意往三门方向，到“岔路”出口下，转往东北方向到岔路镇，前行到梁皇，右转到前童镇。</p><p>线路：从永泰高速公路宁海出口下来后的34省道不宽吗，但非常平坦，此处多丘陵，路面弯道多，但无陡坡，车少行人也少，比较适合行车。</p><p>从梁皇到前童的一小段公路坑洼较多，小车要注意底盘。</p><p>旅游服务：</p><p>甬台温高速公路在此段有宁波服务区、梅林服务区。从宁海到前童的路边都有加油站，可在宁海市内加油或用餐。</p><p>泊车情况：前童镇上不能停车也无法行车，因古镇尚未开放，还没有专门的停车场。在古镇入口处的公路两边可临时停放车辆，一定要靠路边停。如在镇山住宿，在新镇区的小客栈外可以停放车辆。</p><p><br/></p>', 'admin', 1, 15, '16', 1),
(16, '重庆水上门户朝天门', '2017-09-18', '<p><strong></strong></p><p><strong>灯火辉煌，五彩缤纷的朝天门夜景</strong><br/><br/>美丽的重庆水上门户朝天门，襟带两江，壁垒三面，气势雄伟。石壁上，藤萝垂青，黄角树穿岩抱石，绿茵融融。崖边古亭，飞阁临江。两排石阶，比肩而降，至抵下水。据说无论江水怎样枯竭，石级而下，无有穷尽，可通神秘的金竹宫。每值初夏仲秋，嘉陵江水绿，扬至江水黄，两水相交朝天门，撕咬翻卷，流急涡旋，向称“夹马水”，似如野马奔腾。江心有石矶沉浮，相传大禹在古渝州娶涂山氏女，此后治水13年不入家门。涂山氏女伫立矶上，望夫归来，因名“夫归石”。<br/><br/>　　朝天门地扼黄金水道要冲，为重庆主要交通枢纽之一。1949年后，码头一再扩建，沿两江纵深排开。客运大楼拔地而起，客货缆车分道直通江边。两江索道，凌空飞渡，左右穿梭；江面客船货轮，铁驳木舟，鳞次栉比，此静彼动。重庆的繁荣昌盛，在一定程度上，集中表现在水上门户朝天门。<br/><br/>　　朝天门是中、外游人观赏两江环抱重庆半岛，山水相映、百舸争流的好地方。早些时候，朝天们是面朝天子迎接圣旨的地方，现在是俯瞰两江宏伟气势的地方，特别是在夏季，嘉陵江与长江在此汇合，形成了清浊争流的景象。<br/><br/>朝天门为重庆沿江九门之一，位于重庆半岛的东南尖端，低水位沙嘴海拔为160米，是市区最低海拔处。长江的九大主要支流之一的嘉陵江在此与长江汇流，一泻数千里注入东海。重庆是素称“黄金水道”的长江的重要港口之一，是长江上游的经济中心城市。在这里，每天运送数千客人游览壮观的长江三峡，吞吐着万吨货物。<br/><br/>　　朝天门是公元前314年，秦将张仪灭亡巴国后修筑巴郡城池时所建，为历代官接皇帝圣旨的地方。因古代称皇帝为天子，故此而得名。历代古都多建在洛阳，长安（今西安），开封，燕京（今北京），到重庆的最近线路，应该是川陕路，只因当时由四川剑阁、广无至陕西沔县（今成铁路中段，全长710公里）的蜀道，只有鸟道，难于行人。唐代大诗人李白在《蜀道难》一诗中曰：“蜀道之难，难于上青天”。所以，历代帝王派出的使臣才舍近求远，经陕西或河北，河南，再经湖北，在宜昌逆水乘船约需一个月时间到重庆。</p><p>重庆市辖九区九县三个市辖市，长江流经5区2县2市，流经水域248公里，嘉陵江穿过3区2县1市，流经水域137公里，市区三面环水，一面靠陆，长江全长为6380公里，但通航段只有3194公里，逆水而上通航段为795公里，只能行驶小型船只，顺水而下到上海2399公里，到武汉1274公里，到宜宾648公里。著名的长江三峡就在重庆至宜昌航段，所以，人们把重庆作为游览壮丽的长江三峡的最好起点。<br/><br/>　　长江相隔的南崖区，日机轰炸重庆时期，为各国驻华领馆区。据有关史料记载，日本首相东条英机曾密令日机不轰炸该区的海棠溪至弹子石一段。当年这段沿岸江边停靠了不少美、英、法、意的军般及商船。<br/><br/>　　朝天门东边，长江以北，距离五华里的江北区溉澜溪处，有一座白塔，乘船东下，沿江可见。这类塔，或白色、或黑色，这是古代先民渴望生活安定的一种吉祥物，取名“镇妖塔”，都建在入城的必经之道上，意思是好人入城，一切妖魔怪不能经过。在古代，它还起民一个路标的作用，不仅告知行人前面有城池，还说明只距离五里路程了。距离白塔不远的人头山。高高的土山堆上一巨石酷似一颗人头，面向东去的长江，好象被镇压妖塔压住无法脱身一般。<br/><br/>　　朝天门左边是嘉陵江，发源于陕西秦岭山的脉和甘肃的南部，流经陕西、甘肃、四川省，全长1119公里，嘉陵江上空来回穿梭的嘉陵客运索道，1980年12月动工，一年建成，全长740米，慢速为每秒2。5米，快速每秒8米，两台吊车对开，每台载客46人。索道下面低水位露出的沙州，是重庆早年的练兵场，也是抗战爆发后日机轰炸重庆投第一次炸弹的地方。<br/><br/>　　朝天门缆车，是山城重庆独有的短途交通工具之一，主要是为乘船客人解决登石级之苦而设，建于1984年。重庆缆车最早建于1945年，在望龙门。<br/><br/>　　朝天门洪水标志，记载了1981年7月16日百年难遇的特大洪水，高度为海拔193.38米，与低水位相比，净增高度33.38米。由于洪峰24小时回落，内河无风浪加害，洪灾时政府、军队、市民的奋力抢救，因比受灾较小，重庆8区4县3个市辖市境内只死亡28人，其中3人还是因贪小利，游到江心捞取飘流木材而葬身。<br/><br/>　　最佳旅游季节： 重庆朝天门在市内，地理位置属于比较高温的地区！所以最佳旅游季节为 春秋二季。夏天也会非常热闹，但是游客要注意防酷热，防暑！冬天对于北方人很适合，因为这里比北方城市可温暖多了！<br/><br/>　　交通：朝天门乘401路电车，102、103、122、251、382、418路公共汽车或252、413、420路小公共汽车可达。<br/><br/>　　住宿：江口镇转盘过50米的和平宾馆40元/三人间，虽然不是最便宜，但干净，而且四楼的露台直对乌江转盘处，景色饴人。<br/><br/>　　美食：最最有特色的便是重庆火锅了，它可是全世界闻名哟！如果你还比较能吃辣的话来到这里一定要品尝重庆火锅了，在翻滚的红油汤里烫鲜毛肚，鲜鸭肠，金针菇等等……想起都流口水了！如果你不能吃辣的话也有清汤的，不过清汤火锅不是重庆火锅的特色。朝天门还有几家大型的速食店，麦当劳肯德鸡等供你选择！<br/><br/>朝天门资料：明初戴鼎扩建重庆旧城，按九宫八卦之数造城门17座，其中规模最大的一座城门即朝天门。门上原书四个大字:“古渝雄关”。因此门随东逝长江，面朝天子帝都南京，于此迎御差，接圣旨，故名“朝天门”。1891年重庆辟为商埠，朝天门始设海关。1927年因修建朝天门码头，将旧城门撤除。1949年“九·二”火灾使朝天门附近2千米的区域化为一片废墟，从此，朝天门仅余城基墙垣。<br/><br/>　　朝天门位于重庆城东北长江、嘉陵江交汇处，襟带两江，壁垒三面，地势中高，两侧渐次向下倾斜。明初戴鼎扩建重庆旧城 \n，按九宫八卦之数造城门17座，其中规模最大的一座城门即朝天门。门上原书四个大字:“古渝雄关”。因此门随东逝长江，面朝天子帝都南京，于此迎御差，接圣旨，故名“朝天门”。1891年重庆辟为商埠，朝天门始设海关。1927年因修建朝天门码头，将旧城门撤除。1949年“九·二”火灾使朝天门附近2千米的区域化为一片废墟，从此，朝天门仅余城基墙垣。\n 今天的朝天门客运码头，新建宏伟的朝天门广场，是俯看两江汇流，纵览沿江风光的绝佳去处。<br/><br/>　　重庆公路“零公里”点标志将设在朝天门广场。“零公里”是一个国家或城市干线公路的起点，也是一个城市中心点的象征。中国“零公里”标志已经确定在天安门广场中轴线的中间位置，而上海则将零公里标志则设在了人民广场。<br/><br/>朝天门旅游TIPS：<br/><br/>　　每天晚上八点以后，可从这里乘游艇观览重庆江面夜色。朝天宫号船票有100元（含餐），80元（不含餐）、78元、68元4档；满江红号88元（含餐）、68元（不含餐）；金碧辉煌号68元。</p>', 'admin', 4, 3, '17', 1),
(17, '浙江芹川村', '2017-09-18', '<p></p><p>小溪上有30多座小桥横跨，两岸屋舍俨然，从山顶鸟瞰，整个村庄沿小河形成一个“王”字</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739369190157.jpg" title="1505739369190157.jpg" alt="20120207070531528.jpg"/></p><p><br/></p><p>芹川古村离千岛湖40分钟车程。推荐两条去芹川古村的线路：</p><p>线路1：</p><p>上海——杭州（走杭千高速）—千岛湖镇——千岛湖大桥—（千汾线）---姜家镇---浪川乡芹川村。</p><p>（自驾车从千岛湖大桥到芹川古村行程45分钟）</p><p><img src="/ueditor/php/upload/image/20170918/1505739374133265.jpg" title="1505739374133265.jpg" alt="20120207073814613.jpg"/></p><p>线路2：</p><p>上海—杭州西站（快客）—千岛湖镇—客运车站（公交车：票价12元；行程时间55分钟）—浪川乡芹川古村。</p><p><img src="/ueditor/php/upload/image/20170918/1505739378139487.jpg" title="1505739378139487.jpg" alt="村口.jpg"/></p><p>芹川古村３３３号有一个“廿四桥居”乡村客栈，是当地条件最好的一家农家乐。有“廿四桥”（“廿四桥”为仿古明清建筑，风格素雅高贵，古色古香，颇具“乡绅”气息）、“清远堂”（“清远堂”前厅始建于清朝光绪年间，后进始建于明朝崇祯年间，是过去大户人家的私家宅院（著名徽商、开明地主王维光——被誉为古遂安县“四大善人”之一）两座客房，能同时接待40余人住宿。客栈内有清水鱼塘，锦鲤、草鱼、乌鲢、乌龟等游弋嬉戏，一幅恬静清幽的绝佳景致~！</p><p>据传，太平天国末期洪秀全的弟弟洪仁玕、儿子洪天贵富及部分将官曾居住于此，并在此村犄角旮旯处埋下了很多金银珠宝；国民党时期，军统特务头子戴笠、暂62师师长刘梓皋等，均在此逗留小住。“清远堂”相传还是座著名的“风水古宅”，整座房子于清末民初曾在风水大师的指导下，被近百人凌空扛起“车向”，以“改运积福”。屋内现存有王维光为防小妾挥霍过度而设的地下“藏宝处”。</p><p><img src="/ueditor/php/upload/image/20170918/1505739386481482.jpg" title="1505739386481482.jpg" alt="小溪.jpg"/></p>', 'admin', 1, 38, '18', 5),
(18, '桂林漓江漂流之游', '2017-09-18', '<p></p><p>到达桂林的时候，也正是桂花开放的季节，导游说你们来的正是时候，“我们桂林之所以叫桂林，就是因为桂林因“玉桂成林”而得名，八月秋风送爽的时节，正是桂花盛开、香飘四野的时节”。<br/>桂林是祖国锦绣河山里的一颗璀灿明珠。她那挺拨秀丽的群峰，清澈明净的流水，玲珑瑰丽的洞府，奇异峻美的岩石，举世闻名，无不叫绝。&quot;桂林山水甲天下&quot;，市中心的叠彩山、伏山、象鼻山、芦笛岩、七星岩、漓江，这些声名远扬的景点，早就存在我的心中，而今天终于来到有两千多年悠久历史的历史文化名城的身旁，怎么不叫我心花怒放呢？<br/>只有来到桂林，我才发现，过去我对它的了解，是如何的肤浅。这里的任何一座山，任何一洼水，都是浑然天成的。可触可摸可与之融为一体，而且，不会有丝毫的隔阂。这些山，这些水，这些景，只要稍稍换一个角度来观赏，立刻就会产生另外一种完全不同的影致。所谓移步换景，在这里得到了最大的证明。所以，在这里的每一个景点，你完全不用担心和别人争抢最佳位置，因为，几乎每一处，都是拍摄的最佳位置。</p><p>“城在景中，景在城中”，这是我所见过很多的城市要创建国家旅游城市的口号。可是在桂林这里，这样的口号简直是多余的。无论是象鼻山、伏波山、叠彩山、独秀峰、晋陀山，它们都被漓江山环水饶，而城市就穿行在这些大小不一的彩珠之中。它们之间的距离，都不到五分钟的车程。以至于我们从武昌早晨上车，晚上10点多赶到这个陌生的城市的时候，没有走几步，就好比来到了梦幻般的境地。我们所住的南门桥那里，桥是古老的，江是清澈的。夜已经深了，公路上的车水马龙与漓江上的游船灯火辉煌，依然交织成一幅热烈而浪漫的夜景，以至于接我的旅店司机在招呼我拿行李，我却忍不住还在用摄象机扫描。<br/><br/>司机告诉我，前面就是最热闹的中山中路。在很多地方，你刚购完物品，抬脚就可以跨进漓江。白天的时候，你可以从围绕或者经过它们的任何一条街巷迈入其中。去看它古色古香的楼，碧波如玉的水，丛林般的树，千姿百态的桥，变幻莫测的灯，刻满诗曲的溶洞。<br/>在许多城市，都有什么“八景”“十景”的说法，而在桂林，你是无法用数字来形容景点，统计它的美丽之处的。因为，在这青山和绿水相互映衬的天地里，无论你在什么时候，也无论你从哪个方向望去，都是优美的景色。桂林它完全就是一个大的开放式景区，天然形成了以自然山水为背景、田园风光为全景的立体式真正意义上的园林城市。<br/>这里是那么的绿。山是绿的，漓江的水是绿的，满城绿意盎然，沿街花树盛开，让我们感到依然是盛夏的滋味。只是，当我们吃了那甜蜜的红果的时候，才知道，这是夏天到来果子成熟的标志了。<br/>早晨吃了正宗的桂林米粉，忍不住就要拍那不远处的公园里美丽的江景和晨练的人们，直引得店家在笑我，先生，这里还不是漓江的最美丽的地方，还是去漓江吧。我说，老板娘，我这来了就不走了，一翻话，引得吃早餐的人都哈哈大笑起来了。其实，我说的是心里话，桂林的山水，如果单纯用两只脚去走，用一双眼去看，那是远远不够的。只有和桂林人一样，，锻炼在这个美妙的辰光里，才能在细细地品味，什么叫真正的天然画廊。<br/><br/>上午九点半我们从桂林出发，开始我们的漓江精华游的序幕，呵呵，从桂林坐大巴到杨堤路口下车，然后再转中巴车进杨堤，事先上车的时候就发信息给定好的筏工廖师傅，让他先做好接待我们的准备，当我们到杨堤码头时，廖师傅就主动的上来接我们，因为之后我们简单的描叙了一下我们的穿着及其几个人，廖师傅一眼就看出了，主动上来打招呼，之后就带着我们上竹筏，开始我们梦寐以求的漓江之旅，江边的竹筏很多，游客而来自天南海北的中外游客，无一不是兴奋异常。竹筏行走在如画的江上，本身就给这壮美的山河增添了新的魅力。而那江水竟然如此清澈，清澈到能看见江里的鹅卵石和鱼儿。<br/><br/>漓江的精华景色几乎全在去杨堤、兴坪的水路中，人民币上的黄布倒影景色，真实的呈现在眼前，而九马画山的故事更负盛名。那 \n“九马画山”的绝壁上，青绿黄白，众彩纷呈，浓淡相间，班驳有致，宛如一幅神骏图，人们依稀在石壁上隐约可以看出画着九匹马。只可惜，烈日下我只顾拍摄，等老婆问我找出几马时，我一片茫然。匆忙中，我勉强只数出看出了五匹，我对妻说，不要紧，等我回家慢慢地数。<br/>在游漓江的一路上，廖师傅不停的给我们讲解每一处风景，还说了很多山的来历、许多动听的故事，让我们受益匪浅。廖师傅热情的服务给我们带了乐趣，廖师傅也是一个比较专业的摄影师，也许是在江边住久了，知道什么风景在那个角度拍出来的效果好，这一点让我无比的兴奋。因为我自己也是一名摄影师，终于找到了知音。</p><p>晚上我们在浏览了阳朔的著名西街，穿过人流如潮的各国游客后，我在想，这些来自四方的人，今天在这里，有无可能发生来自他乡的的艳遇呢？据说，阳朔西街是懒人的天堂，适合发呆、冥想、游荡，一不留神，也可能成就一段故事，这不是传说，已经有无数的证明。而我们此刻却没有停住匆忙的脚步，我们下午还要游览著名的阳朔月亮山，大榕树，还要在刘三姐电影的拍摄地漂流。<br/>上得旅游车，旅游车司机早已经把车从码头开到了阳朔等我们。那年轻而漂亮的女导游语出惊人：“各位来自祖国各地的狗肉们，我姓王，你们也可以喊我王狗肉。下午我陪各位狗肉们游览阳朔”<br/>原来，桂林人称好朋友作“狗肉”(音勾如)，可见对朋友的亲昵和对狗肉地位的尊崇。难怪小姑娘直呼我们彼此为狗肉。不过，我觉得后面一定有故事。晚上我回来问了很多本地人.有桂林餐馆的老板，宾馆的服务员，路边的老人等.人家回答的不是哈哈大笑就是说法不一，各种解释都有。但一个共同点就是，桂林人喜欢吃狗肉，狗肉就是好朋友的简称，狗肉代表了好朋友。<br/>据说，桂林人好吃的，天上飞的地上跑的是无所不吃。比广东人也不逊色。同时也爱好吃狗肉。冬日里，三两好友，一瓶三花，围坐于香气四溢的狗肉火锅傍，谈板路，品小酒，其乐溶溶，也是人生一大乐事。此时不熟悉之人或者道不同之人的介入都会影响了这份意境。所以有好朋友才在吃狗肉一起的说法。只可惜我们来去匆忙，没有领略到这个境界。<br/>第二天早晨，还是在南门桥边一颗大榕树下，一个锻炼的老人。告诉我关于狗肉的故事：相传，过去在灵川县，有三位秀才是好友，上京赶考。十年寒窗，艰苦异常。彼此相约，考完后一起去吃狗肉。改善生活，兼作庆贺。<br/>三位秀才进了考场。甲乙两秀才天资聪颖，很快做完了，教了卷。出了考场，等丙秀才。很久还没见到丙出来，两秀才急着吃狗肉心切，就想了个法混进了考场去找。发现丙秀才有题不会做，正在犯难，两秀才帮朋友心切，同时为了早点去吃狗肉。就写了答案，给了丙秀才。并在后面关照写上了一句：你赶快抄，抄完我们去吃狗肉。<br/>丙拿到答案，非常高兴，又非常紧张，一急之下，把后面那句：你赶快抄，抄完我们去吃狗肉。当做了答案，一起抄到了试卷上。这下麻烦了，一改卷，三秀才作弊就被发现了。<br/>以前对学子作弊处罚很严厉，功名革去不算，还要在考场门口重枷三天示众。三人重枷立于考场门口，丙觉得很对不起两位朋友，唉声叹气。甲乙两位就安慰他了：没关系的，我们是好朋友嘛，好朋友就应当甘苦与共，你如果还是觉得内疚的话，三天后，枷锁去后，请我们去吃狗肉就好了。<br/><br/>踏上归程后我的心还在那清澈的漓江边，耳边依然是“唱山歌，这边唱来那边和”的三姐与游客的的对歌声里，突然感觉，桂林美的不止是山水呢，还有那里的人和美食呢。<br/></p>', 'admin', 4, 0, '19', 0),
(19, '游绍兴体味鲁迅笔下的古镇韵味', '2017-09-18', '<p></p><p>每当提起绍兴古镇，小时候语文课本中鲁迅先生笔下的阿Q、祥林嫂等人物形象定会率先闪现在脑海中。而把历史文化名人故居与江南水乡风情很好地融为一体，则是绍兴旅游华丽的低调却人气十足的关键。<br/><br/>如果发呆，我希望晃悠乌篷船里，坐在乌篷船里听戏。春天到了，该去坐一下悠悠荡荡的乌篷船，品品黄酒，古城绍兴有江南的春意。这里有美得让人发呆的东湖，幽雅文气的兰亭，绿波上轻轻划过的乌篷船，当然，最少不了的，是鲁迅笔下，让我们熟悉得不得了的鲁镇、百草园、黄酒……<br/><br/>乌篷船是绍兴最有特色的元素，春天到绍兴，坐上乌篷船慢行水乡，别有风味。绍兴基本上是以历史文化名人故居和江南水乡风情为主。到绍兴，鲁迅故里绝对是首选与不容错过的，其不仅高高扯起绍兴文化旅游的大旗，而且据说也是绍兴保存最完好的景点。在绍兴游玩，以3天时间为佳。以鲁迅故里与柯岩风景区为两个游玩重头，这个季节，乘坐乌篷船则是最佳的方式。<br/><br/>鲁迅故里的推荐游玩地包括百草园、鲁迅纪念馆、鲁迅故居、鲁迅祖居、三味书屋等。来到这里，会有熟悉的感觉，因为鲁迅笔下都认识了这些地方。据当地朋友介绍，目前该景区免费游玩，但还得凭票进去，并且每天只有4000张限额，发完即止。可以选择凭身份证网上订票，也可以到现场领票方式领取。<br/>此外，与鲁迅先生笔下人物密切相关，绍兴的另一王牌景区———柯岩风景区亦一定要去。该景区位于绍兴，由柯岩景区(凿石奇观)、鉴湖(古纤道、乌篷船)、鲁镇等三个游览点为主。鲁镇，则是鲁迅笔下风情再现之地，每天都有阿Q、祥林嫂等人物扮演活动在镇上上演。从文化角度看，这两景区已完全彰显绍兴特色的石文化、水文化、桥文化、酒文化、戏曲文化、名士文化、民俗文化等。</p><p>坐小船看绍兴的古韵，傍晚可以到城南的稽山公园看看，那里是一个缩微版的绍兴风情园，免费的。在这里，春天的气息特别浓。另外，绍兴风情浓郁的还是乘环城河游船，坐在江南小船上，到水乡吹吹风，看看枕河人家，看看江南廊桥，游船不将路过绍兴环城河八大公园。<br/>绍兴市区里其他值得推荐的地方，还有八字桥、周恩来故居等。市区以外的景点，比较出名的有东湖、兰亭(书法圣地)、大禹陵(传说中大禹的家在这儿)，每年都会有祭禹大典，另外还有香炉峰可以爬山，联票50元，可以单独买大禹陵的票，仅25元。<br/><br/>绍兴的春天，有浓郁的江南风情,不可错过。柯岩景区一次看尽绍兴风情，柯岩景区很大，这里最有看点的要数云骨和石佛了，都是隋唐时期采石留下的遗迹，大佛是弥勒佛，却没有大肚子，而且听说两个耳朵还是相通的，很是精致。<br/>鉴湖景区内的酒岛上有投醪劳师雕塑，品酒馆里免费品尝绍兴老酒，酒虽然不多，但也可以感受绍兴的风情。品酒馆旁边就是乌篷船码头，坐船去鲁镇景区。<br/>鲁镇可说是鲁迅设计的小镇，是根据他笔下的一些细节而建成。鲁镇里最知名的，当然是水陆双面戏台，戏台很大气，感觉如在《社戏》里看过一般。<br/>经常有演员进行越剧表演。街头还不时出现一些打扮成衙役、老爷的人物，孔乙己、祥林嫂、鲁四老爷等小说里的人物也都活灵活现地出现在街道上，假洋鬼子还和你“哈喽、哈喽”地打招呼。在镇公所升堂审案中，阿Q的表演真是绝了。一定要和他合张影，不过别被他的眼神给吓到。<br/>兰亭书法圣地和山水风光结合，绍兴的古迹很多，其中兰亭是因春秋时越王勾践植兰于此，后王羲之写《兰亭集序》，而成为“书法圣地”。兰亭融秀美的山水风光，雅致的园林景观，丰厚的历史文化积淀于一体。爱好书法的朋友在此则可多花点时间停留，细细品味与研究。</p><p>在鲁镇，可遇到阿Q、祥林嫂等鲁迅笔下的人物。在绍兴要玩的，就是坐乌篷船、划龙舟、唱社戏、拉洋车、坐花轿、猜灯谜、踩高跷、押牌宝、钓鱼虾、抖空竹；还可以同“阿Q”一起“造反”，问孔乙己“茴”字有几种写法，扮“县官老爷”过一过“坐堂审案”的戏瘾……个人觉得，游过柯岩景区与鲁迅故里等于游遍绍兴了。<br/>行走线路上，可以做以下安排：上午到绍兴，时间相对充裕，可先游柯岩，也可先游玩鲁迅故里再到柯岩，晚上坐船夜游绍兴环城河，住市中心。下午到绍兴，两点后入园建议先游鲁镇，再游鉴湖、柯岩，住在市中心。晚上坐船夜游绍兴环城河，第二天起来到鲁迅故里游玩。<br/>出行攻略<br/>交通：自驾车前往者，最好在柯桥或杨汛桥道口下高速，这样离柯岩景区更近一些。整个柯岩风景区游程2至3小时。在市区可乘603路公交或在鲁迅故里乘77路公交能到达柯岩风景区。<br/>住宿：最佳的住宿地点在市中心，两人住宿费大约150元。也可选择住在仓桥直街里的特色旅舍，价格适中，环境还蛮有水乡情调。<br/>门票：柯岩、鉴湖、鲁镇三个景点采用联票制，票价100元(有残疾证可以半价)，散客可登录其景区网站订票，有九折优惠。鲁迅故里免费，夜游环城河50元。<br/>餐饮：鲁镇有两家饭店：一石居、泰牲，酒岛有一家饭店：品酒馆，景区外也有两家饭店，也可到仓桥直街、劳动路等处的一些小餐馆。<br/>推荐菜肴：菜肴可选择臭豆腐、酶千张、炒三鲜、干菜扣肉、萝卜醋鱼、活蛋(鹅蛋为佳)、笋干老鸭煲等。<br/>购物：绍兴花雕、香糕、酱鸭、霉豆腐、梅干菜等，而且景区内也有很多价廉物美的东西。<br/></p>', 'admin', 4, 0, '20', 0),
(20, '河北于家村', '2017-09-18', '<p class="content" style="white-space: normal; text-align: left;">于家石头村落，房屋、院落、街道、桥梁、井窖池等等一色表石建筑，一律就地取材，一划农家手饣，一派古村风貌。<br/></p><p class="content" style="white-space: normal; text-align: left;"><img src="/ueditor/php/upload/image/20170920/1505884097114124.jpg" title="1505884097114124.jpg" alt="1505828648813128.jpg"/></p><p class="content" style="white-space: normal;">高低俯仰，层次错落，各抱地势，互不雷同，古庙、古阁、古碑碣、古歌楼等众多文物古迹镶嵌其间，古人类遗址、古兵寨、无底洞等莘莘景观环踞四周，真可谓深山藏古秀、瑞石撒幽香。人称这里风水宝地、藏古圣地、农居福地、旅游胜地，名不虚传。<br/></p><p class="content" style="white-space: normal;"><img src="/ueditor/php/upload/image/20170920/1505884112493531.jpg" title="1505884112493531.jpg" alt="1505828660219892.jpg"/></p><p class="content" style="white-space: normal;">该村大多数男人都会石匠手艺，祖祖辈辈就是靠自己的双手将伸手可及的石头修成梯田，雕成石器，盖成房屋，直至建成这个庞大的石头村落。</p><p class="content" style="white-space: normal;"><img src="/ueditor/php/upload/image/20170920/1505884121491681.jpg" title="1505884121491681.jpg" alt="1505828666731368.jpg"/>&nbsp;石育人生福，人琢石生辉，于家人古往今来锲而不舍地营造自己的石头王国，与石头产生了深厚的感情，从中创造了智能，健壮体魄，造就了憨厚的禀性和淳朴、纯洁的民俗风情。岁月沧桑先辈们琢石之手留下了许多宝贵的文物和天然景观；如“清凉阁、古街道、于氏宗祠、真武庙、全神庙、观音阁、古歌楼、四合楼院，白庙遗址、搁玉窑、青山洞、南山红叶、老虎洞、（古人类遗址）卧虎山、寨垴山、五里洞”等等。</p><p class="content" style="white-space: normal;"><br/></p>', 'admin', 1, 40, '21', 1),
(21, '浙江绍兴', '2017-09-18', '<p></p><p><br/></p><p>说起绍兴，我们会想起鲁迅笔下那浓香的绍兴酒，古朴的乌篷船，热闹的社戏台，身着长衫赊酒喝的孔乙己，以及住在土谷祠里的阿Q…如今走进绍兴，吸引人们的将不止是这些，还有那悠悠鉴湖水，浓浓古越情以及绍兴那浓厚的文化氛围和悠久的历史。</p><p><img src="/ueditor/php/upload/image/20170918/1505739442931810.jpg" title="1505739442931810.jpg" alt="20111105104547354.jpg"/></p><p>绍兴位于长江三角洲南翼，浙江省东北部，钱塘江西岸，是我国著名的历史文化名城，素以人文景观丰富，水乡风光秀丽，风土人情诱人而著称于世。早在新石器时代，就有人类的足迹，四千多年前，大禹治水东巡到苗山，会集诸侯，计功行赏，将苗山更名为会稽山（即会计之意）。春秋战国时，越王勾践在此建立者陈城，卧薪尝胆，复国兴邦，一度成为中国东部政治文化中心。南宋时越州两次成为临时都城。建炎四年(1130)年),宋高宗赵构取“绍祚中兴，之意，升越州为绍兴府，并于次年改元为绍兴元年，绍兴由此而得名。</p><p><img src="/ueditor/php/upload/image/20170918/1505739447442366.jpg" title="1505739447442366.jpg" alt="20141010110115560.jpg"/></p><p>绍兴之美，美在柔水。境内河道密布，湖泊众多，境内有大小河流90分里，主要有曹娥江，浦阳江、鉴湖水系等，向以“水乡泽国”享誉海内外，是典型的江南水乡小功城。绍兴整个城市布局以河为骨架，随河就街，形成一河一路，一河两路、有河无潞的独特格局。</p><p><br/></p><p>“河是路的一种，桥为路的延续”，密集的河网造就了绍兴形态各异、风格奇特的桥梁万余座，有着“桥乡”的美誉，最著名的当属八字桥以及古纤道。八字桥是宋代石桥，造型优美，虽历经80多年的风雨侵蚀，桥身依然坚实，是我国古代桥梁史上不可多得的重要实物例证。古纤道是古人行舟背纤的通道，它自东而西穿越绍兴全境。它卧伏于西兴运河，是萧绍运河上的一大奇观堪称江南水乡一绝。古纤道长约5公里，多姿多态的数十座石桥横跨其上，形成独特风情。</p><p><img src="/ueditor/php/upload/image/20170918/1505739453398043.jpg" title="1505739453398043.jpg" alt="20151117093840795.jpg"/></p><p>”江南文化看绍兴”。绍兴以文化之邦著称，历代名人辈出，涌现出了许多著名的政治家思想家、文学家以及科学家有立国始祖大禹、越王勾践、哲学家王充、书法家王羲之著名诗人陆游、巾帼英雄秋瑾、文化泰斗蔡元培、文学巨匠鲁迅、一代伟人周恩来等。李白、杜甫、白居易、孟浩然等…多位著名诗人也都曾来此，留下了众多赞美稽山鉴水的绚丽诗篇。</p><p><br/></p><p>绍兴素有“江南十分美，绍兴九分九”之说。五百多年的文明史和25×年建城史使得绍兴每一寸土地都内涵丰富，被人们称为是一认没有围墙的博物馆。在中国艺术史上，晋代书法家王羲之斤写的《兰亭集序》的故事被传为佳话，其中的兰亭就坐落在绍兴城西南约12公里的兰渚山下。由于历史的变迁，亭址以至迁移。现在的兰亭建于明嘉靖二十七年a粥年）是一座幽雅别致的江南园林已成为历代书法家朝圣之地。</p><p><br/></p><p>“红酥手，黄滕酒，满城春色宫墙柳......&quot;这就是陆游的《钗头凤》，八百多年来，一直是脍炙人口的，它最早被题刻在绍兴沈园的墙上。据说陆游初娶表妹唐琬，夫妻恩爱，却得不到陆母的欢心。陆游被迫与唐琬分离，十余年后的一天，他们春游沈园，邂逅相遇。伤感之余，不禁往事重忆，陆游百感交集，就在园壁题了著名的《钗头凤》词，此次邂逅不久唐琬便忧郁而死，陆游为此哀痛至甚。沈园位于红：兴市区东南隅的洋河弄内，是宋代著名的园林之一，园内亭台楼阁，小桥流水，假山叠石，景色极美。</p><p style="text-align:center"><img src="/ueditor/php/upload/image/20170918/1505739458101830.jpg" title="1505739458101830.jpg" alt="20151117093841630.jpg"/></p><p>提起绍兴鲁迅是个不得不提的人物。鲁迅故居地处绍兴市东昌坊口，1881年9月25日鲁迅就出生在这里，并在此生活了18个春秋，后来去南京求学，回故乡任教后也大多居住此地。故居是江南特有的深宅大院，现按鲁迅在｜时的原样陈列。故居后面便是著名的百草园，是鲁迅少年时代常玩的地方。附近还有著名的三味书屋，鲁迅｜2岁到？岁在此求学。书屋的东北角陈列着鲁迅当年用过的书桌。故居东侧建有鲁迅纪念馆绍兴之美，不仅美在古迹众多，更拥有一种风情之美。绍兴拥有越剧、绍剧、调腔、莲花落等丰富多彩的地方戏曲剧种。绍兴黄酒，色泽黄澄透彻，味道醇厚芳香，远近闻名，南北朝时期已经成为贡品。</p><p><br/></p><p>山、水、路、桥、园林、古迹以及乌篷船、社戏等巧妙地融为一体，构成绍兴水乡特有的优开与恬静。来到这里，你可以坐上乌篷船，去领略杜城风光；你可以迈进咸亨酒店，去尝尝正宗的绍兴黄酒；你也可以回到乡间，去看一场传统的社戏。总之，漫步古城，时间似乎都慢了下来。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739470843896.jpg" title="1505739470843896.jpg" alt="20141010110108955.jpg"/></p><p>旅游TIPS：</p><p><br/></p><p>绍兴火车站0575·8022584；</p><p>绍兴汽车站88022222；</p><p>鲁迅故居免费（提前预约）；</p><p>沈园30元；</p><p>秋瑾故居10元。</p><p><br/></p><p><br/></p><p>绍兴鉴湖</p><p><br/></p><p>鉴湖在绍兴城西南1.5公里，为浙江名湖之一。俗话说“鉴湖八百里”，可想当年鉴湖之宽阔。鉴湖面积约30.44平方公里，其主干道东起亭山，西至湖塘，长22.5公里，形如一条宽窄相间的河道，镶嵌在绍兴平原之上。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739476705764.jpg" title="1505739476705764.jpg" alt="20141010110053404.jpg"/></p><p>保存完好的绍兴古城</p><p><br/></p><p>古城是保护最好的一块区域，也是蔡元培先生故居所在地，是一个颇具绍兴特色的明清台门建筑。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739480258491.jpg" title="1505739480258491.jpg" alt="20111105104920411.jpg"/></p><p>沈园绍兴是一个人杰地灵，名人辈出的江南古城。王羲之，陆游，鲁迅，秋瑾，蔡元培等都在绍兴留下了生活的足迹。城市街道整洁，河网密布。大善塔仓桥直街沿着鲁迅路东行，一路酒肆饭庄，咸亨酒店已成为当地的金字...</p><p><br/></p>', 'admin', 1, 2, '22', 0),
(22, '如何体验扬州一日游?', '2017-09-18', '<p></p><p>既然是扬州一日游，就要游览最精华的景点，体验扬州的城市文化，这条线路之所以可以被称为精华游，因为它将扬州旅游景点中不可不去的地方，不可不享受的特色风俗都囊括在内，让你用最短的时间感受扬州的美丽风韵。<br/>线路推荐：<br/>瘦西湖,大明寺,个园,何园。<br/><br/>行程分为七站，具体安排如下：<br/>第一站 扬州特色早茶(卢氏大院)<br/>在“园林多是宅”的扬州，康山街22号一带青砖黛瓦的高大古建筑群，是一座扬州晚清盐商最大的豪华住宅，被誉为“盐商第一楼”的卢氏盐商古宅。原主人为大盐商卢绍绪，此宅建于清光绪年间(1897年)，当年兴建此宅耗银7万余两。它以绵延的建筑群落、精美的建筑风格成为诸多珍珠中最耀眼的一颗，是扬州古城文化中当之无愧的新看点。<br/><br/><br/><br/>扬州瘦西湖<br/>第二站 瘦西湖<br/>瘦西湖位于扬州西北郊，是著名的风景游览点。它原来是一段自然河道，经过历代的疏浚治理，建造园林，逐步发展而成。<br/>第三站 大明寺<br/>古城扬州北郊，蜀冈如卧龙般蜿蜒绵亘。名扬四海的千年古刹大明寺，就雄踞在蜀冈中峰之上。大明寺及其附属建筑，因其集佛教庙宇、文物古迹和园林风光于一体而历代享有盛名，是一处历史文化内涵十分丰富的民族文化宝藏。<br/><br/>扬州个园<br/>第四站 个园<br/>个园以“四季假山”闻名。春景在桂花厅南的近入口处，沿花墙布置石笋，似春竹出土，又竹林呼应，增加了春天的气息。夏景在园的西北，湖面假山临池，涧谷幽邃，秀木紫荫，水声潺潺，清幽无比。秋景是黄石假山，拔地数仞，悬崖峭壁，洞中设置登道，盘旋而上，步异景变，引人入胜。</p><p>扬州何园<br/>第五站 何园<br/>穿越时光的阻隔，一切回到一八五八年，时值晚清昏庸无能的清政府签定了《天津条约》，一时间，中国门户顿开，列强蜂拥而至，时任汉黄德道并兼任江汉关监督的何芷深感与一些畏事愉安者难于相处，又空有爱国自强之抱负而难于兑现于国事，并且家母年事已高，遂归隐于扬州。<br/>第六站 品淮扬菜<br/>扬州狮子楼大酒店、扬州御膳坊、四迎春特色淮扬菜馆、曹大师淮扬三头宴(更多淮扬菜)。<br/>第七站 扬州沐浴<br/>扬州易德商务酒店、扬州醉温泉商务会所、扬州帝豪沐浴酒店、扬州龙桥商务休闲会所。<br/><br/>扬州是一座具有浓厚文化底蕴的古城，地处富庶的长江下游地区，京杭运河穿城而过，自古就是人杰地灵之地。在扬州，不管是洋房别墅，还是青砖旧屋，只要一开窗户，就是扑面而来的清新水气；一出大门，便是温柔婉约的江南水乡。阳春三月，不妨按照下面这条传统线路去扬州走一走，亲自感受一回扬州古城的风情魅力。</p>', 'admin', 4, 0, '23', 0),
(23, '浙江乌镇', '2017-09-18', '<p></p><p>1、枕河而居的水阁</p><p>乌镇与众不同的是沿河的民居有一部分延伸至河面，下面用木桩或石柱打在河床中，上架横梁，搁上木板，人称“水阁”，这是乌镇所特有的风貌。水阁是真正的“枕河”，三面有窗，凭窗可观市河风光。茅盾曾在《大地山河》中这样描述故乡的水阁：“……人家的后门外就是河，站在后门口（那就是水阁的门），可以用吊桶打水，午夜梦回，可以听得橹声唉乃，飘然而过……” 传说水阁是由一位机智的豆腐倌的违章建筑而来，可是随着时间的迁移，许多历史都已烟消云散，唯有这集生活的实用与享受于一体的建筑形式因着人们的喜爱而得以保存和延续。</p><p>从某种意义上来说，水阁是乌镇的灵气所在，虽然它没有奢华，难比高楼。有了水阁，乌镇的人与水更为亲密；有了水阁，乌镇的风貌更有韵味；有了水阁，乌镇的气质更为悠雅；水阁是乌镇的独创，也是乌镇的魅力所在。 所以当你在东栅信步游览时，不妨走入一户人家，穿过厅堂之后，总会在临水一面看见一间或大或小的水阁，有的直接开一扇门，不知道的打开门去，迈开步子，就一脚踏进水里了。有的人家直接在地面上开一扇一米见方的地道门，拉开这扇门，一汪清水就这样呈现在脚下了。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739547180131.jpg" title="1505739547180131.jpg" alt="20120228075835666.jpg"/></p><p>2、小桥流水</p><p>桥是江南水乡古镇不可或缺的因素。据说乌镇历史上桥梁最多时有120多座，真正是“百步一桥”，现在乌镇还有30多座各式各样的古桥。 其中西栅有通济桥、仁济桥，中市及东栅有应家桥、太平桥、仁寿桥、永安桥、逢源双桥；南栅有福兴桥和浮澜桥；北栅有梯云桥和利济桥。这些桥最早建于南宋，大多始建或重建于明清，有些桥还题有桥联，如通济桥的“寒树烟中，尽乌戌六朝旧地；夕阳帆外，是吴兴几点远山。”一座桥一个故事，如果你有耐心坐在那里静静的听当地老人给你慢慢讲来，通过这些桥，一座古镇的历史也就在你满前逐步展开了。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739553122726.jpg" title="1505739553122726.jpg" alt="20120228075838929.jpg"/></p><p>3、石板小巷</p><p>走进乌镇，走在那用青石板铺成的狭窄的小街上，看到两边各式各样的民居和仍住在民房中的乡民，听着回响在耳边的吴侬软语，不禁升起一种亲切自然的感觉。</p><p>漫步于古镇绵延的石板小街上，可以倾听脚步在另一小巷上的回音。有的沿水小街还有“石帮岸”，这些是为了方便当地居民以舟代步时栓船用的，很多这样的“石帮岸”都有着精美的修饰，比如我见过雕上一个瓶，瓶里插着三支戟，取“平升三级”的意思，用以系船。</p><p><img src="/ueditor/php/upload/image/20170918/1505739556971055.jpg" title="1505739556971055.jpg" alt="20120810065645331.jpg"/></p>', 'admin', 1, 9, '24', 2),
(24, '成都、康定、新都桥、稻城、亚丁长途攻略', '2017-09-18', '<p>这次是走四川跟云南，所以我买了四川打折卡，跟云南通票，其实这2样可以给自己省很多钱。中国的门票都是很贵的，所以能有优惠，大家都要找找，一路下来门票都可以省几百块。<br/>如何避免或减轻高原反应？<br/>大部分人初到高原，都有或轻或重的高原反应，<br/>一般什么样的人会有高原反应没有规律可循，<br/>避免或减轻高原反应的最好方法是保持良好的心态面对它，许多的反应症状都是<br/>心理作用或有心理作用而引起的，比如：对高原有恐惧心理，缺乏思想准备和战胜高原决心的人，出现高原反应的机会就多。建议初到高原地区，不可疾速行走，<br/>更不能跑步或奔跑，也不能做体力劳动，不可暴饮暴食，以免加重消化器官负担，不要饮酒和吸烟，多食蔬菜和水果等富有维他命的食品，适量饮水，注意保暖，<br/>少洗澡以避免受凉感冒和耗体力。不要一开始就吸氧，尽量要自身适应它，否则，你可能在高原永远都离不开吸氧了（依赖性非常强）。<br/>我带的药品：高原红景天（至少提前10天服用，但是发现到四川后，有些都说没有用，可能是心理起到作用）、西洋参含片 \n（这个抗疲劳还是挺有用的），百服宁（控制高原反应引起的头痛）、特效感冒药 （在高原感冒是一种危险的事情），维生素C，创口贴等 \n，对于高原适应力强的人，一般高原反应症状在1-2天内可以消除，适应力弱的需3-7天。<br/>其实我后来上高原感觉还是喝酥油茶比较能缓解高反哦，而且能补充能量，但是很多人喝不惯。<br/>附1:各汽车站咨询电话：<br/>成都新南门汽车站电话：028-85433609<br/>成都茶店子汽车站：028-87506610<br/>稻城汽车站：0836-5728565、5728762<br/>康定汽车站：0836-2822211<br/>丹巴汽车站：0836-3523169<br/>雅江汽车站：0836-5124245<br/>道孚汽车站：0836-7122643<br/>理塘汽车站：0836-5322371<br/>乡城：0836-5826555<br/>中甸：0887-8223501<br/><br/>4月16号，一个人，带上一个55L的登山包，带着一个腰包，带上一个相机，乘着一个月前订的特价机票就开始了一个人的旅行。。。。。<br/>宁波机场，这个也是第一次坐飞机，发现这次把自己的第一次都贡献出来了。<br/>宁波到成都的飞机票：360+燃油附加费140=500</p><p>11：30飞机到达成都：双流机场。<br/>根据旅行的计划：我坐机场大巴303到岷山饭店，再搭地铁到达懒骨头青年旅舍，网上有各个站到达这个青年旅舍的详细走法。还附地图。我看重这个新开的，设施比较好，环境也不错。<br/>到达旅舍，把自己的大包放下，然后背个小包出门溜达去也，这个区就一个文殊院，跟旅舍挺近的，咱就一个人开始暴走。这个是跟懒骨头要的地图，我基本把这个区走的差不多了。<br/>文殊坊：一个有点禅语而宁静的地方。或许我去的时间比较好，<br/>让我觉得不是那么商业，那么让人浮躁，心里有宁静的感觉。 游客可以绕着千佛塔走三圈，祈福，拜佛。院里有个喝禅茶的园子，坐下后，穿黄马甲的师傅拎着铜水壶给买了禅茶的游客沏茶续水。 有种尘世皆醒我独醉，奈何放不下芸芸众生。<br/>喜欢他挂着的几句禅语：<br/>1》真正的快乐都是免费的<br/>2》真正的财富并不是因为我拥有很多，而是我要求的很少<br/>3》真正的富有是欢喜而不是财富，真正的贫穷是无知而不是无钱<br/></p>', 'admin', 4, 0, '25', 0),
(25, '乡村游：贵州从江县加榜梯田攻略', '2017-09-18', '<p></p><p>加榜梯田位于贵州省从江县加榜乡境内，地处月亮山腹地。<br/>夏季加榜梯田旅游总行程：<br/>D1：桂林-（班车）-三江-（班车）-从江<br/>D2：从江-（班车）-党扭村路口-（徒步12公里）-加车大寨<br/>D3：加车大寨<br/>D4：加车大寨-（徒步9公里）-党扭村<br/>D5：党扭村-（徒步3公里）-路口-（班车）-榕江-（班车）-凯里<br/><br/>沿途信息：<br/>1、从党扭村路口下车，到党扭村3公里，有王明兴家（就在大路边）可住宿，每个床位每天25元，联系电话：18285554516 \n。王明兴家只有6个床位，且农忙时候全家人都去很远的田里干活，要晚上七八点钟才回来。在此之前网上有人说的党扭村有李姓人家可以住宿，其实是没有的。<br/>2、从党扭村到加车大寨9公里，都是沿着公路走，边走边赏梯田景色。加车大寨的梁老师（一位退休的公务员）家可住宿。从加车村口走进去一百多米就是梁老师家，门口挂有“接待户”的牌子。梁老师家只有12个床位，每个床位每天30元，联系电话：13638557852\n 。梁老师也是摄影爱好者，当地哪里的景色好看，可以向他咨询的。<br/>3、从加车大寨再进去加榜乡的10公里，我没有走，情况不明。<br/>4、从江和榕江都是县城，住宿没有问题。<br/>5、从江县城或榕江县城的车站都有去宰便乡的班车往来经过党扭村路口，每天3-4班，都在上午发车。<br/><br/>在从江车站，坐上开往宰便乡的班车，在中途的党扭村路口下车，沿着这条土公路往山里走，3公里就到党扭村，美丽的梯田开始出现在眼前。然后就是从党扭村到加车大寨的9公里路，沿途都是线条优美的梯田，几步一景。</p><p>大山深处，由梦编织而成的华章，在山坡上一挥而就，时而似行云的高亢，时而似流水的柔美。<br/>加榜梯田是一片需要用心去触摸和体验的梯田。<br/><br/>阡陌弯弯有人家，加榜梯田最美之处，就是在大山的怀抱之间，一圈一圈的梯田，突兀环绕着一个小寨子。<br/>田园生活在这里体现到了极致。这里的村民都非常友善，见到外来人都会打招呼问好。</p><p>夕阳下的梯田，如一页页优美的五线谱，弹奏着天籁之音，在群山间回荡，山风微微，我们希望时间就此停驻，不要走动，停在这人间最美的一刻。<br/></p>', 'admin', 4, 0, '26', 0),
(26, '浙江西塘2', '2017-09-18', '<p style="text-align:center"><br/></p><p>西塘是一座已有千年历史文化的古镇,素以桥多、弄多、廊棚多而闻名。</p><p><br/></p><p>早在春秋战国时期就是吴越两国的相交之地，故有“吴根越角”和“越角人家”之称。西塘地势平坦，河流纵横自然环境十分幽雅。古镇内鳞次栉比的明清建筑与纵横交错的市河相映成趣，街衢多依河而建，民居临水而筑，并以桥多 、弄多、廊棚多而著称。古镇处处绿波荡漾，家家临水人影。晨间，小桥流水，薄雾似纱；傍晚 ，夕阳斜照，渔舟扁扁，犹如一幅丹青，触目所见 ，如诗如画。当地民风醇厚，市场繁华，清逸秀丽。古宅如种福堂、薛宅，古庙如七老爷庙、圣堂及名园西园、陆坟银杏，都是玩赏游览之处，都会引出一段与历史交融的西塘佳话。西塘秀丽的风光、富饶的土地，蕴育出小镇浓浓的文化氛围，居民多舞文弄墨，爱养鸟种花。历史上民间风俗十分活跃，有逛圣堂、城隍庙会、护国随粮庙会等，这些传统风俗均以民间的文化活动为基础，所表现的形式有舞狮子、跳加宫，打莲湘、麟麒送子、财神送元宝、踏白船、抢轿等。</p><p><br/></p><p style="text-align:center"><img src="/ueditor/php/upload/image/20170918/1505739601107265.jpg" title="1505739601107265.jpg" alt="20120224055017709.jpg"/></p><p><br/></p><p>古镇区9条河道纵横交织，将古镇分为8个区块，在其中有27座古桥将市镇连通。古镇在春夏秋冬、晴阴雨雪的长久年代里，始终呈现着一幅，人家在水中，水上架小桥，桥上行人走，小舟行桥下，桥头立商铺，水中有倒影的不断变幻的水乡风情画。到了西塘，临河而建的沿街廊棚最为引人，这里的街道临河而建，商铺的生意就在河边做成。往昔，水乡农家的出行以河为道，以舟代步，许多交易只能在船上岸边进行，为此，一种连接河道与店铺又可遮阳避雨的特殊建筑———廊棚便应运而生，并代代传承，相沿成习。实用的廊棚是水乡特有的建筑，西塘至今保存着1300多米长的廊棚已变成当代人赏古、探幽的休闲一餐。廊棚一色的黑瓦盖顶，沿河而建，连为一体，绵延不断，催人发思古之悠情。古河道旁边还有不少古色古香的古居民，廊棚长长，古建筑重重叠叠，小船儿悠悠，俨然是一派古风犹存的情调。思古之幽情油然而生。西塘坐落在水网之中，这里的居民惜土如金，无论是商号或是民居、馆舍，在建造时对面积都寸寸计较，房屋之间的空距压缩到最小范围，由此形成了120多条长长的、深而窄的弄堂，长的超过百米，窄的不到1米，形成了多处“一线天”。</p><p><img src="/ueditor/php/upload/image/20170918/1505739616230090.jpg" title="1505739616230090.jpg" alt="20120224055023491.jpg"/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p style="text-align:center"><img src="/ueditor/php/upload/image/20170918/1505739628128665.jpg" title="1505739628128665.jpg" alt="20120224055030553.jpg"/></p><p><br/></p><p>西塘庙会：初一至初四，是中国人的狂欢节，在西塘西街、塘东街等一些老街，调龙舞狮、扭秧歌、打莲香等一些民间的庆祝活动再现。除此之外，还有各种小吃，像过年必吃的烧卖、馄饨、年糕以及各种各样有趣的玩意，如年画、捏泥人等。</p><p><br/></p><p>西塘祭灶：“二十三，糖瓜粘”，指的就是民间腊月二十三开始的祭灶活动。祭灶，是一项民间影响很大极为流传的活动，我国的春节也是从祭灶揭开序幕的。灶王爷可是年节敬奉中最露脸的一尊，人们用酒糟、饴糖、粘糕等“贿赂”灶神，求他上天讲好话，如果你留心的听，还会听到奶奶在叮嘱灶王：上天说好话，不好的可别说。在西塘古镇的“倪天增祖居”的老宅，您可以体验并参与“祭灶”仪式。</p><p><br/></p><p>西塘看社戏：春节期间，薛宅“水上舞台”将会上演江南传统的一些戏曲节目，内容精彩纷呈，隔河而观让人留恋往返。</p><p><br/></p><p>西塘学女红：体验古代仕女的韵味，学盘中国结、盘中国式纽扣、刺绣，如果您有兴致，可以在西塘最大的私人花园--西园，尽情尽兴穿针引线，重温旧时的快乐。</p><p><br/></p><p><br/></p>', 'admin', 1, 2, '27', 1),
(27, '河北石头村', '2017-09-18', '<p><strong>石头村交通</strong>：路线一:在石家庄火车站乘203路公交车到井陉县城下车，车费5元，然后换乘去于家村狼窝的汽车，需要花费2个小时：车费3元（此班车每天共16次）。也可每天下午3点在东方宾馆乘到于家村的车，车票7元。</p><p>路线二：北京乘火车T511至石家庄，早7：35发车，10：45到，在火车上就可以买到回程的车票。出站后再坐9路或到纪念碑坐114路公交车至西王长途客运站，坐长途小巴至井陉县城（6元），再从井陉县城坐小巴车到于家村。 <br/><strong><br/></strong><strong>回程：</strong>赶6：10左右有从于家村直接回石家庄的长途小巴 <br/><strong>自驾游</strong>：从正定古城出来，沿107国道继续前行，到石家庄后转入307国道向西行驶、在见到标有“天长镇”的方向拐进去，大约行至2.5公里处，在标有“狼窝”路标的路口拐入，行驶3公里时就可在路边见到标有到“石头村”的路标，按指示行驶即可到达石头村。</p><p><br/><strong>小贴士</strong>：从狼窝到石头村一段，路况较好，只不过进村一段是上山道，应多加小心。<br/><br/><strong>石头村住宿</strong>：村中农户家可接待食宿，需由村委会统一安排。在老乡家住宿并用餐每人每天20元左右，自点餐另计。<br/><br/><strong>特色美食民家饭:</strong>有烤馒头片和一种用野菜和面做的饼，当地人叫仙食，挺好吃的推荐一下。 所以强烈建议大家去石头村时要碗郃饹面尝个新鲜啊。<br/><br/><strong>景点地址</strong>：石家庄井陉县西北部<br/><br/><strong>门票</strong>：20元<br/><br/><strong>景区电话</strong>：0311-82022630</p><p><!--内容关联投票-->						 </p><div class="text-c" id="pages"></div><p style="margin-bottom:10px"><strong>相关热词搜索：</strong>石头 	交通 	住宿</p><p><br/></p>', 'admin', 1, 3, '28', 0),
(28, '安徽呈坎', '2017-09-18', '<p><img src="http://192.168.191.1/ueditor/php/upload/image/20170918/1505739720101748.jpg" title="1505739720101748.jpg" alt="20141019103137415.jpg" style="white-space: normal;"/></p><p>呈坎村始建于东汉三国时期，已有1700多年历史。早在宋代被理学家朱熹誉为“呈坎双贤里，江南第一村”。其实呈坎是一个很有怀旧气息的小县城，一条黄山脚下的偏僻小村，为何能自负地号称江南第一村？它的八卦究竟八卦在何处？</p><p><br/></p><p>在歙县，交通很方便，随便找了一部“地老鼠”，从歙县县城出发，一路是徽州常见的崎岖土路，一路上，棠越过去了，唐模过去了，潜口过去了，呈坎仿佛在路之尽头，路上的车辆越来越稀少了，路也艰难起来。</p><p><img src="/ueditor/php/upload/image/20170918/1505739723117472.jpg" title="1505739723117472.jpg" alt="20141019103142487.jpg"/></p><p>清早，晨雾浓厚，在田野间流动，清润的山水间，笼罩了一股淡淡的神秘感。呼吸一口乡野的潮湿新鲜的空气，在雾里的呈坎，似乎已经静静地等候了我们一千八百多年。如此寂寞偏僻的小村落，尽管已经是旅游旺季了，但游客来得很稀少，在村口没有看到一排排摆卖土特产纪念品的摊档，让我们不由感到清静许多，心情随着恬静、清新的空气越加畅快。</p><p><img src="/ueditor/php/upload/image/20170918/1505739727107239.jpg" title="1505739727107239.jpg" alt="20141019103147811.jpg"/></p><p><br/></p><p>村里有很多高大气派古旧的徽派建筑，除了一些重点国家保护文物，明清的房子都有人随意地住着，人们淡淡然然地生活做事，高兴苦恼似乎都在他们无所谓！村里有一种久违的乡村间的随意和寂寞，这里似乎永远也没有热闹过的时候。</p><p><img src="/ueditor/php/upload/image/20170918/1505739730154182.jpg" title="1505739730154182.jpg" alt="20141019103152742.jpg"/></p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505739735639839.jpg" title="1505739735639839.jpg" alt="20141019103154353.jpg"/></p>', 'admin', 1, 2, '29', 1),
(29, '安徽关麓', '2017-09-18', '<p><br/></p><p>黟县的关麓古村位于南屏以西2公里处。地处武亭山麓、西武岭脚，因西武岭有“西武雄关”之称，而该村居雄关之东麓，因此得名。古村内，古建筑鳞次栉比、错落有致。透露出一些苍凉感，可或许就是这种苍凉感才更显真实、更值得回味。</p><p><img src="/ueditor/php/upload/image/20170918/1505739770139702.jpg" title="1505739770139702.jpg" alt="20141023104504688.jpg"/></p><p>被称为“关麓八家”的八幢古宅建于清朝中叶，是一户汪姓徽商八兄弟的住宅。这八间大宅，互相独立，却又联成一体。每状古宅都有自己的天井、厅堂、花园、小院，但屋楼上下皆有门户走廊互相联结，形成一个整体。</p><p><img src="/ueditor/php/upload/image/20170918/1505739774927722.jpg" title="1505739774927722.jpg" alt="20141023104505580.jpg"/></p><p>这八间古宅的宅名大部分皆与书学相关，体现了徽商亦商亦儒的特色。以清代著名书画家汪曙故居“武亭山居”为首,自北向西依次为“涵远楼”、“吾爱吾庐”书斋、“春满庭”、“双桂书室”、“门渠书室”、“安雅书屋”、“容膝易安”小书斋。</p><p><img src="/ueditor/php/upload/image/20170918/1505739777606446.jpg" title="1505739777606446.jpg" alt="20141023104506146.jpg"/></p><p>古村内最有名的莫过于被称为“关麓八家”的八幢古宅。这“八大家”建于清朝中叶，是一户汪姓徽商八兄弟的住宅。这八间大宅，互相独立，却又联成一体。</p><p><br/></p><p>八大家为汪姓家八个儿子之宅，八个儿子为“宁”字辈，且每个人的名字中都有“金”字。原为八家联一体的宅子，后待老祖宗归西后，渐渐地分开成为独立的宅院。八家之中为老四和总宅，即老八的宅院为完整和大气。精细的木雕和周到的摆设很耐人寻味，就连其中的老人都长得很“古朴”。</p><p><br/></p><p>老屋都有精致的木雕和典型的徽州摆设，只是木雕上的人像脸谱有一些被破坏了，但余韵尤存，值得前往一游。</p><p><br/></p><p>交通:</p><p>可在南屏雇一辆马车前往，来回20元，如能搭上中巴，仅十分钟车程。</p><p><br/></p><p>门票:</p><p>26元。2010年1月1日起，关麓景区门票价格调整为每人次40元。</p><p><br/></p>', 'admin', 1, 2, '30', 0),
(30, '西藏 墨脱', '2017-09-18', '<div class="content" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 14px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"><strong style="padding: 0px; margin: 0px; font-size: 12px;">墨脱, 神秘的莲花圣地</strong><br style="padding: 0px; margin: 0px; font-size: 12px;"/><br style="padding: 0px; margin: 0px; font-size: 12px;"/>墨脱县，生态保存最完好的地方。位于西藏自治区林芝地区，地处西藏东南部，喜马拉雅山脉东端南麓。著名的雅鲁藏布大峡谷主体段都在该县境内。这里是西藏高原海拔最低，环境最好的地方，也是西藏最温和，雨量最充沛，生态保存最完好的地方。在这约一万平方公里的地域里发育、繁衍了丰富多彩、千姿百态、气象万千的自然资源。进入墨脱县，在几小时内便可领略到从高山寒带到热带雨林那千姿百态、丰富多彩的自然景观。<br style="padding: 0px; margin: 0px; font-size: 12px;"/></div><p><br/></p>', 'add', 1, 1, '31', 0),
(31, '棠樾牌坊群旅游攻略', '2017-09-18', '<p>一、棠樾在哪里<br/><br/>棠樾位于<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">安徽</a>省歙县郑村镇。“棠”字主要有两解，一解为棠梨树，又名杜树，为高大乔木；二解为海棠树，为落叶小乔木。“樾”字是树荫的意思。“棠樾”的意思大约就是棠梨树或海棠树的荫凉之处了，想必当年这里一定是棠梨或海棠遍地，荫及村舍，故而该村由此得名也未可知。<br/><br/>棠樾牌坊群位于<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">安徽</a>省歙县郑村镇棠樾村东大道上。共七座，明建三座，清建四座。三座明坊为鲍灿坊、慈孝里坊、鲍象贤尚书坊。这些牌坊按照“忠孝节义”的顺序排列述说着几百年来这座村庄的兴衰。在封建社会里，为了表彰在“忠孝节义”等各方面“功勋显赫”的官员，为朝庭兴旺作出的“杰出贡献”，当朝政府常常批准在这些人的故里村头，修建“功德牌坊”，借以号召人们以此为榜样报效朝庭。<br/><br/>歙县棠樾牌坊群一改以往木质结构为主的特点，几乎全部采用石料，且以质地优良的“歙县青”石料为主。这种青石牌坊坚实，高大挺拨、恢宏华丽、气宇轩昂。到了明清两代，牌坊建筑艺术也日臻完善。棠樾牌坊群就是明清时期建筑艺术的代表作。一连7座，建筑风格混然一体，虽然时间跨度长达几百年，但形同一气呵成。<br/><br/>歙县棠樾牌坊群，既不用钉，又不用铆，石与石之间巧妙结合，可历千百年不倒不败，一座一座直冲云霄，一座座精心设计和施工，一座又一座经官批御审，历代统治者用这一特殊方式来表彰那些在社会中有“贡献”的人，这也从侧面说明历代都极为重视“精神文明”的建设。<br/><br/>在古<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">徽州</a>大地,处处都有林立的牌坊,那是一座座血泪史,也是封建社会禁锢人们的精神枷锁.歙县的&quot;歙&quot;字就形象地说明了古<a href="http://www.goodzhen.com/html/anhui/" target="_blank" class="keylink">徽州</a>人口多如羽,欠可耕之地,一代代的徽州成年男子沿着新安江,外出谋求生计,足迹遍撒大地.妇女们种茶育子,侍奉公婆,遏守妇道,望眼欲穿,企盼郎归,一座座牌坊也就留下了一个个凄婉的故事。</p><p>二、棠樾牌坊群<br/><br/>门票100元/人　<br/>1、景区开放时间：7：30~17：30。 　　<br/>2、儿童身高1.1米以下免票，1.1-1.4米之间购儿童票。 　　<br/>3、半票对象为学生、儿童、60以上老人、现役军人<br/><br/><br/>1、鲍灿孝行坊<br/>2、慈孝里坊<br/>3、鲍文龄妻汪氏节孝坊<br/>4、乐善好施坊<br/>5、鲍文渊继妻吴氏节孝坊<br/><br/>1、鲍灿孝行坊<br/><br/>建于明嘉靖（1552—1567）初年。牌坊挑檐下的“龙凤板”上镶着“圣旨”二字，横梁正反各有一对浮雕雄狮，显得颇为英武。额题“旌表孝行赠兵部右侍郎鲍灿”。<br/>据《歙县志》记载：鲍灿读书通达，不求仕进。其母两脚病疽，延医多年无效。鲍灿事母，持续吮吸老母双脚血脓，终至痊愈。他的孝行感动了乡里，经请旨建造此坊。又因为他教育子孙有方、被皇帝“荣封三代”，并特地为其祖父立坊。由于鲍灿的曾孙鲍象贤是工部尚书，所以皇帝赠鲍灿“兵部左侍郎衔”。据说棠樾的孝子特别多，甚至可以说鲍氏家族是靠“孝”繁衍壮大起来的。这与历代帝王都把“孝道”当作修身齐家治国的根本思想分不开。</p><p></p><p>2、慈孝里坊<br/><br/>为旌表宋末处士鲍余岩、鲍寿逊父子而建，是皇帝新批“御制”的。据史书记载，元代歙县守将李达率部叛乱，烧杀掳掠。棠樾鲍氏父子被乱军所获，并要二人杀一，让他们决定谁死谁生，孰料父子争死，以求他生，感天动地，连乱军也不忍下刀。后来朝廷为了旌表他们，赐建此坊。牌坊上还铭刻了明永乐皇帝的《慈孝诗》日：“父遭盗缚迫凶危，生死存亡在一时。……鲍家父母全仁孝，留取声名照古今。”乾隆皇帝下江南时听到这个事后，欣然写下了“慈孝天下无双里，衮绣江南第一乡”的对联，并拨银将“慈孝里”牌坊重新修缮，又刻御题对联于其上。一座牌坊被几朝皇帝加封，这在我国历史上也不多见。<br/><br/><br/>3、鲍文龄妻汪氏节孝坊<br/><br/>建成于清乾隆四十九年（1784年），额刻“矢贞全孝”、“立节完孤”。据县志记载，汪氏为棠樾人，26岁守寡后，“立节完孤”，把儿子集成培养成歙县的名医。寡妇守节，培养后嗣，被宗法社会认为是最大的孝行，因为宗族是依靠血统来维系的。所以在汪氏80岁高龄时，族人为她请旌，建起了这座宛如其化身的牌坊。\n 　　<br/>所谓“饿死事极小，失节事极大”。在徽州这个程朱理学的故乡，女性受封建的节烈观念残害尤深，出现过一大批像《儒林外史》中的王三姑娘那样、“自愿”殉夫的节妇烈女。</p><p><img src="/ueditor/php/upload/image/20170918/1505739848818034.jpg" title="1505739848818034.jpg" alt="20120516060005780.jpg"/></p><p>4、乐善好施坊<br/><br/>　　建于清嘉庆二十五年（1821年）。据传，棠樾鲍氏家族当时已有“忠”、“孝”、“节”牌坊，独缺“义”字坊。其村鲍氏世家至鲍濑芳时，官至两淮盐运使司，掌握江南盐业命脉。他想求皇帝恩准赐建“义”字坊以光宗耀祖，便捐粮十万担、捐银三万两，修筑河堤八百里、发放三省军饷。此举获得朝廷恩准。于是，在棠樾村头便又多了一座“好善乐施”的义字牌坊。<br/><br/>5、鲍文渊继妻吴氏节孝坊<br/><br/>建于清乾隆三十二年（1768年）。因旌表鲍文渊继妻吴氏“节劲三冬”、“脉存一线”而建。据县志记载：吴氏，嘉定人，22岁嫁入棠樾，时小姑生病，她昼夜护理。29岁时丈夫去世，她立节守志，对前室的孤子元标视如亲生，尽心抚养，直至其成家立业。鲍元标也不负母恩，终于成为清季著名的书法家。年老之后，吴氏又倾其家产，为亡夫修了九世以下的祖墓，安葬好丈夫和族属中没有钱安葬的人。“厚葬”也是对祖宗的孝顺，当然也值得颂扬。吴氏还尽心侍奉患病的婆婆到寿终。她在60岁时辞世。\n 　　<br/>吴氏的举动感动了地的官员，遂打破继妻不准立坊的常规，破例为她建造了一座规模与其他相等的牌坊。尽管得此厚爱，但在牌坊额上“节劲三立”的“节”字上，还是留下了伏笔——把节字的草头与下面的“卩”错位雕刻其上，以示继室与原配在地位上是永远不能平等的。</p>', 'admin', 4, 20, '32', 0);
INSERT INTO `tp_message` (`id`, `title`, `time`, `content`, `author`, `cid`, `hit`, `top_img_id`, `collect`) VALUES
(77, '宁夏 南长滩村', '2017-09-18', '<p><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">南长滩村风光，一幅恬静的田园风光。</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">宁夏中卫黄河岸边有―个神秘的村落南长想村，村子虽小却拥有“三个宁夏第―“：宁夏黄河第一村、宁夏黄河第―渡、宁夏黄河第―漂。群山环绕使得南长滩村几乎与世隔绝、村里人姓拓拔自称是西夏后裔，并保存有完整的族谱。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">南长滩村位于甘肃与宁夏的交界处，与甘肃隔河相望。每年春天，这里绽开着如云的梨花，花繁似锦，因而，人们又称其为梨花村。7＂多年来，散落的西夏党项后裔的一支就与世无争的生活在这个小山村里面。而南长滩村秦汉遗址的发现，将人类生活在南长滩的历史提前到2000年前。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">山谷狭长、河道崎岖，仅容一车往来的路在其间回环曲折，止于黄河流入宁夏的第一处渡口。离了黄河渡口，沿着崖岸凿壁而成的窄道逶迤前行。展眼望去，两岸黑石耸出峙，脚下水势奔涌，险绝的景致依然如昨，峰回路转，依水傍山的南长滩村犹如弯新月，宁谧而温馨地躺在黄河的臂弯里。走进南长滩，恰如自进历史的沧桑里。那那一排排土坏房墙上，至今还保留着“文革”时喷印的毛选语录。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">南长滩村的人们，复姓拓拔，他们自称是西夏人的后裔，代代相传，并保存有完整的族谱。上世纪80年代，西夏学泰斗李范文先生考察认定这个村庄是西夏人的后裔。这是党项族作为一个民族实体消失以后，迄今发现这个古代少数民族最鲜活的载体。近年来，随着旅游者的增加，南长滩村与“神秘王朝”西夏王朝的关系，日益引起人们的兴趣，不少“驴友”爬山涉水来到南长滩村希望一睹“西夏村落”的容颜......</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">南长滩村还有同姓不通婚的不成文的规定，他们主要与邻近的甘肃和宁夏其他村庄的异性通婚南长滩村300棵古老的梨树，挤满了河滩，最年长的树龄有40年，撑起的树冠，能搭起3＂平方米的凉阴，惹得游人不愿挪步，伴着徐徐清风，纷纷席地而坐，与大自然亲密接触……</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">如今的南长滩，正在演义着“围城”里的故事。山里人耐不住寂寞，纷纷举家迁走，到川区寻求发展；城里人又一窝蜂似的涌向这里，观光旅游，吃农家饭，感受世外桃源的美好。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">旅游TIPS：</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">每年春天，南长滩村都会举沙坡头黄河梨花节！以欣赏百年梨树，观万千花簇，探访古老山村人家为主题的黄河梨花节吸引了众多游客前来参加。在南长滩村可以访古、赏景、漂流，还可以住农家屋、吃农家饭，亲身体验农家的悠然生活，真正体味“采菊东篱下，悠然见南山”的生活。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">宁夏回族自治区中卫市香山乡南长滩村。 乘汽车只能从红泉村走，约60公里路程，最好包车前往。中卫市旅游局电话：0955－7012682。</span></p>', 'add', 1, 2, '82', 0),
(133, '苏州东山古镇', '2017-11-25', '<p><span style="font-size:14px;font-family:等线;color:#444444;background:white"></span></p><p><br/></p><p><span style="font-size:14px;font-family:等线;color:#444444;background:white">苏州东山又名洞庭东山，位于苏州城西南23.5公里处，它是延伸于太湖中的一个半岛，三面环水，万顷湖光连天，渔帆鸥影点点。全镇总面积96.6平方公里，现有常住人口五万三千余人。境内自然风光秀丽，物产丰富。春天，百花盛开，万紫千红，茶芽滴翠；端阳佳节时，枇杷林披翠挂黄，杨梅树枝茂茁壮；秋天，漫山遍野的桔林，远看万绿丛中点点红，近看累累硕果树弯腰。在这著名的自然风景区内，青山如壁，林木茂密，泛舟湖中，令人乐而忘返。</span><span style="font-size:12px;font-family:等线;color:#444444"><br/> </span><span style="font-size:14px;font-family:等线;color:#444444;background:white">　　东山镇人文荟萃，历史文化底蕴深厚，仅明清两朝出自东山籍的状元、进士就达43人之多，当代更是名人辈出，东山人在全国各地就业的高级知识分子、教授268名，研究员30名、博士生导师30名、中科院院士4名及其他高级职称的地、市级以上领导约600名。东山镇交通便利，地理环境优越，在这块土地上，聚集了一大批知名企业和商家，并且在近两年呈较快的增长趋势。</span><span style="font-size:12px;font-family:等线;color:#444444"><br/> </span><span style="font-size:14px;font-family:等线;color:#444444;background:white">　　目前苏州东山宾馆是最具规模和档次的接待酒店,以成功接待党和国家领导人和外国元首等而著称. 　　苏州太湖东山又称洞庭山,既有山川林石之美,又有人文风物之萃.古人赞其有山之胜,石之胜,居之胜,花果之胜,山隐之胜,仙境之胜和山水之胜.洞庭之美,美在山,其中莫厘远眺;为东山全景之首.每当朝霞初起,晨雾未消,登莫厘峰观红日喷薄而出,更为瑰丽壮美,激动人心.莫厘峰青山如黛,风光旖旎,登临山巅,置身悠悠漂浮的白云之中,山下田畴村庄,时隐时现,宛如进入仙境一般. 　　莫厘峰支脉绵延起伏,主要山峰有萃峰,芙蓉峰,蓑帽峰,虾缀岭,荷盘顶,玉笋峰等.群峰环抱之中,有法海坞,萃峰坞,西坞等三十一出山坞.山坞中青翠掩映,竹林摇曳,稻菽成片,炊烟袅袅,萃峰寺,古雪禅院,法海寺,紫金庵等古寺庙,坐落期间,使坞愈显其静,景愈显其美.</span><span style="font-size:12px;font-family: 等线;color:#444444"><br/> <br/> </span></p>', 'admin', 10, 0, '141', 0),
(33, '河北正定', '2017-09-18', '<p><br/></p><p>正定是河北石家庄市隶属的一个郊县，但说起历史来，正定算得上石家庄的祖上，石家庄是上个世纪才由一个村庄兴起成为大城市的。而正定，甚至比北京还古老，以前正定被称为真定、镇州、恒州、常山等名，《三国演义》里，我们少年时代的英雄赵云出场时，自报家门：“某乃常山赵子龙是也”！正定人，以赵云为荣，现县城的广场被命名为“子龙广场”，有常胜将军提枪跨马的雕像。但三国时代的遗迹早已荡然无存，城市遗迹，据梁思成先生1933年的考察，城南门内南大街上有阳和楼，可信为宋代遗筑，可惜在解放前被拆毁，现只于城南门一座仿古城楼.<br/><br/><strong>临济寺青塔</strong><br/> 由广惠寺出，北行百余步，右拐，即到临济寺。临济寺还有僧人住持，面积不大，除一澄灵塔，寺院建筑簇新，仿清代之古，乏善可陈。只澄灵塔，梁思成先生说“也许是大定间物”，即北方金代南方南宋年间遗物。塔形有似北京天宁寺塔，不算少见。但临济寺之魂在于“临济义玄”大师。中华佛法，以禅宗流传最广，禅宗流派，以临济宗最为光大，这位临济义玄大师，即为临济宗的祖师，临济寺，是为禅宗的祖庭之一。所以，现在这个小小的寺院，题额为赵朴初手笔，就不难理解了。<br/> 临济义玄受传于黄檗希运，希运受传于百丈怀海，怀海受传于马祖道一，道一受传于南岳怀让，怀让受传于六祖惠能。传承“以心印心，心心不异”的顿教法门，后世有成语“心心相印”，却来于此。临济义玄的接引方法常有“喝”（可理解为“狮子吼”），对弟子或问禅之人冷不丁大喝一声，使之顿然领悟。与义玄同时有另一高僧，法名德山，德山大师的方法更“暴力”，他用“棒”，用棒打来使人顿悟，后有成语：“当头棒喝”，即来于此。<br/><br/><strong>广惠寺华塔</strong><br/> 穿过城南门往北，不过几十米，即看到红墙内有一形状奇特的塔，这便是广惠寺华塔。梁思成先生说：“若由形制上看来，这华塔也许是海内孤例。”广惠寺初建于唐贞元年间，现在的寺内，其他建筑毁损殆尽，只于下这形制奇异的塔，现在是国家级文物保护单位。<br/> 塔顶形式，层层叠叠密集的浮雕,近于天竺风格。</p>', 'dxy', 1, 18, '34', 5),
(34, '安徽宏村', '2017-09-18', '<p></p><p>在宏村我们可以感觉到得益于旅游开发的宏村人已经敞开心扉在拥抱一些改变。“当地人对外界的意识需要时间来转变。宏村是这样过来的。旅游已经改变了当地人的生存方式。”当中坤的高层发出这样的感慨时，一个问题从我们心底萌生出来：当这些古建筑内的原住民，其观念意识悄然改变之后，所谓非物质文化遗产的原住民生活方式，还能否留得住呢？</p><p><img src="/ueditor/php/upload/image/20170918/1505739861296790.jpg" title="1505739861296790.jpg" alt="20121030052641192.jpg"/></p><p>一路下来，我们几乎看到了中坤运作古村落旅游度假项目的全程缩影。虽然其中的每个项目都符合黄怒波作为旅游度假地产“教父级”的箴言：惟一的、不可再生的资源，但是也印证了那句老话：好酒也怕巷子深。</p><p>即便是不愁门票收入的宏村，中坤在当地的管理团队也还是会很介意一度跌出安徽省旅游景点门票收入三甲。虽然姚勇现在已经无须像当年中坤刚刚接手宏村时那样，亲自带队到外地去推介项目，但是他依然很回味那段“自己推销自己”的日子。</p><p>好酒也怕巷子深</p><p>眼下中坤在张谷英村项目上的营销之苦，就好比当年的宏村。幸运的是，宏村可以借力黄山板块的旅游人气。2002年，黟县政府为了从黄山景区引导游客，甚至从黄山景区下来，修了一条直达宏村的公路。</p><p><img src="/ueditor/php/upload/image/20170918/1505739868323211.jpg" title="1505739868323211.jpg" alt="20121030052644576.jpg"/></p><p>湖南岳阳县的张谷英项目就没有这么幸运。虽然张谷英村与岳阳楼、君山岛被很多旅行社列为“岳阳游”三个必到的景点，但是岳阳楼现在的人气都还只有宏村一半。虽然同为湖南最有影响力的旅游圣地，岳阳楼只适宜站在楼上眺望洞庭湖，没有张家界那样能让更多人有游玩、休闲的兴致。中坤在岳阳运作张谷英项目的团队都羡慕，张谷英村如果处在张家界板块，可能带动更多游客。</p><p><img src="/ueditor/php/upload/image/20170918/1505739873129082.jpg" title="1505739873129082.jpg" alt="20130203121645523.jpg"/></p><p>正因如此，姚勇希望在给张谷英这样的“三类景区”设计旅游产品时，反复强调要用“捆绑”的营销策略：比如用“名楼名人名村”的概念，统一对外营销。“名楼”就是岳阳楼，“名人”是屈子祠，“名村”即张谷英村。甚至希望能把张谷英村跟旅游开发已经很成熟的湘西、张家界联系起来，做一条旅游线路。</p><p>很难设想，如果没有“申遗”成功，如果《卧虎藏龙》取景地不是在宏村，如果当年江泽民视察西递、宏村时是按照事先约定而非破例地给宏村亲笔题字，宏村可能会错失很多火爆的机会。</p><p>作为中坤进入宏村时第一批招募的当地导游，黄洁已经转岗到负责宏村景区的销售。她告诉我们，现在与宏村合作的旅行社共有118个，中坤往往会用宏村的影响力来向旅行社打包推广中坤在黄山板块的其他项目。</p><p>比如，如果旅行社带团只参观宏村一地的景点，中坤给的门票折扣可能是9折、8折；如果是两点联合，宏村外加南屏、关麓其中任一景点，给7.5折；如果是三点联合，则可能给7折。</p><p>鱼与熊掌</p><p>中坤在古村落的旅游度假项目，既得益于古村落，也受制于古村落。保护与开发就像是鱼与熊掌，如何兼得，这其中需要有商业模式创新的智慧，有时候更要有游走于政策边缘的勇气。</p><p>我们听中坤的管理层讲，他们如何在一些项目上“有开创性”地尝试收购国家文物资源，讲他们如何在古村落项目的周边开发度假会所、度假酒店、户外运动等敏感项目，讲他们如何与古村落方合作，对方以资源为不动产作价、中坤以资金入股的方式运作股份制公司时，我们既感慨于这家目前中国最有声望的旅游度假地产商如何在运作模式上敢于创新，也多少为他们这种略带“打擦边球”味道的做法捏把汗。</p><p><img src="/ueditor/php/upload/image/20170918/1505739880616344.jpg" title="1505739880616344.jpg" alt="20121026122915766.jpg"/></p>', 'admin', 1, 0, '35', 0),
(35, '安徽三河', '2017-09-18', '<p></p><p>三河镇地处皖中，位于安徽省会合肥市西南部，隶属省会合肥管辖，与巢湖、六安两市交界，东濒巢湖，丰乐河、杭埠河、小南河三水流贯其间，位于国家5A级旅游景区黄山、九华山的黄金旅游线上，合铜公路穿镇而过，京九铁路、合铜黄高速傍镇而过，交通便利，区位条件优越。全镇面积70平方公里，辖19个村，7个居委会，人口7.2万，其中三河古镇景区面积4.71平方公里，人口3万多。</p><p><img src="/ueditor/php/upload/image/20170918/1505739946682613.jpg" title="1505739946682613.jpg" alt="20141026103344458.jpg"/></p><p>三河历史悠久，文化灿烂，人杰地灵。三河有着2500多年的悠久历史，历史上曾是兵家必争之地，据《左传》记载，春秋时期，吴楚相争在此发生了“鹊岸之战”。1858年，太平天国将领陈玉成、李秀成于此大败湘军，创下了著名的“三河大捷”。悠久的历史渊源，厚重的文化底蕴，使三河镇内遗存着古河、古桥、古圩、古街巷、古城墙、古庙宇、古炮台、古茶楼等“八古”景观。</p><p><img src="/ueditor/php/upload/image/20170918/1505739950251176.jpg" title="1505739950251176.jpg" alt="20141026104621387.jpg"/></p><p>三河区位优越，商业繁荣，经济发达。三河历来是商贾云集之地，是巢湖西岸和大别山东麓的重要商品对外集散中心，也是周边区域经济、文化、信息的交汇中心，素有 “小南京”、“小上海”和“皖中商品走廊”之美誉。目前，三河镇对周边辐射直径近50公里，辐射人口50多万人。</p><p><br/></p><p>三河风光旖旎，生态优美，景色宜人。镇内河环水绕，十里长街，镇外河网纵横，圩堤交错，具有“外环两岸，中峙三州”的独特地貌，拥有长约10公里的湖岸线和万亩荷花塘。桃花岛风光秀美，姑山岛秀丽多姿。其中，水生蔬菜、水产养殖、葡萄种植是肥西县三大特色农业产业基地之一。</p><p><br/></p><p>三河民俗丰富，美食独特，荣誉多多。三河是合肥市地方剧种--庐剧（被评为国家非物质文化遗产）的发源地，素有“庐剧之乡”的美誉。“三河菜系”是徽菜之精品，享誉皖中大地，传统菜肴、地方点心令食客赞不绝口。近年来，三河镇先后被确立为全国文明村镇、国家发展改革试点镇、安徽省环境优美镇、安徽省卫生镇，是中国历史文化名镇、国家AAAA级旅游景区、“合肥十景”之一。</p><p><br/></p>', 'admin', 1, 0, '36', 0),
(36, '安徽万安', '2017-09-18', '<p></p><p>万安是徽州四大古镇之一，地处休宁县城城东四公里处。它依傍于横江北岸，临水而立，皖赣铁路，屯景公路横贯全镇。镇东尽头是风光的绮丽的古城山。</p><p>　　三国吴永安元年(258年)至隋开皇九年(589年)，古城山一直为县治驻地。隋末汪华起兵，统辖歙、宣、杭、睦、婺、饶六州，也曾设治于此，并改古城山为“万寿山”，在山上建妨筑庙，大兴土木。明末清初，民族英雄金声招 募义军数千人，屯扎于此，后北上抗清，兵败被杀。</p><p><img src="/ueditor/php/upload/image/20170918/1505739991909046.jpg" title="1505739991909046.jpg" alt="20140327041518222.jpg"/></p><p>　　万安地处盆地，依畔横江，又当休宁县通往州府的要道，遂成为早期外来居民的聚居地。在陆路不发达时，万安一直是古徽州重要的水运码头，来往行商众多，渐成集镇。镇街长约5华里，明清均为休宁九大街市之首， 有“小小休宁城，大大万安街”之誉。</p><p>　　万安古镇风貌及格局是非常具有特色的，主要体现在“小桥、流水、人家”典型江南水乡风貌和与横江平行发展的鱼骨状街巷空间风貌上。万安除横江外还有松萝水、榔源水等支流穿越古镇，有古桥“富来桥”、“轮车桥”等联系镇区交通，沿河建筑与古桥、小溪形成亲切宜人的江南水乡环境，生活气息浓郁。</p><p>　　万安古镇的街和巷，构成其鱼骨状的街巷系统格局。古镇的东西向仅有一条主街——万安街，垂直其街衍生出十数条巷子，仿佛是鱼的肋骨。这样一条并不复杂的道路结构长期服务着古镇繁忙的内部交通和日常生活，构成古镇独特的街巷空间格局。由古镇的布局结构，可以非常清楚地看出街巷和码头的直接联系——亲水性。由水体、古水埠码头、古桥、岸线的内凹、外凸、滨河古建筑及渡口、渡船等共同构成万安秀丽宜人的滨河水景观。</p><p><img src="/ueditor/php/upload/image/20170918/1505739996124529.jpg" title="1505739996124529.jpg" alt="20141020031709896.jpg"/></p><p>　　以商业功能为主的“街”和以交通功能为主的“巷”，构成了镇区内部颇具特色的街巷空间。万安街两侧原有大小商号、店铺400多家，沿街开敞的店铺和传统民居封闭式立面，形成蜿蜒弯曲、不尽相同的风貌。这是一条目前在安徽省内保存完好的、不多见的、风貌古朴的古代商业街。由于许多商业活动在店堂内，由建筑的山墙构成的空间“巷”在宽度上明显比街道低一个等级。但万安传统建筑中，由于经营性建筑为主体，所以封火山墙较居住型的徽州古镇要少一些。依势而下临码头的巷以河道为底景，并不显得非常狭窄。</p><p><br/></p>', 'admin', 1, 0, '37', 0),
(37, '平遥古城游览攻略', '2017-09-18', '<p>一、梳理平遥古城的历史脉络<br/><br/>平遥在山西的位置<br/><br/>　　平遥古城在山西太原以南90公里处，是联合国批准的世界历史文化遗产之一。平遥古城史我国境内保存最完整的一座明清时期的中国古代县城的原型，是由完整的城墙、街巷、店铺、庙宇、民居组成的大型古建筑群，完整的反映了明清时期的历史风貌。<br/>　　古城的票号是中国金融发展史上的里程碑，一度成为中国近代金融业的控制中心；古城的传统民居体现了独特的地方风貌。1986年，平遥古城被国务院公布为国家历史文化名城，1997年，被列入“世界文化遗产”。<br/>　　关于平遥，只要曾深入到平遥每一寸土地中的游者都会为它感慨良多。那保存完好的古城墙，明清时代遗留的建筑，以及曾经显赫的全国金融中心的地位，无疑能激起人们对过去那些辉煌岁月的向往。也许正因为这样油然而生的向往，才会有越来越多对这些古老的历史感兴趣的游人，如一个虔诚的朝圣者般来到这位老人跟前，神圣地仰望着他的光芒。<br/>　　古城不同于紫禁城的庄严，有别于钟鼓楼的肃穆，相异于姑苏城的柔媚。它的气质从每一座瓮城溢出，从每一条街道流出，从每一扇旧窗淌出，从每一道雕纹渗出……古老却并不荒废。走在明清一条街，在街中心雄姿壮观、飞檐翘角的市楼映人眼帘。街道两旁那点亮的红灯笼、历史气息浓重的字号和传统风格的建筑，仿佛回到辉煌的晋商时代。所以在平遥，寻找晋商往昔的辉煌是一定要做的事情，因为这里曾是“晋商”的发源地之一，中国第一家现代银行的鼻祖“日升昌”票号就诞生在这里。<br/><br/><br/>二、游览平遥古城必做的N件事:<br/><br/><br/>1、古城墙上俯瞰历史的脉络<br/>　　四座角楼，六道城门，七十二座敌楼，三千个垛堞。这些个数字，拼凑出一堵古城墙的盛世。<br/>　　迎曛，拱极，太和，亲翰，永定，凤仪。光听听这六座城门的雅称，便已让人肃然起敬。试想，若无昨日的显赫，又哪来今日的威仪？<br/>　　七十二座敌楼平面呈方形，绵延在视线之外，极目不可尽。奎星楼顶端悬挂的红灯笼，日晒雨淋，沾染了风尘，色泽已变得黯淡陈旧，却依然有丝缕媚气隐现。衬着这巍然城郭，似霸王虞姬，有种刚柔并济的美。<br/>　　流年如逝水，朝代会更替，红颜终将化白骨。惟有这古城墙，年年镇守护城河畔，笑看春风度。<br/><br/>TIPS：<br/>不少游客都会一大早去登城墙，不想人挤人的话，其实大可选择傍晚时分来这里，下班后是不收门票的。灰色的城墙在夕阳的映衬下，更有一分沧桑之感。而当华灯初上，勾勒出城墙和角楼的轮廓，又多了一种瑰丽的神秘感。<br/>也可以租一辆自行车，在平遥古城墙上溜达，很惬意，你肯定美得不行。</p><p>2、明清一条街，体味时光倒流<br/><br/>或许是上苍的格外垂青，这里在历史上没有遭到任何的损毁。应景的游人大多走不到这儿，道路便骤然开阔起来。街道两侧是几百年前的古老建筑，偶尔还有身着清兵服装的“更夫”敲锣走过。铺面都开着，生意却做得漫不经心。经营的古董、铜钱、瓷器、子弹壳，甚至生锈的日本军刀零零落落地摆放在街头，在夕照中泛着微光。<br/>你还可以看到一些只在电影中见过的招牌，比如镖局，走进去还可以了解这家镖局的创办及历史。而每个体面门庭的花岗岩门坎上两道很深的车辙印痕，则告诉人们当年繁华的街市。</p><p>3、探秘日升昌票号<br/><br/>　　来平遥如果不去“日升昌”看看就算白来了，倒不是因为这里有多好玩，而是在于这是中国历史上的第一个票号，从此结束了千百年来镖局押送白银的历史，是“汇通天下”成为可能。如今的“日升昌”虽然早已见不到繁忙的生意场景，但游客可以参加到模仿当年账房先生写汇票、写聘书的情境中，花一点钱带封“骨灰级”的汇票或大掌柜的聘书回家，也是一种难忘的回忆。<br/>　　在街上另一个票号旧址“协同庆”里面，则可以亲身下到隐秘的地下金库里面，看着当年修建的巨大藏金屋，尽管现在里面放得是假金假银，也还是让人忍不住心痒痒。<br/><br/>票号必游之处：<br/>　　在“柜台”的墙上，有当年日升昌创造的防假密押，这是兑换汇票的一套密码，里面暗藏汇票的时间、地点、金额等内容。正是依仗这套密码，100多年来，日升昌从未发生过错误。<br/>　　在“会客厅”大炕头旁边的炉煤灰地下，更是日升昌的身家性命所在的常备金库入口，通常金库都会有几十万到几百万的库存银两。<br/><br/></p><p>4、亲历县太爷升堂<br/><br/>　　没想到咱们现代人也能触摸到古代的衙门，尽管已经过去了600多年，平遥县衙仍然完好地保存了下来。六进的大院落，占地达2.6万平方米，雕梁画栋、青砖黛瓦都是文物，默默讲述着149任知县在此为官的故事。除了静态的建筑和文物之外，我们还“亲历”一次县太爷升堂判案的全过程，这些原本只在影视上看见的场景，如今就活生生在眼前，真是有趣。<br/><br/>Tips：<br/>　　表演时间：周一至周五早10点、11点、下午3点半及周六、日早10点、11点、下午3点、4点。<br/>　　营业时间：早8点~晚7点<br/>　　地址：平遥古城县衙门街，沿南大街往南过永庆斋客栈后往西100米即到。<br/></p>', 'admin', 4, 0, '38', 0),
(38, '云南宝山', '2017-09-18', '<p></p><p>宝山石头城位于丽江城北110公里处的金沙江峡谷中，因百余户人家聚居在一座独立的蘑菇状巨石之上而得名。石头城三面皆是悬崖绝壁，一面石坡直插金沙江，仅有南北两座石门可供出入，是一座真正的天险之城。</p><p><br/></p><p>整个小城建在金沙江畔的一块巨岩上，城中的所有物品都是用石头做成，石床、石枕、石桌、石凳、石灶、石缸。相传，当年忽必烈南征时，大军就是经过石头城渡过金沙江的。</p><p><img src="/ueditor/php/upload/image/20170918/1505740031850867.jpg" title="1505740031850867.jpg" alt="20140310054949998.jpg"/></p><p>宝山石头城建于元朝初年，当时为云南行中书省丽江路宣抚司所辖七州之一宝山州治所。管辖今大具、大东、鸣音、宝山、奉可五个乡的范围。宝山州在长江第一湾的怀抱之中，三面环江形似口袋底，境内山川纵横，河谷深切，有茂密的原始森林和丰富的动植物资源。气候十里不同天，山头冷凉，河谷干热。</p><p><br/></p><p>宝山石头城东临奔腾咆哮的金沙江，南扼陡绝如削的岩可渡，西依层峦叠嶂的牦牛岭，北据峭拔险峻的太子关，四周山坡上辟有重叠连绵的层层梯田，犹如一朵秀丽的灵芝挺立在千山万壑之中，远道而来的旅游者，往往产生别有洞天的强烈感受。</p><p><img src="/ueditor/php/upload/image/20170918/1505740036130554.jpg" title="1505740036130554.jpg" alt="20140310055707760.jpg"/></p><p>宝山石头城纳西语称为“拉伯鲁盘坞”，意为“宝山白石寨”，城内瓦屋鳞鳞，巷道纵横，纳西族居民辟岩建屋，房屋柱石和房沿石均随势打成，古朴自然、奇绝无穷。</p><p><br/></p><p>石城周围所有的山坡，凡能开垦的全都辟为梯田，与元江的梯田相比，宝山的梯田还有独创，那就是别具一格的自流灌溉系统——不是上田满了流下田，而是在每块田的下面都修有暗渠，形成一条由暗渠和水口形成的浇灌网络，堵住暗渠水口，水便会流灌整块田地，满水后打开暗渠水口，再堵上灌田水口，水由暗渠流下，便可浇灌下层田块——不会产生夺肥现象，这种梯田建设的独创，充分显示出古代纳西族人民的聪明才智。</p><p><br/></p>', 'admin', 1, 1, '39', 0),
(39, '云南大理', '2017-09-18', '<p></p><p>大理古城始建于明洪武十五年（公元1382年），又名叶榆城、紫禁城、中和镇，位于下关以北十三公里处，背靠苍山，面临洱海。是一九八二年二月国务院批准的我国第一批二十四座历史文化名城之一，也是全国44个重点风景区之一，位于南诏和大理国都城遗址的东部，始建于明朝洪武15年（公元1382年），历代屡经修建。</p><p><img src="/ueditor/php/upload/image/20170918/1505740071122640.jpg" title="1505740071122640.jpg" alt="20120222112358345.jpg"/></p><p>大理城方圆12里，古城城墙高2丈5尺，厚2丈；东西南北各有一城门，上有城楼，分别叫做通海、苍山、承恩、安远。城的四角还有角楼，也各有名称为颖川、西平、孔明、长卿。城墙的外墙为砖，上列矩碟，下环城沟。城内市井俨然，布局呈棋盘状，从南到北有5条街，从东到西有8条巷。</p><p><img src="/ueditor/php/upload/image/20170918/1505740075792559.jpg" title="1505740075792559.jpg" alt="20120408034814547.jpg"/></p><p>古城外有条护城河，城内街道都是南北、东西走向，是典型的棋盘式布局。古城的建筑为清一色的青瓦屋面，显得十分的古朴。</p><p><img src="/ueditor/php/upload/image/20170918/1505740078492972.jpg" title="1505740078492972.jpg" alt="20120408034817760.jpg"/></p>', 'admin', 1, 2, '40', 0),
(40, '云南独克宗', '2017-09-18', '<p></p><p>独克宗又称建塘古城，至今已有一千三百多年的历史，它位于云南香格里拉县中心，古城依山而建，古城中石板街两侧多是高大的藏式老屋，古朴沧桑，凝聚了一千多年的厚重古韵，镌刻着历史沧桑的痕迹。这里曾是历朝历代马帮进藏后的第一处驿站，房屋大多采用当地一种白色的黏土做的房屋外墙的涂料，白色的外墙在月光映照下显得洁白纯净，因此而得名月光城。</p><p><br/></p><p>在藏语中，独克宗的意思是建在石头上的城堡，另一种意思就是月光城，以前的古城安详静谧，在酒吧和客栈入驻后，那些光线暗淡的酒吧和茶肆有人静静在在里面喝茶闲聊，和谐恬静，入夜的古城那些古朴的藏式房子在月色下的倒影被灯火点亮，一些人在其间欢歌起舞，醉酒高笑，或者是一些人低声私语，独自发呆，他们为这座美丽古老的小城增添了崭新的色彩与魅力。</p><p><br/></p><p>在独克宗的石板街有很多藏味小餐馆，在这里可以尝到正宗的藏区美食，比如牦牛肉、酥油茶等，其中特别推荐牦牛肉火锅，去独克宗别错过了，是值得品尝的特色美食。</p><p><br/></p>', 'admin', 1, 0, '41', 0),
(41, '凤凰古城旅游攻略', '2017-09-18', '<p></p><p>一、在凤凰必做的一些事<br/><br/>1、闲逛边城，感受凤凰<br/><br/>凤凰的美，不是江南小镇的那种柔美，也不是丽江那样的浮华。它就像一位遮着头盖羞涩湘女，含蓄而又多情。凤凰青山环抱古城，沱江水绕城过，红红石板街，小巧吊角楼，风雨古长城，沧桑老城墙……这就是凤凰古城，它需要你静心去品味。<br/><br/>每一次勾勒心中的凤凰，脑海中总是浮现起这样的一幅画面：清清淙淙的沱江水，踏着岁月的脚步，轻轻地穿过古城沧海桑田的时光。蜿蜒的江边，逶迤着一排排古旧错落的吊脚楼，而那驻立在老城墙脚下的旋转水车，年复一年地转过了小城的四季炎凉……清晨的第一声鸡啼唤醒了沉睡的小城，早起的浣衣女子，“嗒嗒”的棰击声敲碎了黎明的宁静。<br/><br/>清晨的凤凰<br/>凤凰如今已经变得越来越市侩了，也许初到此处的游客会略有失望。但清晨的凤凰褪去夜晚它略带繁华的外衣，此时的凤凰，才真正地属于她自己，宁静且富有诗意。浮躁的心是体会不了凤凰的，也许只有一颗宁静的心沉浸其间，才能触摸到凤凰的灵魂，终究，凤凰是不适合我们这些匆匆的脚步的。<br/><br/>清晨的凤凰是一定要好好的逛一逛的，在薄薄的晨雾中，不论是一个人，还是与爱人一起漫步在安静的凤凰小城里，都能体会到凤凰展现出的另一种静谧的美。　<br/><br/>晨光中的沱江一片流光溢彩，那摇橹的船翁乘一叶扁舟带着他的翠翠在晨风中踏歌而来。“吱呀”一声，吊脚楼上不知谁家的闺女悄悄地推开窗，羞红了脸向外张望，寻找那个唱了一夜情歌的痴痴少年郎……我想我真是迷醉了，醉在沈老用那点点怀念，丝丝惆怅为我织成的一张网，用那声声叹息，浅浅忧伤为我酝酿的乡愁……<br/><br/>摄影提示：<br/>清晨的凤凰是最适合摄影的时刻，通常在早上6点至7点前，凤凰是最安静的。也是游人最少的时候，适合拍晨雾中的凤凰城。<br/><br/>推荐摄影地点：沱江，跳岩，万名塔，城墙，吊脚楼，大水车，虹桥等。<br/><br/>2、沱江泛舟，品味吊脚楼<br/><br/><br/>去凤凰最不能错过的就是住吊脚楼见识一下。凤凰古城最有名的是那一幢幢古色古香、富有浓郁土家族风韵的吊脚楼，但河畔的吊脚楼大多已不在了，只有十多间老屋，细脚伶仃的木柱立在河中，托起一段沉沉的历史。<br/><br/>吊脚楼也叫“吊楼”为苗族、壮族、布依族、侗族、水族、土家族等族传统民居，在湘西、鄂西、贵州地区的吊脚楼也很多。吊脚楼多依山就势而建，呈虎坐形，以“左青龙，右白虎，前朱雀，后玄武”为最佳屋场，后来讲究朝向，或坐西向东，或坐东向西。<br/><br/>即使你不愿意住在里面，也可以找一家临江的吊脚楼酒吧或咖啡馆歇歇脚。<br/><br/>沱江河是凤凰的母亲河，她依着城墙缓缓流淌，世世代代哺育着古城儿女。坐上乌蓬船，听着艄公的号子，看着两岸已有百年历史的土家吊脚楼，别有一番韵味。顺水而下，穿过虹桥一幅江南水乡的画卷便展现于眼前：万寿宫、万名塔、夺翠楼……一种远离尘世的感觉悠然而生。<br/><br/>看河边高低错落的吊脚楼，穿过和两岸的石墩被磨得光滑，身边似乎找不到什么匆忙的身影，吊脚楼的窗口总是三三两两的坐着几个或者一身休闲装扮，或者一身湘西民族服装的人，在那里看着河景聊天。<br/><br/>推荐的吊脚楼：<br/>茂松园<br/>　　沱江边上的吊脚楼，打开窗子就可以看到沱江水。不过只能住30个人。有8个临江房间。<br/>价位：双标间100元一间。临江120元一间。<br/>TEL：13574424677<br/><br/>茂盛园<br/>　　在万名塔对面的吊脚楼，打开窗子就可以看到沱江水。可以接待42个人。有12个临江房间。<br/>价位：双标间100元一间。临江120元一间。<br/>TEL： 15174356455<br/><br/>凤凰公寓<br/>　　有两栋房屋，一栋在南华打桥旁，离江边100.可以看到整个凤凰城的夜景。房间设施全新。24小时热水。一栋在民族村。离虹桥只有200米。虽都是江边可从这里看不见虹桥.房间舒适宜人。<br/>价位：100元一间</p><p>TEL： 15174387851</p><p>3、享受凤凰的白天与黑夜<br/><br/><br/>凤凰的古城自然也不再可能还像百年前那样幽静而安宁，但只要我们能够去体会那种感觉，也是一样的美。<br/><br/>凤凰的街道尽管古朴，但是也有着繁荣的商业，现在已经很难看到古时挑担，牵马，背背篓的乡土风情。但是那种市井的情趣依然随处可见。<br/><br/>街道的两旁几乎是淋漓满目的铺子：玉器行，银号，蜡染店，苗族服装店……不少铺面仍保留着各式的格子窗，雕花窗，以及屋檐象征着图腾檐角。<br/><br/>凤凰的夜有着宁静的外表，内心却是沸腾的。成千上万的游人在它体内搅出无数个气泡，争先恐后地冒开，发出汩汩的巨响。它仿佛在不停重复着一句话：靠近一点，靠近一点，靠近一点。<br/><br/>酒吧 凤凰夜的精髓<br/>都说，万万不能错过的，是凤凰的酒吧，这是凤凰的夜的精髓。不论你与它匆匆擦肩而过时听到的是里面重低音的巨响，还是扭动身体的人群，或者暗色暧昧的灯光。<br/><br/>在凤凰，晚上泡酒吧追求的是一种感觉，这是一个可以让爱流浪的人休憩的地方。从凤凰古城北门沿沱江到虹桥短短的500米距离，沱江两边聚集了很多家风格各异的酒吧。<br/><br/>一般酒吧里的啤酒都与日常我们在酒吧里喝的差不多，如科罗娜、百威、小蓝带、喜力、太阳……大概都是20～30ＲＭＢ／瓶。也有现磨咖啡，在一般25-40ＲＭＢ／杯。建议除了喝酒和果汁咖啡外，叫点当地的小吃。<br/><br/>威马酒吧：位置极佳，号称广东人的大本营，路过时果然发现里面鸟语阵阵。<br/><br/>地址：老营哨13号！<br/><br/>青石酒吧：位于游船码头对面，原始人酒吧旁，是凤凰的最舒适的酒吧之一，二层的木质地板上有很多大垫子，可以随意坐、卧。<br/><br/>推荐：青石酒吧的青石之恋鸡尾酒（酒精度较高，色彩艳丽）<br/>地址：老营哨14号<br/><br/>阿罗哈酒吧：“阿罗哈”是夏威夷土语“你好”、“我爱你”，2位老板都是湖北的而迷失在凤凰的人，舍去很不错的工作，来到凤凰古城经营着自己的淘金梦。<br/><br/>地址：老营哨8号，0743-3261707<br/><br/>水木酒吧 ：沱江跳岩前面，平时人来人往，二楼是观景的发地方。<br/><br/>地址：沱江镇老营哨119号，0743-3261149<br/><br/>古城守望者酒吧：是个规模较大的酒吧，环境位置好，地方较大。与水木酒吧相邻，二楼靠江的观景窗，可以看到跳岩的全貌。<br/><br/>　　古城守望者位置最醒目，是最好找的一家，你到了跳岩，就可以看到它了。<br/><br/>推荐：古城守望者（鸡尾酒，色味都像巧克力）<br/>地址：沱江镇老营哨118号</p><p>4、走走戴望舒描述的悠长的雨巷<br/>　　凤凰是适合发呆的，若不是丽江抢先注册了“柔软”这个词，它或许更适合凤凰。<br/><br/>诗人戴望舒笔下曾有这样的场景：悠长的雨巷，丁香般的姑娘打着油纸伞，脚步轻敲在麻石街上。如今，我们再也寻不到如此诗意的场景了。然而在凤凰却有那样的小巷。如果你心中仍有这雨巷情结的话，不妨在雨天时候去那儿寻寻。<br/><br/>当然，凤凰也不是那种人人都不食烟火的小城，这里一样有着市侩的一面。好在，这里的人们有着随遇而安的性情。兵来将挡，水来土掩。这种性格也被发扬在今天。上至耄耋老者，下至豆蔻女童，全民皆商。这座本该是世外桃源的小镇，真的拥有我们想象中的气度吗？还是，也许没有我们，它会更幸福一些呢？<br/><br/><br/>5、关于凤凰的咖啡馆<br/><br/>Soul Café<br/>　　门口悬一块扎染蓝布。写着Soul \nCafé，和一个隶书［素］。门口旁边是下楼的楼梯。还有一个小书架。翻得烂烂的书。小说，散文，杂文，哲学……各种读本。书架顶层一个盒子，装着老板收集的和出售的雪茄。桌椅被漆成咖啡色。全木质。散落阳光和爵士。这样美丽的地方，永远少不了这样美丽的小资女子。<br/><br/>提示：Soul Café有两家，分别开在沱江左右两岸<br/>凤凰左岸：湖南凤凰县沱江镇回龙阁17号Detail<br/>Tel：86－(0)743－3229927 P.C：416200<br/>凤凰右岸：湖南凤凰县沱江镇老营哨18号1楼<br/>Tel：86－(0)743－3260396 P.C：416200<br/><br/>摄影提示：<br/>素咖啡就在凤凰水车的上面，这里美女云集，可以拍照，但不要直接对着姑娘家拍个不停，会引起美女们的反感的。<br/><br/>俏皮的凤凰，总在天堂与凡间之间勾留着那最后的渴望。像那绝望般的单恋，在你心灰意冷的时候，给你一个摄人心魄的媚眼，一个美若仙人的微笑。<br/><br/>凤凰，并不适合匆匆的脚步：“青山、绿水、古桥、吊脚楼；寺祠、亭阁、民居、石板街；傩戏、苗歌、鼓舞、拦门酒；城楼、碉卡、烽烟、镇竿人……”人们就是用这些词来概括凤凰的。</p><p>6、放河灯<br/><br/>许愿灯是唐朝时出现的，当时凤凰城经常出现洪涝等灾害，苗民为了祈求来年风调雨顺就在沱江上放起了许愿灯，到后了来，人们用许愿灯祈求幸福平安和美好爱情。当夜幕降临，可以拿着五彩缤纷的许愿灯，当人少的时候我们可以到沱江去放许愿灯，祈福全家平安。<br/><br/>提示：<br/>河灯通常都在沱江跳岩边卖，卖者众多且集中，有心型、全家福型、船型等造型。<br/>价格：2~20元，大个的河灯可以侃价。</p><p>二、凤凰景点<br/><br/>提示：凤凰只有联票出售，通票（古城内11个景点）168元。通票11个景点包括：南方长城、苗人谷、沈从文故居、古城博物馆、崇德堂、东门城楼、杨家祠堂、沱江泛舟、熊希龄故居、虹桥艺术楼、万寿宫九景。现在不单卖古城内九个景点票，采用一票制168元。售票处在文化广场旁，旅游局售票窗口，跳岩边上的城墙下还有一售票点。<br/><br/>1、沱江跳岩<br/>2、沈从文故居<br/>3、熊希龄故居<br/>4、杨家祠堂<br/>5、田家祠堂<br/>6、虹桥、北门城楼、石板老街<br/>7、奇梁洞<br/>8、夺翠楼<br/>9、万名塔<br/><br/>1、沱江跳岩<br/><br/><br/>　　位于凤凰古城北门外沱江河道中，跳岩全长100米，15个岩墩依次横列在沱江河床上，墩与墩之间相距5米，上面用木板搭铺，木板再用铁链捆牢，固定在河两边的铁桩上，以防被洪水冲走。沱江跳岩始建于清康熙四十三年（1704年），旧时是进出凤凰古城的主要通道之一。乾嘉苗民起义，辛亥革命凤凰光复起义、解放战争，这里都是进攻古城凤凰的主要通道。为方便百姓进出，乃在原址上游20米处修一石墩木桥，也称“跳岩”。如今，跳岩坚固牢实，保存完好，成为凤凰古城颇具民族特色的亮丽风景。<br/><br/><br/><img alt="加载中..." title="图片加载中..." src="http://www.goodzhen.com/statics/images/s_nopic.gif"/><br/><br/>2、沈从文故居<br/><br/><br/>世人知道凤凰，了解凤凰，是从沈从文开始的。1902年12月28日，著名作家、历史学家、考古学家沈从文先生就出生在这里——一座具有明清建筑风格的小巧院落。小院中间有个天井，共有10间房，是沈从文的祖父沈洪富于清代同治初年建造的。沈从文在这度过了他的童年和少年时光。<br/><br/>交通：就在古城里面，走路就能到，位于古城中营街10号。<br/>开放时间：冬天8：00-17：30 夏天7：30-18：00<br/>门票：属于古城11景内，使用168元的联票就可观看<br/><br/>祭拜沈从文<br/>你被沈从文的《边城》感动过吗？如果有，请你在放下行礼之后先轻装到沈先生的墓地，哪怕只是去献上一束小花也好。沈先生的墓地就在听涛山下。拾阶而上。石青径曲，泉水淙淙，苔绿藤绕，清幽宁静。这，就是沈老魂归故里的憩息地。\n \n上山的路旁立着一碑，“一个战士不是战死沙场，便是回到故乡”，先生回到了故乡。现在先生的书在故乡也多了起来，游客们因为湘西凤凰古城而记起了先生，也会有更多的人来看望先生的。墓地没有凸起的坟土，没有华丽的装饰，一块从墓穴后凿起来的不规则的天然五色巨石肃然矗立，成为沈从文墓独特的标志。这块巨石高1.9米，重6吨多，前面临摹沈从文手迹，刻有沈老富有哲理的语句：“照我思索，能理解‘我’，照我思索，可认识人。”背面刻有美国耶鲁大学教授张充和先生撰书和沈从文侄女婿——中央美术学院著名雕塑家刘焕素教授镌刻的挽联；“不折不从，星斗其文；亦慈亦让，赤子其人。”这四句话的最后一个字连起来是“从文让人”，这是沈老一生的真实写照。听其言、观其行、读其文，就能找到沈从文之所以受到人们敬仰，得到众多文人骚客祭拜的答案。</p><p>3、熊希龄故居<br/><br/><br/>熊希龄故居位于古城北文星街内的一个小巷里，故居往东200米便是秀丽的沱江。故居为四合院系南方古式的木瓦结构，比较矮小但很精致，故居现存房屋4间基本是保持原貌，很富于苗族情调。环境幽静安谧，建筑古老质朴，四方形布局，中有一天井宽坪。熊希龄人生的脚步，演绎着他人生的轨迹。家事、国事、天下事都在他文晦蹈略之中。他胸藏珠玑，学富五车，在中华民族堪乱危难之时，为国家和民族倾注了一生的心血。毛泽东同志曾评价熊希龄：“一个人为人民做好事，人民是不会忘记他的，熊希龄是做过许多好事的。”周恩来总理曾中肯地评价：“熊希龄是袁世凯时代第一流人才，是内阁总理。熊希龄的事，我看后就记得很清楚。”<br/><br/>交通：在古城内，徒步可到<br/>时间：冬天8：00-17：30 夏天7：30-18：00<br/>门票：属于古城11景，使用168元的联票可观看<br/></p>', 'admin', 4, 0, '42', 0),
(42, '云南建水', '2017-09-18', '<p></p><p>第一怪 洪武铜钟响天外。今朝阳楼上悬挂的洪武年间铸造的铜钟，传为当年一个像济公一样的机智人物疯摇城以草绳悬挂，曾嘱在他离去七日后方可撞钟，但撞钟人一天后便撞钟，故今洪武铜钟可声传一天路程之内。</p><p>　　第二怪 仙人脚印天上来。建水古城城东十里处泸江河上有清代古桥名为“天缘桥”。传为天上仙人下凡所造。至今桥面青石板上留有硕大的仙人脚印，凡人到此均以脚与仙人脚印比合。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505740165606274.jpg" title="1505740165606274.jpg" alt="20140311091919916.jpg"/></p><p>　　第三怪 东门比天字门早盖。建水古城建于明代洪武二十二年，即1389年，而北京紫禁城建于1417年，如此算来，建水古城现存之东门（迎晖门）比紫禁城的天字门早建二十八年。</p><p>　　第四怪 新房没有旧房帅。建水古城被称为“古建筑博物馆”。寺庙连云，书院相望；亭台楼阁多姿多彩，庭院殿堂金碧辉煌。如文庙、朱家花园、朝阳楼、指林寺、团山民居等，气势之雄伟，风采之亮丽，令新房子们“自叹不</p><p>如”。</p><p>　　第五怪 三步走过五条街。建水古城街巷纵横交错，城东永善街处三步便迈过五条街，令人稀奇，自古为建水一景。</p><p>　　第六怪 朱家井水漫井外。城内朱家花园庭院中有井，井水水位高出地面，伸手可掬，可自然溢流到井外地面。朱家当年生意兴隆，称此水为“发财水”，经常用之洗手。</p><p>　　第七怪 半亩方塘也叫海。全国的文庙都有泮池，独建水文庙称泮池为“学海”，取“学海无涯”句。</p><p>　　第八怪 四十二个天井一家在。被称为“滇南大观园”的建水朱家花园，规模庞大，富丽堂皇，计有房屋214间，大小天井四十二个，房舍鳞栉比，院落层出迭进，犹如迷宫，但仅供朱氏一家在。</p><p>　　第九怪 人人都把孔子拜。建水古城是“诗书郡，礼乐邦”，尊孔拜孔，蔚然成风。每年春节，古城男女老少都要去文庙朝拜孔子，其中文庙盈门，先师殿前，乐舞一片。</p><p>第十怪 蜘蛛蚊子知好歹。传文庙先师殿内有“弼蚊珠”和“弼尘珠”，令蜘蛛蚊虫不敢在孔子牌位前惊扰先师。文庙内蜘蛛蚊子也知也歹，敬而远之，远而避之。故先师殿从无蛛网蚊虫，纯属圣地净地。</p><p><img src="/ueditor/php/upload/image/20170918/1505740171211329.jpg" title="1505740171211329.jpg" alt="20140311093507107.jpg"/></p><p>　　第十一怪 榕树柏树谈恋爱。文庙西明伦有榕柏共生古树一棵，柏树婷婷玉立于中，榕树（也称万年青），怀抱柏树于外，相抱相依，生死之爱，令观者感动，谓之曰“万将军抱白小姐”。有优美故事流传。</p><p>　　第十二怪 柱子出头不会坏。文庙棂星门有四棵柱子不藏于顶下而是穿脊而出，称通天柱。除柱顶有清花瓷罩外，约有一米裸露于风雨之中，然而历数百年风雨侵蚀而不朽，实为罕见。</p><p>　　第十三怪 宴席长长摆通街。哈尼族过阿玛熬节时，依街摆桌，各家自上酒菜，全村男女老少转桌而坐，状如长龙，名曰哈尼长街宴。</p><p>　　第十四怪 家家户户吃馊菜。古城家家户户在除夕之夜用上好的猪鸡重油煮青菜萝卜，沤在缸盆内，待有馊味后热来吃，别有一番诱人味道，为传统美食。</p><p>　　第十五怪 草芽当作象牙卖。草芽系水生草本植物生长在根部的芽苞，长盈尺余，色若白雪，状如象牙，趣称“象牙菜”。可炒可氽可凉拌，甜嫩可口，为建水独有之云南名之菜。旅游者喜食喜买。</p><p><img src="/ueditor/php/upload/image/20170918/1505740176647413.jpg" title="1505740176647413.jpg" alt="20140311093508816.jpg"/></p><p>　　第十六怪 燕子窝窝皇帝爱。建水燕子洞特产一种与人参齐名的名贵滋补品—燕窝，是采集白腰雨燕的窠巢取其唾液制成，当年清朝皇帝和慈禧太后仰慕其滋阴补阴，延年益寿的功效，对建水燕子洞的燕窝尤其喜爱。</p><p>　　第十七怪 豆腐用玉米数着卖。建水烧豆腐为云南名吃。到烧豆腐摊上去吃豆腐，摊主用一粒粒的玉米为你记数，最后以玉米多少结帐，十分有趣，令人起起古老的“结绳记数”。</p><p><img src="/ueditor/php/upload/image/20170918/1505740180565050.jpg" title="1505740180565050.jpg" alt="建水双龙桥.jpg"/></p>', 'admin', 1, 2, '43', 0),
(43, '滦州古城', '2017-09-18', '<p></p><p>滦州古城位于河北省唐山市滦县滦州镇，枕燕山而踏渤海，屏京津而扼关外，横陈乌桓之南，幽蓟之东，鲁豫之北，素有“京畿锁钥，兵家咽喉”之称。滦州大地物阜民丰，先后有18位皇帝驾临于此。古城展现的是古老的明清建筑，细腻的北方古街博览区、丰盛的传统民俗旅游区以及原生态的滦河文化风景区。<br/><br/> 滦州人杰地灵，物产丰富，是一个经历了千年文化滋养的历史名城，同时也是契丹、鲜卑、女真、蒙古、满、汉多民族聚集地，是中原文化和北方各民族文化交流的前沿，是唐山的历史依托和文化母体。中国人设计的第一座铁路大桥、震惊中外的辛亥滦州起义、张学良东北易帜等重大历史事件均发生在滦县。<br/><br/> 古城整体建筑以盛清(康、乾)的建筑风格为主基调，共同展现北方古镇的地域特色；在装饰装修，景观节点等方面，不同区域根据不同的历史节点和民族特色，用不同的文化元素和建筑形式，表现不同民族的不同个性。以古为体，以今为用，以史为据，功能齐全，环境优美。用本土文化包装城门广场、崇贤广场、文姬楼、韶音广场、和园、戏院广场、西门广场七个节点，展现民族的交流、碰撞、融合与繁荣。<br/><br/>古城以环形水系青龙河作为民族文化轴，展现“河”文化的多元性。沿河建筑采用统一的建筑风格和形态。巡滦大街将青龙河环形河道分为南北两个部分，分别在南河道、北河道展示中国南方、北方不同的文化特点。				<!--内容关联投票-->				        						 </p><div class="text-c" id="pages"></div><p style="margin-bottom:10px"><strong>相关热词搜索：</strong>河北 	滦州 	古城</p><p></p>', 'dxy', 1, 3, '44', 0),
(44, '四川 萝卜寨村', '2017-09-18', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">萝卜寨100余公顷的黄土地养育着岷江中人口最多（全寨有1000多人口）、住房最密集、并且是唯一以黄土为建筑材料的古老羌民，是迄今为止发现的世界上最大、最古老的黄泥羌寨，被誉为“云朵上的街市、古羌王的遗都”。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">萝卜寨距汶川县城巧公里，距成都的）公里。从汶川县前往萝卜寨，转过声名远扬的雁门关，便见一根矗立于317国道旁的羌碉和一座座古老的烽火台指明了进入萝卜寨的入口。沿着十公里黑色路面的盘山1道盘旋而上，映入眼帘的首先是索桥村，之后便是小寨子村，最后进入了萝卜寨。萝卜寨历史悠久，地势独特。考古学家说：萝卜寨早在：3000―400年前就有人类生存；地学：家说，在岷江大峡谷中阡的这片黄土地感觉是天外来物；民间传说更是丰富多彩、委婉动人。萝卜萝卜寨最早称为凤凰寨，后为富顺寨，再后为老虎寨，最后是沿用至今的萝卜寨，四次更名是萝卜寨历史久远的见证。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">萝卜寨村位于雁门乡境内岷江南岸海拔Zm）多米的高半山台地上，为冰水堆积的阶坡台地地势平缓、宽阔，是岷江大峡谷高半山最大的平地，也是鸟瞰岷江大峡谷风光最理想的场所萝卜寨村地处凤山和凰山之间，整个建筑形态看上去像一支展翅欲飞的凤凰，村民全部是羌族，是纯粹的羌族聚居村寨，整个寨子宛如一座有无数街巷的微缩的小城，是黄泥羌寨的大群体，是古羌人最古老的街市，也是古羌人御敌的坚固壁垒。中心寨区的建筑几乎户户相连，层层叠叠，错落有致，屋面几乎连成一片，上一家 屋顶即可通到数十家甚至百家。民房均为高土房，二至三层；寨门上有太阳形状的图形和白石，白石崇拜为羌族人有名的信仰习俗，萝卜寨的每家屋顶上都能看到，除代表天神外，还代表别的神灵。羌族人常常向白石虔诚祈祝寿，燃香祭拜…</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">大禹王庙建在一块巨石上，游人可可在禹王庙内祈福（即羌民族的挂红仪式和许愿（许愿的载体是白石）。游人祈福、许愿所得到的红绸可以带走给亲朋好友，亦可J挂在萝卜寨的神树上许愿的白石也可带走，亦可挂放在祭坛旁专设的祭塔上，日积月累可形成气势恢宏的白石组成的白塔羌族文化历史悠久，民族风情多姿多彩，但只有语言没有文字，因此，羌族的历史，除了人们口耳相传外，只有依靠本民族人文风情、建筑、服装、舞蹈及民间艺术等到方面来表现，其中羌绣则是表现羌文化的一个重要组成部分。羌族妇女在长期劳动实践中形成独特的审美观念，用刺绣的形式把羌族的劳动生活表现在饰品及服饰上，表达她们对美好生活的渴望，对大自然的热爱和对爱情的追求。羌绣画面生动形象，色彩艳丽、题材广泛，大自然的花、鸟、鱼、虫、动物等内容都在飞针走线中栩栩如生，呼之欲出。羌族的传统服饰为男女皆穿麻布长衫、羊皮坎肩，包头帕，束腰带，裹绑腿。女装衫长及踝，领镶梅花形银饰，襟边、袖口、领边等处都绣子花边，腰束绣花围裙与飘带腰带上也绣着花纹图案。男人喜着青色或白色头帕，女人包绣花头帕，脚穿有鼻的“云云鞋”，鞋子绣有云彩图案及波纹，鞋尖微翘村民以农业生产为主，喜饮青稞制成的咂酒和玉米制的蒸蒸酒，普遍吸兰花烟。羌族自己腌制腊肉，在各家各户有火塘的正梁上，大多都挂着很多陈年腊肉，煮好的蜡色浓味香，肥而不腻。另外，羌民用粗粮做出的美食也很多，酸菜搅团、香酥荞面角、玉米锅边馍馍、面油果子、金裹银饭、猴头蘑菇、豆芽花、鹿耳韭等等，总之天然食品，绿色食品，应有尽有，令人垂涎三尺。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">2008年5月12日，汶川发生大地震，萝卜寨遭到严重损坏。如今，恢复重建后的中国羌族第一寨仍那么美丽动人，地震灾区所有的道路都已恢复正常通车，萝卜寨村最大的收入来源除了旅游就是销售大樱桃，另外，千年羌寨还喝上了自来水，结束了世世代代从高山顶上背水喝的历史...…萝卜寨悠久的历史，古老绚丽的羌文化，奇特而坚固的羌族建筑，以及当地淳朴的羌民风情，会让你对羌族，甚至于对人生有个全新的了解和感悟。</span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"><br/></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">旅游TIPS：</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">提示：萝卜寨村2008年毁于大地震，目前仍在恢复重建之中。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">四川省阿坝州汶川县雁门乡萝卜寨村。从汶川县城约40分钟车程可到萝卜寨。从成都约4小时车程可到达到萝卜寨。 50元。&nbsp; 雁门乡雁门关电话：0837―6226326</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">羌族是我国历史最悠久的民族之―，今羌族多聚在茂县、理县、汶川、北川县等。羌族人居住在具特色的羌寨中。羌寨中的碉房是羌族的石砌</span>民居<span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">，―般高达三四层底层圈养牲畜，中层住人：上层储藏粮食，屋顶为平台，既可以用来摊晒粮食，又可以作为老人歇息、妇女针织、孩子游戏的场所。碉房建造时就地取材，以土石为原料，不用绘图、吊线，全凭高超的技艺和经验。碉房形式多样，层次不一，结构严密，棱角整齐，不仅坚固耐久、实用，而且冬暖夏凉，充分体现了羌族人独特精湛的建筑艺术。</span></span></p>', 'add', 1, 0, '45', 0),
(117, '陕西 陈炉古镇', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505829099115140.jpg" title="1505829099115140.jpg" alt="43188554_17.jpg"/><img src="/ueditor/php/upload/image/20170919/1505829099260686.jpg" title="1505829099260686.jpg" alt="43188554_16.jpg"/></p><p>----炉山不夜 文化传承</p><p><br/></p><p>陈炉古镇位于陕西省铜川市东南15公里处，属铜川市印台区所辖。全镇总面积99.7平方公里，地形为土石低山梁塬丘陵地貌。</p><p><br/></p><p>陈炉古镇是宋元以后耀州窑唯一尚在制瓷的旧址，其烧造陶瓷的炉火1000多年来灼灼不息，形成“炉山不夜”的独特美景，是古同官八景之一。</p><p><br/></p><p>特色美食：菜面馍馍、地三鲜凉拌苜蓿菜、五柏芽、槐花麦饭、辣子蒜羊血、面食、饺子，这里的什么都是就地取材，普通的菜都作得非常的鲜香</p><p><br/></p><p>自驾路线：走包茂高速王益出口下，走宜园路</p><p><br/></p><p>乘车路线： 从西安火车站旁的汽车站或城北客运站乘发往铜川长途车，在铜川转乘去陈炉古镇的中巴车。</p><p><br/></p>', 'add', 1, 4, '123', 0),
(45, '云南腾冲', '2017-09-18', '<p></p><p>黑鱼河位于腾冲县火山公园的黑鱼河峡谷，与火山胜景柱状节理毗邻。黑鱼河是在熔岩流的作用下，堵塞地下水脉使其露出地表而成。黑鱼河巨泉一年四季清澈见底，而且流量不减，其中两股水流各有特色，一股哗哗欢跳着</p><p><img src="/ueditor/php/upload/image/20170918/1505740274114378.jpg" title="1505740274114378.jpg" alt="20140309041004425.jpg"/></p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505740278577744.jpg" title="1505740278577744.jpg" alt="20140309041007456.jpg"/></p><p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505740284498480.jpg" title="1505740284498480.jpg" alt="20140309041739453.jpg"/></p>', 'admin', 1, 5, '46', 0),
(46, '八百庄堡', '2017-09-18', '<p></p><p>历经四百多年风雨的北方城村，依然保持了明代规划的“丰”字形布局，建筑遗产文物古迹和传统文化比较集中，是我国城池格局保存比较完整的典型的北方四合院式建筑群和传统村落之·具有较高的历史文化、艺术和科学价值。北方城村始建于明万历四年（15怕）位于蔚县城北8公里处的丘陵地带。该村有明、清时期和民国年间建筑所形成的民宅、戏楼、庙宇、碾坊、堡门等古建筑群体，数量较多、种类齐全、形制独特、格局完整，至今保护完好，是研究华匕乃至中国历史与民俗文化的重要基地。<br/><br/> 蔚县有着“北京西大门＂之称，同时又是草原游牧文化与中原文化碰撞．交流最多的地区之一在明代中期，蔚县地区广建城：堡，历史上有“八百庄堡”一说。北方城堡即建于这个时期，城堡南北、东西各2＂米，平面呈方形，建筑设计整齐方正，故称方城。该堡构造布局是中国典型的一街六巷对称结构，主街由南向北依次有财神庙．马神庙、三觉堂，街尽头为主供真武大帝的真武庙，堡前建有古戏楼北方城堡的“前身”是村东北的一座小堡，现尚存遗址。后来，经过一段时期的休养生息，人丁繁衍壮大，小堡位互应生存，遂选址另建新城。即今天我们看到的北方城。新城的择地，首要考虑的是水，其次，便是交通。北方城前的道路便是明清时期县城通往西北五岔关口的交通要道，为“燕云古道＂之一；同时，这条路又是人们前往蔚县境内最大的重台寺（当地人又叫丛台寺，为儒释道三教合一的寺庙）的必经之路。昔日，这条古道上骡帮驼队穿梭往来，数不清的西域人，蒙古人，汉人留下了他们的足迹。古时，出入北方城只有南门，在冷兵器时代，只要将它关闭，便活总是安稳而惬意，堡门楼高耸而坚固，为砖石拱券墩基，券上镶扇面形石匾一块，上镌三个典雅大方的大字：“北方城”。<br/><br/> 堡门外还有村子的唯一座戏台，系，f增卷棚布瓦顶面宽三间，进深二间．六架梁，设前后台，以金柱及市隔扇界开。梁架为“松柏装＂采绘。后台东墙开门，1题“清净室”三个楷子。每年正月，这里热闹异常，村民们喜欢听专业居团的山西梆子但是没钱了也会请民间艺人人台龙王庙位于戏台东侧，有正澱三间，是面宽三间进深二间硬｜山布瓦顶建筑。正殿内山墙上有彩绘壁画，色泽艳丽造型古朴。戏台对面是观音庙和Fdi澱，它们建于慢门东西两侧外面还被一堵半米多高的扇开砖墙围起来。墙上开才门，看戏时，女人站在这里面。只有男人可以站在戏台前。除了尊卑观念，“男女授受不亲”的传统道德伦理在此也可见斑。堡内从堡门向北约0咪处，东西各建一座庙宇。东为财神庙，西为马王庙均为面宽三间进深间硬山布瓦顶建筑。从财神庙与马王庙之间向北约5咪处，正中建有佛殿一座。面宽三间．进深―间，硬山布瓦顶。佛殿内正北墙上砌方开神龛，两侧墙上有采绘壁画。佛殿与正北堡垣上的真武庙遥遥相对。高峻壮观的真武庙建在一个高台上，是一座“瞭塔”，同时，它还是“统治整个城堡内的建筑”：哪里起烽火，或者村子有什么情况，可以一览无遗。该庙面阔涧，进深二间，五架梁，前出廊，硬山布瓦顶。真武庙下边建有前院，有小山门，进入小山门，上砖砌台阶数＋级，才能登上北台，进入真武殿，殿内东，西北三面墙上，绘有骏金的壁画：这些壁画以连环画的形式出现，它记录着真武大帝从出生到成神的完整过程。殿前为悬山鼓楼存完好。<br/><br/> 现在，城门外的古道上虽无往日繁华、熙攘，但是，因路旁的这座古堡，它依然引人注目。近年来，颇受外地游客的喜爱，游览者络绎不绝，一些影视界人士也来此选景拍摄。<br/><br/><strong>古村美食：</strong>糊糊面又名“多维豆面糊”，是蔚县人民自己研制的豆制产品采用纯天然的绿色食品扁豆、碗豆、蚕豆等12种豆类炒熟后磨制而成，保持原有豆类的多种营养成分，其面质细腻、口味纯正，具有丰富的营养价值。</p><p><br/></p><p><br/></p>', 'dxy', 1, 0, '48', 0),
(47, '江南杭州好风景', '2017-09-18', '<p></p><p>装备攻略：<br/><br/>一、 食物：城市旅游，无需太多吃的，带点小糖果、巧克力、话梅让小朋友们在火车上打发时间、交换零食增进友谊就可以了。配方奶粉全部压缩到简易包装罐里，只装了一个带刻度的水瓶，冲牛奶和白天带水两不误！<br/><br/>二、 衣物：因为带儿子出门，厚外套必备一件，自己的可以省点儿地方！袜子一定多备几双，你们懂的，特别是儿子的那一双小汗脚，呵呵。<br/><br/>三、 其他：去杭州前专门买了一台佳能小摄像机，功能齐全且小巧便携。本次旅行纪录片还初步尝试了佳能DV独有的涂鸦、电影怀旧滤镜风格和超强防抖，文中的视频截图可以看出效果，很赞！另带了一个小卡片供儿子随手乱拍来玩儿。<br/><br/>四、住宿：对于只有三天时间且拖着小朋友行李较多的游客来说，住在西湖边无疑是最好的选择，交通便利不说，邻近美丽的环湖路和西湖的各个景点。这次我们住在位于南山路柳浪闻莺附近的景上南山国际青年旅社，大床房和标间都是238一天，对于长假期间的杭州房价而言，性价比相当高。地段更是闹中取静，旅社的小院子和餐厅的布置都显出江南的温婉，孩子们即便在旅社里也能玩得很开心。<br/><br/>五、吃：精致、细腻、软糯、绵绸，总之一切和婉约有关的形容词都可以用来形容江南美食的妙处。不咸不淡、不辛不辣、略带酸甜的口味也很符合小朋友的喜好。西湖边的外婆家和北京的一样排着长龙一样的队，老字号知味观的卤菜也是地道的杭州风味，老少咸宜。红泥砂锅大酒店虽然菜价不菲，但好在包间够大，适合小孩子们吃完饭满地玩耍。<br/><br/>六、行：由于时间紧，又带着小朋友，这次的杭州之行我们大多数时候选择了打车，例如去较远的西溪湿地和去丝绸市场采购。杭州的公共交通很发达，唯一的遗憾是太堵车，也许和清明长假游人太多有关。实际上，在杭州的旅行，最好的交通工具是自行车，租车点多，几乎任意一个大的景点外都有租车点，借和还都很方便，交押金办理一张租车卡，就能免费用车。带后座的自行车尤其受欢迎！<br/><br/>行程攻略：<br/><br/>一、搭乘Z9火车，开往初春的杭州<br/><br/>晚上6点半，送走工作日最后的忙碌，背上早已收拾好的行李，我们翻山越岭跋山涉水各种换乘地铁后，终于抵达了气派的北京南站。上了Z9次直达列车，小豆儿和他的朋友们很快自动调整到旅行模式，在软卧车厢里串来串去，激动的讨论杭州可能会有什么好吃的和好玩的。三岁左右的小朋友聊着我们理解不了也参与不了的家长里短，在火车的轰鸣中开往初春的杭州。<br/><br/>二、下雨天，杭州这座城市有点冷<br/><br/>天有点冷，阴雨绵绵，于是我们以休整为主，让小朋友们适应适应气候和环境。<br/><br/>早上8点半，我们抵达了传说中的杭州。阴雨天的杭州，尽管温度并不低，可湿冷的空气还是让我们一行大大小小直打冷战。打车前往早已预订好的景上南山国际青旅，在青旅后院的餐厅里迅猛解决早餐，烤吐司、热牛奶和香喷喷的白粥让我们心情大好。安顿了房间和行李，小朋友们把大床当做蹦床玩得不亦乐乎，大人们则凑在一起探讨接下来的行程安排。<br/><br/>由于下雨天儿冷，我们的行程果断缩短。大家决定就近逛逛西湖边小公园，看看烟雨濛濛的西湖，感受初春江南如丝的细雨，感受着周围潮湿的空气，总让人有点儿怀旧的小情绪，应景儿用DV的旧照片滤镜拍一些片段，有点儿旧时光的味道~~我也开始感受旅行中DV记录的感觉，除了记录之外，那种应景儿动态记录的感觉也让我跟着镜头来了一次穿越之旅，回到柔软的旧时光。<br/><br/>下午的雨滴儿渐渐变大，我们也由室外转为室内活动。小朋友们睡午觉、看动画片、听爸爸们讲故事，为第二天的活动积攒能量。<br/><br/>三、天气晴好，有西溪湿地<br/><br/>西溪湿地有一段长长的水路，这天的行程排得比较满。大家集体前往了西溪湿地和西湖的花港观鱼、断桥、苏堤白堤，对小朋友们来说强度有点大。<br/><br/>一早起床，天气晴好。开心不已的儿子迅速整理妥当，还顺带收拾好了自己的小背包。三岁多的小男孩，有这样强的自理能力，我这当妈的感觉好欣慰。</p>', 'admin', 4, 0, '49', 0),
(48, '四川 阆中古城', '2017-09-18', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">阆中</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">古城</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">位于四川盆地北缘、嘉陵江中游，已有2300多年的建城历史，向为古代巴蜀军事重镇。阆中土肥水美、气候适宜、物产丰富。阆中汉为巴郡，隋时改称阆内县，宋以后称阆中，历代多为州、郡、府治所。</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">古城</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">阆中的建筑风格体现了我国古代的居住风水观，棋盘式的古城格局，融南北风格于一体的建筑群，形成“半珠式”、“品”字型、“多”字型等风格迥异的建筑群体，是中国古代建城选址“天人合一”完备的典型范例。</span></p>', 'add', 1, 0, '50', 0),
(49, '四川 康定古城', '2017-09-18', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">康定是一座享誉世界的历史文化名城。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">康定，一座跑马山，名扬五州四海；康定，一曲情歌千古绝唱，醉了天下人。康定，甘孜藏族自治州经济、政治、文化的中心。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">康定藏语叫达者都，意为三山相峙，两水交汇的地方。打箭炉是其谐音。古为牦牛国疆域，唐宋属吐蕃；元置鱼通长河西宁远军民宣抚司；明置长河西鱼通宁远宣慰司；清雍正七年置打箭炉厅，光绪三十四年（公元1908年）改设康定府；1939年建西康省，设省会于康定，1950年3月康定解放，为甘孜藏族自治州的政府驻地。康定幅员面积1.14万平方公里，辖三个镇，十九个乡，总人口10.8万，是以藏族为主，汉、回、彝、羌等多民族的聚居县。康定，情歌的故乡，茶马互市的重镇，锅庄文化的发祥地，是贡嘎山国家级风景名胜区的一颗明珠。既是重要的旅游目的地，也是全州旅游的中转站。主要景区有木格措、跑马山、塔公草原、莲花湖等等。</span></p>', 'add', 1, 0, '51', 0),
(51, '西文兴村', '2017-09-18', '<p></p><p>西文兴村是一个以宗族血缘关系为纽带的柳氏家族世代聚居的古老村落。15世纪初年，柳宗元第16世孙柳琛在西文兴村修建起宗祠、文庙、关帝庙等一批祭祀建筑物和香泛柳下、磐石常安、恪守先业等3个四合院以及49孔砖窑。16世纪前期，柳琛之孙担任正四品承德郎期间，皇上恩赐“行邀天宠”金匾。1544年柳琛之孙病卒后，朝廷派钦差率山西地方官亲赴西文兴督造“丹桂传芳”石牌坊。柳遇春受皇上恩赐“青云接武”金匾后，1550奉旨建造“青云接武”石牌坊。16世纪中期，柳遇春再次大兴土木，建造了占地98亩的13个院落。这时的西文兴村规模宏大，气势不凡。有门庭森严的四合院、有肃穆华贵的牌坊街、有赏心悦目的赏景台、有临溪建造的观河亭、有祥云缭绕的关帝庙、有瑞霭笼罩的魁星阁、有神秘莫测的闺绣楼、有四季葱郁的小花园。<br/><br/> 村庄街巷纵横交错，各院地道暗中相连，十三个宅院浑然一体，高墙防护下的外府区仅有3个石拱门让人通行。全封闭的内府区只有东北和西南两个出入口，俨然一座山中城堡。四合院上空架设有铁丝网，与地道合称为“天罗地网”。有几个院大门后竟有横竖软硬12道门栓，防范措施可谓严密。然而终究还是抵挡不住强敌入侵。规整华丽的西文兴柳氏民居在1628年前后遭遇战争破坏。1811年以后，柳春芳重修祠堂、文庙、文昌阁、关帝庙、魁星阁、真武阁等祭祀建筑物和中宪第、武德第、司马第、河东世泽、行邀天宠等5个四合院，使西文兴村再度兴盛。</p><p><br/></p>', 'dxy', 1, 2, '53', 0),
(52, '海南东寨港', '2017-09-18', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">当我们来到红树林，也许你首先就会问，红树林是海上长得森林吗？其实红树林是热带滨海泥滩上特有的常绿植物群落，这种树皮呈红褐色、树叶墨绿、根茎纠结交错、生长于沿海泥滩和海水中的绿色灌木，你很难想象它是一种非常特殊的胎生方式繁衍生息的。</span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170918/1505740607576195.jpg" title="1505740607576195.jpg" alt="20121106090558495.jpg"/></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);"><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">红树林的种子成熟以后并不落下，而是在母树上发芽，向下伸展出幼根，待幼根长成茎生出叶，变成一颗真正的幼树时，它便自行从母树上脱落，扎入淤泥之中。如果下落时没有着地，种子还能随波逐流数月不死，一旦遇到泥潭便又能很快扎根生长，因此人们也把红树林称为漂泊流浪的植物。红树林也如同一道绿色的屏障，有效阻挡被雨水自陆地上冲刷下来的泥土。它的落叶掉入海中腐烂后，还可以转化为海洋生物喜欢食用的养分丰富的食料。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">如果你恰巧在大海涨潮后来到红树林，租上一条小船，迂回划行在港汊一丛丛翠色的树冠间，此时形态秀丽的绿树冠，纠缠在一起的粗大树根，眼睛瞪得溜圆圆的招潮蟹，竖起背鳍的弹涂鱼，安然栖息在树冠上的彩蝶，迎风歌唱夏日的花蜜鸟，眼入眼帘的一切都随着涌动的海潮在忽左忽右地悠然荡漾。</span></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);"><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170918/1505740613520254.jpg" title="1505740613520254.jpg" alt="20121106101849515.jpg"/></span></span></p>', 'admin', 1, 1, '54', 0),
(78, '在合肥体验古老的徽菜', '2017-09-18', '<p></p><p>当小妹伸出热情的双手邀我去安徽合肥时，我很是傻不愣噔地问她：合肥有什么好玩的吗？小妹说，好玩的？哎呀，一时还真说不上，好吃的有，鸭脚包知道吗？就是把鸭脚包起来？不是，是鸭脚包鸭心，然后用鸭肠缠起来。啊？想想流口水，那要尝一尝。小妹又说，茶干吃过吗？是用茶叶做的干？小妹说简直实在跟外国人说话。我后来坐在西塘的河边，一边喝着铁观音，一边谦逊地问朋友，茶干是用什么做的？朋友说，就是茶叶泡的豆腐干呗。当我后来把这个答案告诉小妹的时候，小妹才知道，这个世界上白痴其实不止我一个。<br/><br/>安徽自古出帝王将相，明有朱皇帝，今有胡主席，晚清还有李中堂等；安徽盛产文人才子，早有胡适之，后有海子，而我熟悉的文人墨客如简大宁、郑大平、张大锐等都来自安徽；安徽有座山，江爷爷一句动词加形容词的“日破云涛万里红”，就狠狠地将毛爷爷数量词加名词的“天生一个仙人洞”藐视了；乾隆年间，四大徽班进京促进了西皮与二黄的交融，完成了徽调与汉调、昆曲的合流，理直气壮地成了当今“国剧”的老祖宗；很多人和我一样，还能背出周恩来在皖南事变后写的那首“江南一叶，千古奇冤”。所以对安徽的熟悉程度，我大抵也就只能说这么多，陌生是显然的，除了上面所述的一组人名和和典故以外。<br/><br/>为了多了解一点合肥，我在离京之前老老实实地上网温习了一下功课，合肥有个逍遥津，据说是三国时“张辽威震逍遥津”的古战场，我对打仗不感兴趣，又接着看，合肥城里有个包公祠，包公都不在了，那祠堂还有什么用，不过当时我嘴上条件反射地哼哼了几句《铡美案》的唱段；又发现合肥边上有个巢湖，这不错，我喜欢有水的地方，在我看来，有水的地方就有灵气，于是问小妹，巢湖怎么样？小妹说，基本上一潭臭水，当即很失望。我在百度上输入南京至合肥．景点，结果很失落，至少百度还不到360度全方位多角度。<br/><br/>就是这样的，带着对安徽朋友的十分熟悉，对安徽地域的完全陌生，我坐上了从上海开往合肥的动车。从上车开始，我便进入了吃的状态。临走前，小妹夫买了肯德基的鸡块6个，蛋挞6枚，原本就是打算这些东西连早餐带午饭一起解决的，火车驶出上海后，问列车员要了一杯速溶咖啡，开始了第一轮吃喝，当3个小时候后火车抵达合肥站的时候，食品已经扫荡一空，很好，手上不用拎东西了。<br/><br/>出合肥站，天气真好，上海多日的阴霾细雨被合肥剌剌的阳光所替代，云开雾散见太阳，心情似乎晴朗许多，后来才知道，胃口因此也变得比心胸还辽阔。见到小妹大概是下午3点半的光景，因为塞车在火车站里堵着，小妹拿出核桃干说：且让他们堵吧，我们慢慢吃。没吃多少，最多两小袋。很快便到酒店住下，小妹说，来安徽别的先不说，徽菜是一定要吃的，我一边在心里想着江浙菜的甜腻腻，一边问徽菜有什么特点，小妹说，应该是八大菜系中的一种吧。</p><p>百闻不如一吃，五点过后，小妹将我带到一个叫环翠堂的私人公馆处，说，这一家的徽菜做得不错，环境也好，嗯，内里九曲回肠，还可以和开心捉迷藏，菜也不错，第一口鹅汤就把上颚和喉咙管烫得半天说不了话，最叫我记忆深刻的是，是吃到了张志和诗中的鳜鱼，不是桂鱼，是鳜鱼啊，搞得我们很兴奋地回想并背诵起“西塞山前白鹭飞，桃花流水鳜鱼肥，青箬笠，绿蓑衣，斜风细雨不须归”，呵呵，吃鱼能吃到“吟哦口垂涎，嚼味有余隽”，这还是头一次。<br/><br/>把小开心送回家，和小妹继续在街上游荡，兜了一圈，来到一家路边小店，小妹说：接着喝啤酒吧，就龙虾。俺花颜失色，刚吃完，又来，哪里装得下呀？不是龙虾太好吃，就是胃口太博大，反正到最后，我们俩满手辣椒红油蒜香味地剥了上百只，外带四瓶啤酒。<br/><br/>第二天一大早，在酒店外的小吃店扒拉了一碗牛肉米粉，几个锅贴饺子，然后上了路，小妹指着护城河说，以前，护城河以内的就是巴掌大的合肥，我记住了，护城河其实就是城中心的一条四周有绿树的河。<br/>尽管巢湖是一潭臭水，毕竟还是五大淡水湖之一，就这样，小妹将我领到了一个叫宗庙的巢湖边上。站在湖边柳堤下，可以看到近处的姥山，远处的鞋山，她们跟我讲了典故，我没怎么记住。波光粼粼的巢湖水天一色，看不到边，无怪乎开心对着巢湖说：大海啊，真大！上午的巢湖很安静，宗庙里有香火，不知道香客多不多，听说宗庙的签很灵验，我没去，这把年纪了，听天由命吧，管它好与坏；巢湖的水绝对不算臭，我想多是小妹先用批评与自我批评的精神降低我的高标准严要求，湖边的打渔人家家家户户晾晒着小虾米，空气中有一股腥膻的味道，别说还真跟海边的空气里的咸味差不多。<br/><br/>趁时间还早，到肥东吃了一锅重庆公鸡煲，麻辣鲜香味道齐全，本欲在此做个广告，可惜临走时忘记把门牌号抄下来，算了。<br/>肥东到南京实在太近，俺开车技术实在太好，超了几辆奥迪，被一辆凯迪拉克超，超与被超之间就到了长江三桥，秦淮河原是不打算去的，但惦记着那碗鸭血粉丝汤，还是去了，并且连带着扫荡了一笼灌汤包子。<br/>从到合肥，再到南京，其间24小时，感官系统最敏锐的是胃，最大规模的运动是吃，对于安徽，对于合肥，我还是熟悉而陌生，宛若当初。<br/></p>', 'admin', 3, 8, '83', 1),
(54, '海南古盐场', '2017-09-18', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">洋浦最古老的晒盐工艺已被列入国家级非物质文化遗产名录。盐工们根据海南岛高温烈日的特点，改变过去“煮海为盐”的方法，用经过太阳晒干的海滩泥沙浇上海水过滤，制成含高盐分的卤水，再将卤水倒在石槽内，经暴晒制作成盐巴。由于这里的盐味鲜，并对多种疾病有疗效，清朝乾隆皇帝闻报后，御书“正德”赐给盐田人。海南千年古盐田被誉为最早采用日晒的制盐场，至今保留完好的原始民间制盐工序的古盐场。目前有1000多个形态各异的砚式石盐槽密布海滩。盐田村仍有30多户盐工，每天都在这片盐田上沿袭着1200多年来的古老方式劳作。</span></p>', 'admin', 1, 0, '56', 0),
(55, '大海东来第一山的全羊宴', '2017-09-18', '<p><strong></strong></p><p><strong>大份羊肉</strong><br/>沂山风景区位于山东潍坊市临朐县沂山镇，沂蒙山区北部，距离临朐县城约50公里，沂山风景区是国家级森林公园，有机会去了趟“大海东来第一山——沂山”，虽然天气有点冷，但大家玩的挺开心，尤其是吃了顿美味大餐。爬了半天的山，下山时已经接近中午，山下是“大观”镇，之前问过一个朋友，听说有一家的羊肉不错，所以下山直奔杰达全羊饭店。</p><p><img src="/ueditor/php/upload/image/20170918/1505740728166317.jpg" title="1505740728166317.jpg" alt="20140303053250105.jpg"/></p><p>羊汤，味道很正宗，上来一小会就被大家灭了，赶紧叫老板又上了半份。</p><p><img src="/ueditor/php/upload/image/20170918/1505740753824618.jpg" title="1505740753824618.jpg" alt="20140303053808596.jpg"/></p><p>炒羊杂，爽口。</p><p><img src="/ueditor/php/upload/image/20170918/1505740802106094.jpg" title="1505740802106094.jpg" alt="20140303053808333.jpg"/></p><p>当地的火烧，一点也不硬，挺好吃的，喝羊汤吃火烧，简直就是绝配。</p><p><img src="/ueditor/php/upload/image/20170918/1505740820121194.jpg" title="1505740820121194.jpg" alt="20140303053809332.jpg"/></p><p>红烧羊头，香喷喷的。</p>', 'admin', 3, 21, '57', 2),
(56, '王家大院', '2017-09-18', '<p></p><p><br/></p><p>王家大院位于灵石县城以东12公里的静升镇，此镇能够在建设部公布的第一批中国历史文化名镇中名列榜首，王家大院功不可没。目前修复后对外开放的王家大院主要包括高家崖、红门堡两大建筑群，共计54座院落，总面积三万余平方米，两组建筑群东西对峙，一桥相连，皆为黄土高坡上的城堡式建筑，依山就势鳞次栉比，气势宏伟，再加上随处可见的砖雕、木雕石雕，匠心独具，精致典雅。无论规模等级，以及其中蕴含的文化和建筑装饰艺术，王家大院都可称得上是北方民居建筑之集大成者，湿示出贵精而不贵丽，贵新奇大雅，不贵纤巧烂熳”的民居特色。<br/><br/> 静升王家原为太原王氏宗裔云元朝时静升王氏始祖王实迁至此地定居，以务农为主兼营豆腐坊C后世逐渐发家，清康熙十二年（公元673年）；王家捐献24匹良马支援清政府平定吴三桂叛乱受到康熙帝褒扬。借清政府的势力，王家生意规模扩大，资本日趋雄厚，通过科举和买官等方式开始从政做官，最高做到正二品。王氏家族由农及商；由商到官，终于在清代中叶发展成为富甲一方、声名显赫的灵石四大家族之一·王家发迹后，便大兴土木，不断营造住宅、祠堂、牌坊、坟茔，从康熙年间一直修到嘉庆年间；持续了半个多世纪，累计修建了“三巷四堡五祠堂”等庞大建筑群总面积d五万平方米目前对外开放的部分，仅是王家鼎盛时期建筑面积的四分之一。<br/><br/> 大院入口就是高家崖的东门，高家崖共有两个主院，·是王家于＝七世子孙王汝聪3王汝成兄弟俩的宅子：均为前堂后室A三进式四合院，建于嘉庆元年（公元1796年）至嘉庆十六年（公元1811年），是静升王家修建的第五座也是最后一处堡院，当时王家实力最强，因此处处显现出精湛的工艺和雄厚的财力。砖雕、石雕木雕极尽雕刻技艺之能事，其含义之深远、构思之巧妙、手法之精湛、造型之奇异、品位之高雅，无不令人叹为观止。在高家崖里，有共用的书院、花院、长工院、围院和祭祖阁，每院还有各自的厨房、绣楼，大小院落既珠联璧合，又独立成章，院套院、门套门，如同走入了迷宫，营造出错落有致、虚实相间的视觉效果。<br/><br/> 东边的院子是哥哥王汝聪的敦厚宅，西边的是弟弟王汝成的凝瑞居，由于弟弟是二品官而哥哥是五品官，从两个院的正门就能看出职位高低，门和匾的大小不同，二品的连门槛、上马石、旗杆石都比五品的更加高大气派，亲兄弟间也一定要分出等级高下。敦厚宅前的砖雕照壁是第一个景点，大多数人只注意到正面的狮子滚绣球图案，其实背面的图案也颇有意思，以公鸡戏牡丹象征“功名富贵”，鸳鸯闹莲象征“恩爱多子”，鹌鹑菊花象征居乐业”，喜鹊登梅象征“喜上眉梢”，同时这四幅图案也分别代表了春夏秋冬四季敦厚宅迎面的石雕影y照线刻’远看不太清楚’细看是—幅意境感远的山水画。前院原为主人接待客人的地方，北房内有王家大院模型及简介，可以作―个全景式的了解。北房的额枋、雀替木雕很精致，以三个层次的浮雕刻画出不同形象的吉祥图案，第―层以平面阳刻花纹作底，第二层高浮雕分别雕以荷叶、芭蕉、佛手、卷轴、盆景等，第三层再在高浮雕上雕以鼎、四艺、如意、葡萄、兰草等，层层相叠，立体感非常强。门框装饰则采用了透雕与额枋相呼应的方式。门槛下被铁链围着的是由鹭鸟、芦苇、莲花图案组成的过门石，谐音“一鹭（路）莲（连）科”，鹭在古代属于吉祥鸟，是六品文官的服饰标记，寓意事业顺利，赴考场则连登科第。<br/><!--内容关联投票-->				        						 </p><div class="text-c" id="pages"></div><p></p>', 'dxy', 1, 2, '58', 0),
(57, '逛凯里夜市，品黔贵小吃', '2017-09-18', '<p></p><p>夜色降临，牛场坝夜市舞台的大幕徐徐拉开了。也许你已经品尝过了凯里最著名的酸汤鱼，但是夜市上的各色贵州风味小吃还是会让你有大饱口福的机会。<br/><br/>一条不过一公里长的小街，此时路两旁已经被密密麻麻的小吃摊占据了，灯火通明，香气四溢。最常见的是煎豆腐和土豆饼，方方正正的豆腐块在铁算子上煎成金黄色，蘸汁自然是少不了的“折耳根”（即鱼腥草）调料。另一边摊主正在熟练地煎土豆饼，把软烂的土豆泥摊成小饼，撒上香葱辣椒，两只小铲来回翻转，很快就煎得金黄焦脆，还没吃就让人食指大动了。</p><p><img src="/ueditor/php/upload/image/20170918/1505740929555411.jpg" title="1505740929555411.jpg" alt="20131112033013875.jpg"/></p><p><strong>炒面</strong><br/><br/>怪噜饭是贵州独有的一种特色炒饭，原料自然也少不了那味“折耳根”，腊肉和酸菜唱主角，加上其他一些配料，端上来时红、黄、白、绿，五颜六色，味道更是麻、辣、酸咸俱全，陪一碟泡酸萝卜，一碗青菜汤，街头小铺的一碗普通炒饭堪比豪华大餐，令人回味无穷。<br/><br/>米粉更是家常便饭，摊子上粉条的种类甚为齐全，粗的细的，白色的黄色的，圆滑的方正的，各式各样随你选择，大锅里煮着卤蛋和豆腐，也是随你添加。其中最有特色的算是鹅肉粉，据说这也是来自苗家的煲汤秘方，一碗看似普通的清汤滋味却是异常鲜美，而且营养丰富，颇有滋补功能。<br/><br/>那边支开德小矮桌上小盘小碟摆得满满当当红、黄、黑、绿煞是好看，细看有胡萝卜丝、豆芽丝、黄瓜丝、海带丝、榨菜丝、白萝卜丝、辣椒等十多样，原来这就是贵州有名的小吃“丝娃娃”，因其用巴掌大的薄饼卷起各种菜丝，形状犹如薄包的婴儿，故此得名。丝娃娃的小吃摊位总是夜市中摆的最漂亮最养眼的一个，小矮凳上一坐，一点一点包起自己的“娃娃”，再浇上特制的调料汁，入口外软里脆，酸辣香甜，更难得的是这份逍遥自在。<br/><br/>吃得差不多了，再来碗甜酒汤圆吧，现滚的汤圆雪白齐整地摆放在笸箩里，新鲜的甜酒酿开锅了，汤圆软糯，馅料甜香，酒酿爽口，这道收尾的饭后甜点也是回味无穷。<br/><br/><strong>交通：</strong><br/>位于凯里大十字新华书店附近，国泰大酒店隔壁。承公交车可至。<br/><br/><strong>旅游TIPS：</strong><br/><br/>一、北京路也有夜市，乘坐公交车可以到达，能吃到各色小吃，也有餐馆小炒。<br/>二、夜市一般晚上八点左右最红火，食物种类很多，每样少吃一点才能争取品全。牛场坝夜市早上的时候也有几家小吃摊子可以吃早点，有糯米饭、卷粉、汤圆等。<br/>三、人多的地方必有美食可寻，白天的牛场坝夜市口烤豆腐摊，电影院门前的老字号卷粉摊，中博好吃街的罐罐香酸汤米粉摊，总是被大批人群包围着，不难找到。<br/></p>', 'admin', 3, 6, '59', 0);
INSERT INTO `tp_message` (`id`, `title`, `time`, `content`, `author`, `cid`, `hit`, `top_img_id`, `collect`) VALUES
(58, '江西清华', '2017-09-18', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">江城如画里</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">山晓望晴空</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">两水夹明镜</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">双桥落彩虹</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">这是李白笔下的双桥。一千多年后的今天，我们再也看不到李白笔下的“双桥“了，留给我们的只有一座木制廊桥，孤独地横亘在湍流之上，注视着她身旁穿梭的游客。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">彩虹桥坐落在婺源县曾经的府治所在地清华镇上，始建于宋代，名字便取自“两水夹明镜，双桥落彩虹”一诗。</span></p><p><span style="font-size: 14px; line-height: 21px;"><img src="/ueditor/php/upload/image/20170918/1505740967398043.jpg" title="1505740967398043.jpg" alt="20141105090556514.jpg"/></span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">虽然名为彩虹，其实这座廊桥并不是拱形结构，而是标准的廊亭桥，全长140米，宽6.5米，四墩五孔，由六亭五廊组成，之所以取名“彩虹”，是因为传说廊桥建成那天，雨后天空恰现彩虹，彩虹倒映水中，和廊桥相互辉映，煞是壮观。取其吉兆，所以称为彩虹桥。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; line-height: 21px; background-color: rgb(255, 255, 255);">婺源一带雨水多，彩虹桥建成八百多年来，只有过一次修葺，虽然期间发生过多次洪水，彩虹桥却依然完好无损。这里面其实有着很多学问。一看这廊桥的形态：远看廊桥，似乎廊与亭是一般高低、连为一体的，而近看则能发现，其实廊和亭中间是隔开的，错落有致。这样做一方面是为了桥的美观，最重要的还是考虑到廊桥的维护，万一哪座坏了，也不会牵一发而动全身；二看廊桥的位置：廊桥前面的湖面人称“小西湖”，水面不足60米宽，廊桥却没有修建在那上面，这完全是为了安全的需要，因为河道宽处水流不急，泄洪能力强，这样才能更好地保护廊桥；三看桥下的桥墩：桥墩并非四四方方，而是成“前锐后丰”的燕尾状，每个桥墩间的距离也不相同，砌成桥墩的青石是一块块垒起来的，中间结合得相当紧密，这样的设计可以减缓水流，遇到洪水时也可以避开上游的漂浮物，使廊桥免遭破坏，可以说正是这几个桥墩保佑了廊桥的千年平安。</span></p>', 'admin', 1, 0, '60', 0),
(59, '品尝贵州凯里红酸汤', '2017-09-18', '<p><strong></strong></p><p><strong>红火酸汤鱼<br/><br/>凯里亮欢寨红火酸汤鱼</strong><br/><br/>详细地址:近郊凯里市环城西路98号<br/><br/><br/>说道贵州的美食，几乎人人都知道酸汤鱼，其实无论酸汤鱼、酸汤牛肉、酸汤排骨，甚至一碗酸汤面，其中的精华都是那份浓浓的红酸汤。而这最正宗的红酸汤正是来自黔东南的凯里。如今凯里的红酸汤已被中国食品协会认定为贵州特色，并成为我国三大特色火锅底料。<br/><br/>如果你来到凯里，怎么能错过这闻名遐迩的红酸汤。贵州菜已入选新八大菜系，其味道的奇绝之处就在于它的酸，开胃的酸对于食欲不振的城市人来说听着就是一种诱惑。凯里红酸汤的配方来自于苗家秘方，加入了特产的糟辣椒和各种有营养的中草药，再借助番茄的口味颜色熬制出红艳艳、浓烈酸香的红酸汤。据说凯里本地居民的长寿人口居全国之首，不得不说酸汤的营养价值也在这里得到了绝佳验证。<br/><br/>如果你在凯里只有一次品尝美食大餐的机会，那首选不是酸汤鱼，吃酸汤鱼自然还是亮欢寨（即快活林饭店）。这家饭店开业有二十多年了，将从苗家民间引进的酸汤摆上了\n 大雅之堂，并形成了具有民族特色的大型服务酒店，甚至名扬全国，旅行者来到凯里，可以不登香炉山，却不能不来亮欢寨。</p><p><img src="/ueditor/php/upload/image/20170918/1505740967725257.jpg" title="1505740967725257.jpg" alt="20131111033739539.jpg"/></p><p><strong>凯里老地方啤酒鸭</strong><br/><br/>美食推荐：<strong><strong>凯里老地方</strong></strong><br/><br/>详细地址:五华区小西门钱局街112号<br/>订座电话:0871-5318698<br/>菜式风味:云南菜<br/>适合类型:家庭聚会,大型聚餐<br/><br/>浓郁民族特色的建筑庭院，穿戴苗家服饰的服务员门前迎客，一进门就闻到了浓浓的酸汤气味。店内装修得精致整齐，席间还不断有苗家姑娘唱歌敬酒。用餐氛围一派其乐融融。当然，不能不赞的还有那锅喷香浓烈的酸汤鱼。这份酸可不是醋调制出来的，而是用西红柿、香菜、生姜等做料放入米汤中存于瓦缸中发酵而成的，活鱼剖开后整条放入汤中煮熟，再加入生姜、花椒、香菜、葱、香椿叶一起煮沸，吃的时候鱼肉可蘸上用辣椒粉和酸汤、香菜调制而成的蘸料，入口酸香带着辣味，让人食指大动。<br/>而这里选用的鱼据说都是没有污染、非饲料喂养的清水江野生活鱼，其中最有特色的就是角角鱼。这种鱼鱼肉细嫩，鱼皮富有弹性，口味酸醇清香，吃了鱼，再喝上两碗汤，出一点细细的汗，全身毛孔都被打开，倍感畅快淋漓。除了酸汤鱼，还有一些贵州的特色菜，如蕨巴腊肉、泡菜炒汤圆、腊肉香肠血豆腐等等，都让你这顿美餐回味无穷。<br/><br/>也许在之后黔东南的旅程中，你还有很多机会品味这红酸汤的魅力，那么也不妨和凯里的亮欢寨比较一番，看看到底哪一家红酸汤更为正宗，更对你的胃口。<br/><br/><br/><strong>交通：</strong><br/>亮欢寨的位置稍微偏僻一些，位于环城西路98号，可以从市中心打出租车前往。<br/><br/><strong>住宿：</strong><br/>凯里的住宿选择很多，建议在大十字附近，那里有很多家宾馆，高中低档俱全，附近还有夜市小吃。<br/><br/><strong>第一站主题客栈</strong><br/>2010年10月开业的客栈，经营模式类似于青年旅馆，有电脑可免费上网，空调、液晶电视一应俱全。客栈命名为第一站，老板就是希望作为黔东南旅行的起始站，能让游客的黔东南之旅有一个美好的开端。地址：营盘东路26号（州武警支队公交车站旁）。<br/><br/><strong>鸿祺宾馆</strong><br/>位于大十字地带，交通很方便，有不少公交车可达，旁边不远就是牛场坝小吃夜市，宾馆门脸不大，但里面房间的设施很好，比较舒服。地址：韶山南路3号。<br/><br/><strong>文化宾馆</strong><br/>位于凯里市北京东路和文化南路路口，从长途汽车站走路约十分钟可达。房间略显老旧，但床褥和卫生间都很干净，房型种类很多，性价比高，适合结伴出游的驴友。<br/><br/><strong>永丰酒店</strong><br/>位于文化北路与北京东路交叉路口西，从火车站坐公交车道永丰大楼下车即是，从汽车站步行约5分钟可达，标间内有电脑可以上网。地址：文化北路2号。<br/><br/><strong>营盘坡民族宾馆</strong><br/>二星级宾馆，距离汽车总站约500米，环境幽静。营盘东路街道两边较多特色食品，如酸辣米粉、酸汤鱼火锅等。宾馆门口天天有人卖刺绣和民族手工艺品。地址：营盘东路53号。<br/></p>', 'admin', 3, 4, '62', 0),
(61, '山西 夏门古堡', '2017-09-18', '<p>夏门村是汾河旁边的一个古村落,汾河水自东向西绕村而去，汾河转弯处是龙头岗，夏门古堡就建在高高的龙头岗上，古堡风景秀丽，堡内建筑雄伟壮丽，它依山就势，顺坡而上，密密匝匝的布满了整个龙头岗。既有北方民居高大雄浑之气势又有南国园林之秀雅风韵，是一处不可多得的理想宅第。<br/><br/> 夏门古堡至今尚有保存完好的传统街巷9条。其中东西走向的街3条：东街、中街、西街；道1条：后堡道；巷5条：大夫巷、御史巷、堡九巷、梁家巷、天九巷。各条街、道、巷连通各个院落，并与暗道相连，方便交通和藏匿，兼具排水之功能，成为夏门古村落传统街巷空间的一大特色。<br/> 古堡主要建筑群始建于明万年间，居住于此的梁氏家族是当时灵石县的四大望族之一，官宦学士众多，工商财力雄厚，因而古村落选址、布局及营建极为讲究。目前古村落中半数以上的建筑群为明清时期的山西传统建筑风貌，古建筑面积约7.5万平方米；现有保存完好的院落6组共60余幢，窑洞、传统民居千余间。窑上建窑、院中修院、窑窑相通、院院相连，是夏门古村落建筑环境的一大特色。</p>', 'dxy', 1, 2, '64', 1),
(62, '陕西 漫川古镇', '2017-09-18', '<p><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">漫川关鸳鸯戏楼</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">漫川关鸳鸯戏楼，俗称双戏楼，是漫川</span><a href="http://www.goodzhen.com/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">古镇</a><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">的标志性建筑。由南式戏楼和北式戏楼两座同向戏楼组成，是中国唯一的一座联璧式双戏楼。南楼为重檐，显得灵秀，北楼为单高挑檐，显得大气。该楼在建筑艺术、绘画艺术、砖雕、石雕、木雕方面都颇具独到之处。据说双戏楼曾经常唱对角戏，往往北秦腔，南汉剧，同台演出。漫川关在古老的三秦大地上，曾上演过多少出历史的悲喜剧。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><a href="http://www.goodzhen.com/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">古镇</a><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">漫川关位于商洛山阳县东南边睡70公里，依山傍水，群山环绕，东有崔嵬的龙山，北有巍峨的鹘岭，西南有磅礴的郧岭，金钱河、靳家河穿境而过。这里气候温湿，盛产柑桔、稻米、芝麻、桐油。陇海铁路通车前，漫川为水旱码头，成为陕鄂豫蜀物资交流重要集散地之一。漫川街道长而狭窄，均用石条石块石子铺成。两旁民宅，多以木板为墙，房屋小巧玲珑，上有一层阁楼，绘画山墙，翘龙凤角，明镜悬顶，古色古香。街道日日有集，商铺颇多，一直延续着“男人下田，女人摆摊”的习俗。素有“南通吴楚、北连秦晋”的“水旱码头”之称。历史上的漫川关曾有“水码头百艇联樯，旱码头千蹄接踵”的辉煌。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">贴士TIPS:</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">旅游攻略：</strong><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">从山阳至漫关的路，是一条非常漫长、美丽、精彩的路，公路一直沿着溪流在群山中穿行，两边的群山高耸，公路犹如在峡谷底。路上山景水景处于不断变化中，间或出现一些石板屋顶、铁索桥，给你意想不到的惊喜。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">住宿：</strong><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">镇上菜市场那里的古镇宾馆是漫川最好的宾馆，标准间100元/天（带空调），电话0914—8565625。古镇里的小可以旅馆，卫生条件不错，普通间60元/天（带空调）。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">交通指南：</strong><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">从商洛市乘车到山阳十元，再从山阳乘车到漫川关十六元。</span></p>', 'add', 1, 0, '65', 0),
(63, '如清秀素丽之女的江南美食', '2017-09-18', '<p></p><p>公认的鲁、川、粤、闽、苏、浙、湘、徽八大菜系江南地区占了两个。苏、浙菜就好比清秀素丽的江南美女，其中江苏菜是由苏州、扬州、南京、镇江四地菜肴为代表构成，特点是浓中带淡、鲜香酥烂、原汁原味、浓而不腻、口味平和、咸中带甜；浙江菜则以杭州、宁波、绍兴、温州等地的菜肴为代表发展而成的，其特点是清香脆嫩爽鲜。上海菜则是上世纪初汇聚了苏、锡、宁、徽等十六个地方风味柔和而成，又称为本帮菜。</p><p><img src="/ueditor/php/upload/image/20170918/1505741149100341.jpg" title="1505741149100341.jpg" alt="20121012053505620.jpg"/></p><p>通常一些老字号的餐馆都集中在旧城内一些历史浓厚的地区，如上海的绿波廊、杭州的楼外楼、苏州的得月楼等等。一些餐馆云集的地方形成了一条条美食街，如上海的云南路，杭州的高银巷、南京的狮子桥等等。</p><p><img src="/ueditor/php/upload/image/20170918/1505741179349418.jpg" title="1505741179349418.jpg" alt="20121012053528681.jpg"/></p><p>比较著名的老店还有苏州的采芝斋、嘉兴的五芳斋、绍兴的咸亨酒店等，都是以经营当地小吃著名的，价格也不贵。</p>', 'admin', 3, 4, '66', 0),
(64, '泰州的风味小吃', '2017-09-18', '<p></p><p>泰州的风味小吃、美食在淮扬菜系中久负盛名，且种类繁多。特色小吃主要有金松皮蛋、黄桥烧饼、中庄醉蟹、烫干丝、溱潼鱼饼等、油炸臭干、五味干丝、鱼汤面、姜堰酥饼、农家荞面汤饼、柴墟蝉翼饼、宣堡小馄饨、大炉饼等。</p><p>其中，当最为有名的当属溱湖八鲜宴，溱湖八鲜以溱湖簖蟹、虾球、鱼饼和甲鱼为代表品种；蟹黄汤包是靖江的传统名点，距今已有200多年的历史，与天津狗不理包子、上海南翔小笼包、扬州三丁包并列为\n “中国四大名点”。 每年4月初～5月，泰州都会举办大型的溱湖八鲜美食节、靖江江鲜、汤包美食节，是品尝的最佳时候。<br/>溱潼会船一年一度，日期在清明节的第二天。据传，宋代岳飞部下张荣、贾虎与金兵大战秦潼村，溃敌于缩头湖。自己也陈尸湖畔不少。每逢清明节第二天，溱潼人便撑篙划桨，汇聚溱湖，追悼忠魂，竞舟礼祭，一直沿袭至今。届时十里溱湖，千舟竞发，万民欢腾，中外友人&#39;浪花中携手，笑声中相逢；心比春风暖，情比美酒浓&#39;.</p>', 'admin', 3, 1, '67', 0),
(65, '陕西 华阳古镇', '2017-09-18', '<p><br/></p><p><img src="/ueditor/php/upload/image/20170918/1505741242594656.jpg" title="1505741242594656.jpg" alt="20140825124707103.jpg"/><img src="/ueditor/php/upload/image/20170918/1505741242631190.jpg" title="1505741242631190.jpg" alt="20140825124714146.jpg"/><img src="/ueditor/php/upload/image/20170918/1505741242996099.jpg" title="1505741242996099.jpg" alt="20140825124712707.jpg"/><img src="/ueditor/php/upload/image/20170918/1505741242114399.jpg" title="1505741242114399.jpg" alt="20140825124711331.jpg"/><img src="/ueditor/php/upload/image/20170918/1505741242206552.jpg" title="1505741242206552.jpg" alt="20140825124708470.jpg"/><img src="/ueditor/php/upload/image/20170918/1505741243110858.jpg" title="1505741243110858.jpg" alt="20140825124707327.jpg"/>华阳古镇位于陕西洋县，地处秦岭山脉深处，是历史上有名的古道驿站（傥骆古道），古军事要冲，古经济政治重镇。古镇始于秦晋，至今已两千多年的历史。古镇内明清建筑保存较为完好，古华阳县县城墙残垣轮廓尚在</p>', 'add', 1, 1, '69', 0),
(66, '西塘的小吃1', '2017-09-18', '<p><br/></p><p>在西街的入口处有个卖小馄饨的摊子，在西塘，就连一个摆生意的小贩也可能会声名远扬。小馄饨汤料独特，馅少皮薄，最具特色倒是他那套做生意的担当，是有年头的了。烟雨长廊的来凤桥和永宁桥之间，有家专做豆腐花的小店，同时也卖绿豆粉条，酸香入味，独具特色。阿明荷叶粉蒸肉、龙蹄是西塘 古镇特产传统名菜，历史悠久，具有香得纯正、酥而不烂、肥而不腻三大特点。\n西塘的特产也很好吃。嘉善黄酒、荷叶粉蒸肉、八珍糕、芡实糕、麦芽塌饼、薰青豆、蜜汁大头菜 这些都是一些很好吃的特产，去那里旅游的朋友一定要带些回来给情朋好友尝尝。</p><p>芡实糕芡实糕买了“平安堂”（明清木雕馆对面)的的和“森林”的，吃口大家多觉得“平安堂”的好吃，口感“甜而不腻、软、有嚼劲”。口味独特，跟人家不一样的,小馄饨在西街的入口处有个卖小馄饨的摊子，即受众多网友推荐的“陆氏小馄饨”。在西塘，就连一个摆生意的小贩也可能会声名远扬。小馄饨汤料独特，馅少皮薄，最具特色倒是他那套做生意的担当，是有年头的了。如果去的时候正好赶上吃饭时间，还要排队呢！一定要尝尝　一线天（在送子桥附近）旁一家馄饨店，野菜大馄饨远比小馄饨好吃。<br/>特色美食(11张)　豆腐花 粉蒸肉阿明荷叶粉蒸肉、龙蹄是西塘 古镇特产传统名菜，历史悠久，具有香得纯正、酥而不烂、肥而不腻三大特点。臭豆腐送子桥附近的一家“三味臭豆腐”别有风味，外脆内嫩，其他的如牛肉羹、酒酿圆子都很不错，一律5元/份。老太臭豆腐，口味非常嫩滑。甜酒酿在烟雨长廊中，过了环秀桥约30米，有家挂着祖传甜酒酿的小店，小杯2元，大杯3元，有冰冻的酒酿，香甜润滑，酒味恰到好处，令人念念不忘。粽子阿六一口粽，肉粽不腻，甜粽香甜。</p>', 'admin', 3, 0, '70', 0),
(67, '陕西 青木川古镇', '2017-09-18', '<p><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">雾锁青木川</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">青木川是陕西最西边的一个镇，</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"></span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">物华天宝，资源丰富,地处陕、甘、川三省交界处，素有“一脚踏三省”之誉，是陕西省最西的一</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">个</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">古镇</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">，西去227公里即是九寨沟。女作家叶广岑一部小说《青木川》，勾起了多少人想要到此一游的念想。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">青木川的西沟是嘉陵江的支流，水质清澈，有九寨沟水的感觉。“碧水绕村寨，青山映田园”，木制古朴的乡村显得分外迷离。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">据称青木川发轫于明中叶，成型于清中后期，鼎盛于民国，是羌汉杂居地区。经岁月沧桑，现有大量保存完好、风格迥异的古街、古祠、古栈道、古建筑等历史遗迹。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">青木川是一块美丽、富饶、神奇而正在开发的风水宝地，有悠久的文化沉淀，也有很多令人陶醉古老遗风：一条“回龙场”古街从南向北把小镇拉得悠长，两边的明清民居错落有致地连缀在一起：四合院式的、船形的，中西合璧式的……雕刻精美的窗棂、门楣、瓦当风格典雅，古朴的青石板街道尽</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">显古镇</span><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">的静谧安详。古街中段，一座盖顶华丽的“飞凤桥”横跨金溪河两岸。整条古街呈现出“平盘端凳，雕窗扇门，院落集中，四水倒淌”的格局。现在还留存的几处中西结合的老式豪宅，里面有回廊、天井，宽大舒展，土改时分给当地居</span>民居<span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">住，现在仍然住着多户人家。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">古镇远离县城，地处僻远。虽为要塞，却一直处于闭锁状态。有几条逶迤石径连通邻省邻县，供山民来往交通。逢三趁墟，山民结伴，来来往往，背扛肩担，荷货互市，互通有无。当年杜甫过境时曾吟道：“仰凌栈道细，俯映江木疏。地僻无网罟，水清反多鱼。好鸟不妄飞，野人半巢局。喜见淳朴俗，坦然心神舒。”虽白驹过隙、日月如梭，世界的面貌日新月异，青木川中先秦遗风尚存，青山风貌依旧：重峦叠嶂，山高路险，清溪碧湾，飞流溅珠，自然生态秀美，民风古朴。</span></p>', 'add', 1, 2, '71', 0),
(68, '晋南唐代卧牛城：新绛', '2017-09-18', '<p></p><p>新绛在仅有2。5平方公里的古城里几乎囊括了唐宋元明清等多个朝代的文物古迹，几乎没走几步就能看到一处名胜古迹。<br/> 新绛古称绛州，位于山西省西南部运城市，北靠吕梁山，南依峨眉岭，属于运城盆地范围。新绛古城是一座唐代古州城，因形似卧牛而被称为“卧牛城”，古城历经一千四百多年的风雨洗刷，至今依然完好保存了唐代的形制。<br/><br/> 新绛的每一寸土地都写下了悠远的历史变迁，远眺绛州城，从西至东，绛州三楼、绛州大堂、哥特式“天主教堂”、唐代“宝塔”从西至东，雄居高垣之上，成为古绛州的重要标志。创建于隋开皇十六年（596年）的“绛守居园池”，是目前全国唯一的时间最早的官家园林；创自于唐代的“绛州大堂”，曾是大唐名将张士贵的《帅府堂》；龙兴寺内的“唐代宝塔”，塔顶曾多次持续数日腾烟，为千年未解之谜。</p>', 'dxy', 1, 2, '72', 0),
(69, '室韦俄罗斯民族乡', '2017-09-18', '<p><img src="/ueditor/php/upload/image/20170918/1505741410274585.jpg" title="1505741410274585.jpg" alt="20110417105640732.jpg"/></p><p>室韦俄罗斯民族乡位于内蒙古自治区位于呼伦贝尔额尔古纳市境内，是以俄罗斯族和华俄后裔为主体，汉、蒙、达、满、鄂温克、土家族等七个民族构成。 \n室韦虽然是俄罗斯族民族乡，但也是蒙古民族的发祥地，人们常说的“蒙兀室韦”、“天骄故里”指的就是这里，800年前成吉思汗就是从这里开始征服世界的。全乡所辖12个自然村屯、两个国有农牧场，全乡总面积4351平方公里，边境线长136.4公里，南北最大跨度185公里，东西宽约115公里，室韦乡所在地吉拉林，距拉布大林161公里。室韦是最具现代气息的边陲小镇，华俄后裔家庭建筑都是经过人工修饰而成的，目的是开展家庭旅游，既可以让游客感受到少数民族的<a href="http://www.goodzhen.com/html/minsu/" target="_blank" class="keylink">民俗</a>风情，又可以体验俄罗斯族独特深厚的民族文化底蕴。<br/><br/><br/><strong>额尔古纳河<br/><br/>室韦景点</strong>：额尔古纳河<br/>额尔古纳河全长970公里，比起世界上另外几条大的母亲河来，她还是个“小号”的母亲。她的总流域面积15万平方公里，相当于4个台湾那么大。沿途汇集了海拉尔河、根河等1800多条大小河流，有这么多河流，那草原能不好吗？其水系还包括呼伦湖、哈拉哈河、乌尔逊河、克鲁伦河（今属蒙古国，其下游流入呼伦湖）等。<br/><br/>额尔古纳河畔的室韦历史悠久，旅游资源十分丰富，旅游景点众多，景色优美，极具观赏性。室韦口岸因位于额尔古纳市室韦俄罗斯族民族乡而得名，历史上是中俄民间往来、军事会晤、互市贸易的重要渡口。十九世纪末，室韦至恩和一带采金业兴起，到二十世纪初，形成了以采金为主，服务行业为辅的横跨额尔古纳河两岸的经济区域。<br/>　额尔古纳河是黑龙江的支流，上游是发源于蒙古的克鲁伦河，从1689年《中俄尼布楚条约》签定至今，一直是中国与俄罗斯的界河。主要支流有海拉尔河。以海拉尔河为源，全长１６２０公里。</p><p>额尔古纳河在《旧唐书》中称之为望建河，在《蒙古秘史》中称之为额尔古涅河，在《元史》中称之为也里古纳河，在《明史》中称之为阿鲁那么连，自清代开始称之为额尔古纳河。额尔古纳河上、下游流域地形差异很大，阿巴盖堆至黑山头为草原丘陵区，地势平坦，河谷开阔，多湖泊沼泽，水流分散，杂草、柳条丛生。自右岸根河，得尔布干河、哈乌尔河流入后水量大增。自粗鲁海图至吉拉林河段河谷变得狭窄，河中沙洲和岛屿较多，河水变深。自吉拉林以下，河水进入峡谷，河谷更窄，两岸山地陡峭，河床稳定，水流平稳，河水宽约200——300米，水深在2.5米以上，可以通航，是良好的航道，且水能资源丰富。<br/><br/>　额尔古纳河沿岸地区土地肥沃，森林茂密，水草丰美，鱼类品种很多，动植物资源丰富，宜农、宜牧，是人类理想的天堂。这里良好的自然环境，养育过许多古代少数民族，古代蒙古部落曾经在此游牧、渔猎，并深为这片美丽的家园而自豪。后来，成吉思汗就把他的弟弟合拙—哈萨尔分封于此，至今，在黑山头还有其王府遗迹。<br/><br/>室韦的住宿除了饭店外，还有牧场招待所和俄式木楞房，房内的装饰透着中俄交融的风格。</p>', 'add', 1, 2, '74', 0),
(70, '柳村-五当召', '2017-09-18', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">五当召村位于内蒙古包头市石拐区五当召镇。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">五当召有蒙古、汉、藏三种名称，五当，蒙古语意为”柳村＂，因召前峡谷柳树繁茂而得名；藏名“巴达格尔”，意为“白莲花”；汉名”广觉寺“，系清乾隆二十一年（1756）由乾隆皇帝亲赐。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">五当召村位于包头西北阴山深处的五当沟，距包头70公里，“吉忽伦图”蒙古语危为·兴旺＂，是石拐区五当召镇政府所在地，五当召是祖先留给我皙的珍贵遗产，五当召镇青忽伦依嘎查是特定历史时期人类活，动的产物，蕴含着当时社会政治，经济文化和习俗等诸多方面的信息。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">五当召始建于清康熙年间，以西藏扎什伦布寺为蓝本，经乾能嘉庆光者年间多次维修、扩建，遂成今日规模。它与西藏市达拉宫、青海塔尔寺齐名，为我国喇有球的三大名寺之一五当召被群山环抱，山上苍松翠柳，郁郁葱葱，寺前小溪清澈，流水涓涓，整个寺院依山势建造，规模宏大，殿堂仓舍2538间，占地约20万平方米，鼎盛时喇嘛ZW多人。主要建筑由六殿三府―堂和94材喇嘛住宿楼组成，各幢建筑自成一区。所有建筑兰采用藏式结构：平顶、直墙、小窗、白色，整个寺院山巅松柏辉映．显得十分精美壮观。是内蒙卫地区现存规模最大、保存最完整的纯藏式喇嘛寺庙。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">远看五当召殿堂洁白如雪，楼顶金光夺目，十分壮观。庙内塑像俱全，壁画绚丽，唐卡（卷轴佛画）夺目，各殿各有特色，或立高达10余米的释迦牟尼铜像，或供高达咪许的黄教始祖宗喀巴铜像。据统计五当召当召内有金、银铜、木、泥各种质料铸成的佛像5）余尊，其中最大的有三层楼高，最小的不过盈寸。寺内现存的大量壁画，精细逼真地描绘了历史人物、风俗、神话及山水花鸟，是研究少数民族历史文化的宝贵资料。</span></p><p></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">现在五当召寺依然是本召喇嘛进行佛事活动和信徒们朝拜的场所。每逢农历初一到十五，诵“甘珠尔经“；十五这天，进行酥油花展出；农历五月初五，举行那达幕大会和赛马会；农历七月二十三至八月初一，举行一年一度的嘛呢会，喇嘛们手持经轮，吹着法号，敲着羊皮鼓绕召庙而行，颇为壮观。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">美食tips：</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">五当召寺院附近有许多餐馆，供应独具特色的蒙餐，如手扒肉奶酪、奶茶、炒米、蒙古包子、奶酒等，也有价廉物美的面食和炒菜，不管您是何种口味，都能让您心服口服。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">游览tips：</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">从包头市东河区汽车站乘7路公共汽车到石拐区（票价7元，约1小时车程），到石拐区再坐公共汽车去五当召（票价3元），打的约需35元。</span></p>', 'admin', 1, 0, '75', 0),
(71, '内蒙古 美岱召村', '2017-09-18', '<div style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">美岱召因十七世纪初麦达里胡图克图活佛在此坐床传教而得名，一直延传至今，成为村名。美岱召是喇嘛教传入蒙古时的一个重要弘法中心，它对于研究明代蒙古史、佛教史建筑史、美术史都具有重要价值。美岱召村始建于明朝嘉靖年间，距今已有400多年的历史，距包头市东约50公里，距呼和浩特市｜00公里。<a href="http://www.goodzhen.com/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border: 1px dotted rgb(170, 170, 170); text-align: center;">古村</a>依山傍水，景色宜人，是阿勒坦汗及夫人三娘子（金多哈屯）居住和议政的地方，也是喇嘛教活动场所。在建筑上更有独特的风格：它是仿中原汉式，融合蒙藏风格而建，是一座城寺结合，人佛共居”的喇嘛庙。</div><p><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">美岱召原名灵觉寺，后改为寿灵寺，位于呼和浩特至包头公路的北侧，明隆庆年间（1弱7·52），士默特蒙古部主阿勒坦汗受封顺义王，不钍默川上始建城寺，万历三年（1575）建成的第一座城寺，朝廷赐名福化城。西藏迈达里胡图克图于万历三十四年来此传教，所以又叫做迈达里庙、迈大力庙或美岱召。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">美岱召周围筑有围墙，土筑石块包砌，平面呈长方形，总面积约40平方米，四角筑有外伸约十一米的墩台，上有角楼。南墙正中开设城门，并建有城楼，城门上嵌有明代扩建寺庙时刻的石匾额，上题“泰和门”。城内有顺义王家族世代居住的楼院，还有供奉传为储藏三娘子骨灰的太后殿，骨灰储藏在搬内的檀香木塔中。寺里主体建筑有经堂、大雄宝殿、罗汉堂及观音殿等，殿宇高大雄伟，殿内供奉佛像、菩萨像，并陈列列法器，墙面绘有佛及描绘蒙古贵族拜佛的场面的壁传故事和护法神像等内容的壁画。如大雄宝殿内释迦牟尼历史壁画画都完好无损，其中画面上蒙古服饰的人物的人物像中，有传为阿勒坦汗及夫人三娘子的画像，为内蒙古召庙壁画中独有的一处。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">玻璃殿为3层楼房，是阿勒坦汗和三娘子接受朝拜的地方。在东北方，有座重歇山顶试建筑，这就是“太后庙”，亦称“三娘子庙”。三娘子为明朝时蒙古可汗阿勒， 坦汗的第三妻，由于她智勇兼备，才貌超群，深受阿勒坦汗的信任，并采纳了她与中原友好往来的主张。阿勒坦汗死后她又执行了继续与明朝友好球的政策，维系了土默：特部与明朝间和睦相处40多年的安定局面。在七其间她和极开展与中原地区的和平互惠通商与文化交流，促进了内蒙古地区经济文化的发展。明朝政府在j历十五年（1587）封她为忠顺夫人，封阿勒坦汗及其子孙为顺义王。在美岱召大门上保存的块匾额上面刻有“皇图巩固、帝道咸宁、万民乐业、四海澄清”16个字，表现了国家安定和平、人民安居乐业的意义。最有意思的是碑上刻了大明金国”的字样，把曾经是敌对的明朝刻在前面，表示既接受了明朝的封赐，又保留了自己的国号。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">美岱召这座庙与城互结合的建筑，不仅在建筑布局、造型以及建筑艺术等内容上，有着重要的价值，而且更重要的是它所反映的蒙汉和好、民族团结的历史对今天来说，更有其深远的意义。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">旅游TIPS：</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"> 每年农历五月十三，美岱召村都要举办盛大的传统庙会，同时也是当地最隆重的物资文化交流大会，是内蒙古西部远近闻名的文化节日，届时，人山人海，热闹非凡。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">攻略:</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">网内蒙古自治区包头市土默特右旗美岱召镇美岱召村。 从包头市长途汽车站可乘旅游专线车、中巴车或包车前往美岱召村。或从呼和浩特坐火车到萨拉齐站（约2小时车程，出站后西行到立交桥路口，乘过路的6路公交中巴（5块钱）可到美岱召村。</span></p><p><br/></p>', 'add', 1, 0, '76', 0),
(72, '新疆旅游推荐美食', '2017-09-18', '<p></p><p>这里推荐的新疆食品，都是新疆人日常吃的，真正的民间美食。<br/><br/>1、维吾尔抓饭:抓饭是乌兹别克和维吾尔人的美食。用大米、羊肉、洋葱、胡萝卜做成。它的主要特点是1、营养丰富，号称十全大补饭；2、速度快。坐下来30秒，一盘子抓饭就端上来了。在新疆，尽管来自各地的人饮食习惯不同，但以提起，多半都会流口水，尤其是过去营养不足的年代。小时候有个邻居，听说就是吃抓饭被撑死了。<br/><br/>2、哈萨克马肠子:马肉是一种稀罕的食品，中国主要产马地是内蒙和新疆，但蒙古族不吃马肉，所以在中国马肉店最多的就是新疆了。<br/>熏马肠是哈萨克人的独特做法，把整匹马的肉切成块，塞进马肠子中，然后用松木熏制。马肉营养丰富，富含不饱和脂肪酸。但马肉偏热性，不宜多吃。在乌鲁木齐和伊犁一带，熏马肠一般在“哈萨克奶茶馆”里面售卖，2011年的价格是100元一公斤。乌市最著名的哈萨克餐厅叫“苹果之父”。<br/>特别提示：按照伊斯兰教规，马肉被列为禁食肉类，但哈萨克、克尔克孜等民族由于生活在草原，普遍食用马肉。近年来，新疆维吾尔、回族等信仰伊斯兰教的少数民族也有人吃马肉。内地来疆的穆斯林朋友可以根据本地风俗自行决定。<br/><br/>3、回族凉皮子:“在每一个维吾尔烤肉摊前面，都会有一个回族人的凉皮子摊，摊位前的桌子上总是坐满了汉族的食客”<br/>新疆凉皮子传承陕西、甘肃一带的凉皮子风格，大同小异。一般女孩子喜欢吃凉皮子。<br/><br/>4、维吾尔烤肉:内地人叫羊肉串，新疆本地人叫烤肉，维吾尔语叫“卡瓦普”。世界各地都有烤肉，但我以为最好的烤肉是乌兹别克和维吾尔人的烤肉。肉块的大小，火候，调料都恰到好处。在新疆吃烤肉，一定要到山西巷子附近的维吾尔烤肉店，不仅便宜而且正宗。<br/><br/><br/>5、拌面:拌面很难说是哪个民族的食品。拌面的维吾尔语“浪漫”，其实就是汉语“拉面”的转音。新疆拌面是新疆人最主要的快餐方式，类似于兰州人的牛肉面、北京人的杂酱面。但拌面的体量和材料都比上述面食高很多，一大盘子上来，往往会让上海人觉得要四个人才能吃完，当然价格也高，2011年的拌面均价是12元了。拌面馆子一般由回族、维吾尔族经营，风格略有差异。回族拌面油多，亮堂堂的，以奇台拌面最为著名。<br/><br/>新疆拌面一般一个人足够了，如果吃不饱，可以免费加面。以前有个笑话，一群驴友去吃拌面：老板，来一份拌面，加8个面...<br/><br/>6、俄罗斯格瓦斯:这是一种饮料，俄罗斯人发明的，带到伊犁、塔城一带。格瓦斯用啤酒花、蜂蜜等酿制，微甜微酸，在夏季有解暑作用。现在的格瓦斯都工业化了，一个大桶，接上一杯，一般3元-4元。目前能找到的手工制作的格瓦斯是布尔津（喀纳斯的中转站）夜市的俄罗斯老太太列娜家做的，5块钱一瓶。去喀纳斯徒步的驴友如果在布尔津停留，一定要在夜市上喝一瓶，记住，不要老板拿给你的工厂有贴标的，一定要一号摊位俄罗斯老太太“列娜”他们家的。</p><p><img src="/ueditor/php/upload/image/20170918/1505741629866574.jpg" title="1505741629866574.jpg" alt="20120606090400338.jpg"/></p><p>7、馕:馕这个词是波斯语，从伊朗到中亚到新疆，馕是主要的食品。<br/>馕用馕坑烤制，维吾尔馕主要有两种，一种厚一些，吃的时候可以切片。一种薄一些，边上有圈。<br/>乌鲁木齐最著名的馕是阿布拉馕，3块钱一个，一般能管两个人的正餐。不过这已经是改进的馕，阿布拉囊门前排队的永远都是汉族人。维吾尔人自己还是坚持吃传统的馕，这种馕在山西巷子、二道桥一带随处可见，一般是推着小车售卖。山西巷子一带曾经那个有个著名的馕坑，连维吾尔人都要排队买，不过一般的驴友找不到，我也一次也排队去买，周围的人都好奇地看我。<br/><br/>阿布拉馕店在西北路有一家，就在小羊军团办公室的楼底下不远。<br/><br/>8、绿疆牌的冰激凌:这种产自新疆塔城的冰激凌源自俄罗斯人在塔城开设的冰激凌店，现在工业化生产了，依然味道纯正，奶香十足。和绿疆冰激凌相比，伊犁、雀巢等冰激凌就有明显的添加剂的味道了。<br/>几乎大小超市，只要有冰柜的，都能买到。<br/><br/>9、额尔齐斯河冷水鱼:中国只有一条河流向北冰洋，这条河叫额尔齐斯河。 喀纳斯湖的水就汇入额尔齐斯河。<br/><br/>额尔齐斯河盛产冷水鱼，最著名的是乔尔泰，也叫狗鱼。这种鱼是肉食鱼，有尖厉的牙齿，肉厚，适合烧烤。几乎每一个去喀纳斯的驴友都会在布尔津的夜市上吃一顿烤鱼。<br/><br/>当然，额尔齐斯河水中已经很难打到野生的河鱼了，市场上销售的主要是在附近的乌伦古湖养殖的冷水鱼。乌伦古湖是一个2000平方公里的大湖，在20多年前早已经和额尔齐斯河水系连成一体了。<br/><br/>10、大盘鸡:将一只整鸡切块，用土豆、辣子爆炒、炖，做成大盘鸡。大盘鸡的味道如何且在其次，能流行起来的主要原因是“大盘”、“整鸡”的概念，这种概念很容易被挑剔的消费者接受。<br/><br/>新疆大盘鸡有两个流派：沙湾风格和柴窝铺风格，具体差异吃了就知道了。<br/><br/>11、葡萄:吐鲁番的无核白葡萄，国内只有在新疆才有。这种葡萄多数用来晒葡萄干，少数鲜食葡萄在9月上市。还有马奶子葡萄，也很有特色。至于其他品种的葡萄，内地也有，不吃也罢。<br/><br/>12、哈密瓜:新疆人叫甜瓜，因为这种瓜在新疆各地都有，不仅在哈密有。而且最好的哈密瓜也不产在哈密，而是在鄯善。<br/>甜瓜容易上火，吃2-3牙就行了。<br/><br/>13、新疆酸奶:酸奶是三大健康食品之一，内地的酸奶，包括蒙牛、伊犁等品种的酸奶实在没法吃。<br/><br/>最好的新疆酸奶是回族人做的，过去用红色箱子扛着走街串巷卖，现在很少了，不过在城市街头，还能看待摆摊卖的酸奶，一碗一碗的，比工厂做的防腐时间长的好吃多了。这两年我吃到的最好酸奶在米泉羊毛工。不过我也见到二道桥的酸奶摊子，老板往上面喷凝固剂假冒奶皮子，要小心。<br/><br/>14、喝一碗牛奶:牛奶？哪里没有，非要到新疆喝?!<br/>常去内地出差的人都知道，内地的牛奶寡然无味，不如我们的佳丽和西域春牛奶。据说在欧洲，用干草饲养的牛的牛奶的价格，是用饲料喂养的牛的牛奶的价格的三倍，当然，这两种牛都是圈养的，养法和猪、鸡一样。但是，如果在乌鲁木齐的南山徒步，如果遇到哈萨克的毡房，你可以买一点牛奶，这个牛奶是刚刚吃了草地上新鲜青草的、散步归来的牛产的奶。那么这样的牛奶的价格，应该是圈养的吃饲料的牛的牛奶的价格的多少倍呢？<br/><br/>不过，这种牛奶烧开了，会有点青草和牛粪混合的味道，内地驴友也许会不习惯。<br/><br/>15、哈萨克人的酸奶疙瘩:欧洲人叫奶酪，价格就高了，其实就是哈萨克人的奶疙瘩。可以做成酸的，耐久；也可以不脱脂，做成淡的，就是奶酪了。<br/>这东西营养价值极高，内地驴友初到草原吃不惯，你就当是瑞士人做的奶酪吧，就能吃惯了。<br/><br/>16、奶茶:奶茶在新疆、西藏、内蒙、青海都能喝到，但风格略有差异。<br/>新疆奶茶主要是咸奶茶，将砖茶熬好，冲泡碗底的一点牛奶即可。<br/><br/>奶茶能补维生素，有利于消化，有利于理解新疆人的生活和草原文化，也是驴友来疆的必修课之一。<br/><br/>17、马奶子:很多哈萨克人不愿意下山居住，据说原因是下了山就喝不到马奶子了。马奶必须发酵有喝，略有酸味，有一定酒精度数，是7-8月清凉解暑的最佳饮品。据说马奶中富含多少多少营养，不知道，反正是好喝，而且上瘾。对于一户哈萨克人来说，围坐在一盆马奶子的周围是一件及其幸福的事情。<br/><br/>马奶子好喝且营养丰富，可能还有另外一个原因：马匹常年在草原上活动，吃的是精选的百草，从不吃饲料。而且马奶只在7-8月产马驹的时候才有，所以可称得上是饮料中的极品。目前草原上的价格是每公斤17元，四倍于啤酒的价格。<br/><br/>18、清炖羊肉:这种吃法在内蒙叫手把肉，新疆叫手抓肉。就是把羊肉清炖，加点盐就行。当然这种肉最好选草原上的3-5月的羊羔肉，羊圈里集中养，吃饲料长大的，进过冰箱的口感都会差一些。<br/>在乌鲁木齐的山西巷子一带，有好几家“羊羔肉”店，其中有一家回族开的“吾吾子”，其他几家是维吾尔人开的。价格大约是羊肉市场价格的2倍。<br/><br/>19、烤全羊:号称新疆美食的极品，不过本地人也很少吃，毕竟是烤全羊。但是在山西巷子一带，也有卖烤全羊的，要那块就切那块，价格也大致是羊肉市场价格的两倍。2011年的价格是一公斤80块钱左右，两个人吃的话，买半公斤就行了</p>', 'admin', 3, 0, '77', 0),
(73, '靖港八大碗', '2017-09-18', '<p></p><p>靖港八大碗常于重大节目烹制，用于招待贵宾。有八大菜式，从第一碗到最后一碗分别是什锦杂烩，鱿鱼笋子，五圆肚片，五圆整鸡，八宝果饭，清炖牛肉，黄焖鲜鱼和虎皮炖肉。<br/><br/>1、什锦杂烩：杂烩又叫七层楼，因为它将七种菜料分层堆码，在碗中码出一个七层小楼，故有此名。又名什锦杂烩，亦寓其品种繁多，色性多样。它是宴席上最受欢迎的一道菜。在餐馆中，有时也单独点上一份杂烩，另配数样菜肴便成一席。<br/><br/>2、鱿鱼笋子<br/>此菜虽称鱿鱼笋子，却是笋子为主，鱿鱼只是盖码。第一道杂烩既出，大家口中有了些许油腻，此时再来一道青素的笋丝，自然分外爽口。<br/><br/>3、五仁肚片<br/>靖港人喜食猪肚，旧时走亲戚多是孝敬长辈或探望病人才送猪肚。既可增强体质，又是补血之良效。前两道菜为咸菜，而五仁肚片无盐而味甜，可使口味更换。而且五仁各有药性，也是靖港八大碗从传统药膳中吸取精华而创造的一道菜肴。<br/><br/>4、五圆整鸡<br/>五圆整鸡既略含甜味，又比五仁肚片稍加油腻，此时食客已呈半饱之式，酒过三巡，正需借油味化解口中酒味。五圆整鸡此时上桌，恰得其时。<br/><br/>5、八宝果饭<br/>八宝果饭是一道纯粹的甜食，此时菜上五道，食客正等饭食，有此八宝果饭，足以充腹之饥。因其味甜而烂软，颇受老人和儿童欢迎。每至撤席，此饭总无剩余。<br/><br/>6、清炖牛肉<br/>清炖牛肉是八大碗唯一的汤菜，同时又是极为鲜美的肉菜。此时食客满口甜味，用此清炖牛肉，喝上几口清汤,可以去除甜腻。同时，清炖牛肉也是一个转折，它在不经意间从甜菜、甜食转向后面的两道咸菜。<br/><br/>7、虎皮扣肉<br/>虎皮扣肉肉皮酷似虎皮，且带斑纹，整肉枣红，间以金黄纹理，呈马蹄圆形扣放盘中，造型优美，色泽诱人。虎皮柔中带脆，与肥肉若断还连，筷子一拔即可分条撕下。<br/><br/>8、黄焖鲜鱼<br/>清炖牛肉以鲜著称，黄焖鲜鱼也是以鲜取胜，但两道菜的鲜各有不同。牛肉是带有腻带膻味的鲜，鱼则是清淡带腥味的鲜，两鲜入口，各逞其味，食客至此，已觉口舌生津，八大碗宴席也渐入尾声了。</p>', 'admin', 3, 1, '78', 0),
(74, '青海 郭麻日村', '2017-09-18', '<p><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">站在郭麻日寺塔顶可以俯瞰郭麻日古寨全景</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">郭麻日村是同仁土族集中的地区，为同仁古寨之一，其古寨城墙是为军事防御而建造，对研究中国古代屯守和现代战争城防学具有重要价值。还有郭麻日寺时轮塔风格别具，端午风情令人倍感新奇。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">郭麻日村位于同仁县隆务河以西8公里处，古寨城墙始建于明代初期，以夯土板筑，呈长方形，开东、西、南三门，东门为正门，寨内仍有土</span><a href="http://www.goodzhen.com/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">民居</a><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">住，巷道布局星罗棋布，错综复杂，如进入迷宫一般。每·处寨门顶上都设置嘛呢经轮，是古寨建筑独具特色的地方。为改善狭小的寨内空间，当地居民均修建了二层木结构楼房，和青海乡村</span><a href="http://www.goodzhen.com/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">民居</a><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">庭院宽敞的特点形成了反差。寨内民居庭院多为四合院式，房屋为土木结构平顶房，一般民居都有飞椽花藻之类。屋内一般以木板作隔扇室内有护炕木板、护墙木板墙围，且多雕花草于其上。房屋一般面阔三间，正面以木板隔墙并装上木板条方格小花窗。佛堂设在二楼，佛堂所在的房屋一般都是上房，和佛堂不同向的两边厢房一般做卧室。院落中央一般都有竖挂经幡的旗杆，还设有桑台，具有明显的藏式特点。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><a href="http://www.goodzhen.com/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">古村</a><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">里最有名气是郭麻日寺，藏语全称“郭麻日噶尔噶丹彭措林”，意为“郭麻日具喜圆满洲”，初建于明万历年间（1391年）叶什姜活佛于该寺建三层楼式弥勒殿座，首创正月祈愿法会，成为该寺依怙，是叶什姜活佛的下属寺院。寺庙98年开放，现有大经堂、弥勒殿、建隆务仓和堪布仓昂欠（府邸）等，其信仰者主要为郭麻日村群众。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">郭麻日寺主殿周围有12座小塔，塔顶为圆拱鼓形，塔拱内部设有佛堂。塔基周围环绕藏式平顶小屋，内置嘛呢经轮。整座佛塔建筑结构严谨、布局新颖别致、造型较为特殊、建筑雄伟，构思奇巧，典雅大方，充分显示了热贡地区藏传佛教建筑艺术的高超和热贡艺术及技艺的精湛。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">郭麻日寺前的时轮塔为藏区最大的佛塔，其颜色之艳，建筑风格之独特、造型之美、耗资之巨、民族特色之浓，在我国藏区首屈一指。1994年，此塔由该寺住持设计，高五层，其基座是一圈转经长廊。此塔仿造古印度波罗奈城鹿野苑释迦牟尼初转法轮所在地佛塔的造型，历时5年建成，高33米，塔基周长33米，占地面积约］56平方米，下面基座成佛寺的门廊形式，层叠而上，塔心中以十世班禅的袈裟等衣物及生活用具装藏。可沿塔内的盘旋式阶梯逐层登高，沿塔的外壁上塑有菩萨、观世音和35尊般若佛像。主塔四周有小塔塔顶设有佛堂，是安多地区最高最大的佛塔，故又称“安多第一塔“。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">郭麻日村民在端午节清晨起床后，在太阳出来之前，家中全部人员到自家麦田中打滚，沾染晨露，并到隆务河边洗脸、洗澡，因为传说端午节这天麦苗上的甘露及河水都是神水。尽管已是夏季，但清晨的河水仍冰凉清澈，而青年男女则分区洗浴，以祛病驱邪，年纪稍大者洗佥洗脚，象征性地洗浴。妇女们在日出之前将家中所有盛水器具打满，而后做韭菜包子。其挑来的新水先要叫家中老幼喝一口，再作他用。从河边回来时要折柳枝、掐鲜花。男人们主持在门顶插柳枝和鲜花，同时要给家中小孩用柳条扎一条打铺鲜花的腰带，小孩身上的柳条和鲜花要在太阳出来后去掉。这天早晨还要给家中的马、驴等牲口剪一些鬃毛和尾毛，以祈六畜平安。还要在野外采集一种有气味发臭的草，名叫“赛日埃松”，采回后晒干，可备药用，主治皮肤病之类频病。韭菜包子蒸熟后，要先给出家为僧的亲戚送去，并召回已出嫁的女儿共享。僧人又往往是主持本家日常佛事的阿卡，当地土语称“安确”，汉语意为“先人”。每家都有一个自己的“安确”，—年中逢年过节都要送食品给“安确”，以示尊敬，并附有请其勿忘为本家的先人（亡人）诵经祈祷之意。对当年家中有丧事的亲朋好友团馈赠美食，以示慰藉和情谊。这些都h活完后全家人共享端午包子。吃完早饭，每家都要有人到一个名叫“吉东”的山上煨桑，同时还要到村里各自“措哇”（部族）的“本康”（供神的宗教场所）煨桑。之后家中男女老少到河边玩耍娱乐。这天往往要着新装，条件好的家庭则在河边扎帐篷游玩一天。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">端午节后，村民开始进行抵挡冰雹、暴雨的祭祀仪式，即每年在某固定的田埂自然土堆上，插上三叉树枝并用树枝捆成人形（藏语称“赛日尕”）。并有专门的僧人负责全天候观察天气，一旦有暴雨及冰雹候兆，便吹法号阻挡，如若无效，寺内要转动一种特殊的经轮，俗信其所产生的风能将乌云吹散，此经为万不得已时使用。这种观天象活动直到秋收为止。据说此仪式以前由部落头人主持，现在由各分队队长或选出的年轻人主持，逐年轮流，并无咒语之类。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><strong style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);">旅游TIPS：</strong><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">青海省黄南藏族自治州同仁县年都乎乡郭麻日村。西宁发往同仁县的班车途经郭麻日村。 票价10元；同仁县旅游局电话：0973―8722969</span></p>', 'add', 1, 1, '79', 0),
(75, '中国八大菜系', '2017-09-18', '<p><strong></strong></p><p><strong>徽菜：</strong>徽菜又叫皖菜，是安徽菜的简称，是中国八大菜系之一。 安徽风味，主要由皖南、沿江和沿淮三方菜式组成，其中以皖南菜为代表。皖南菜源于古微州府，即今世界闻名的旅游胜地黄山脚下歙县一带；沿江菜系指合肥、芜湖、安庆一带的地方菜；而沿淮菜则由蚌埠、宿县、阜阳等地方风味构成。三支徽菜各有千秋，丰富多彩。但归纳起来，它主要有四个方面的基本特征：<br/><br/>一是就地取材，以鲜制胜。徽地盛产山珍野味河鲜家禽，就地取材使菜肴地方特色突出并保证鲜活。二是善用火候，火功独到。根据不同原料的质地特点、成品菜的风味要求，分别采用大火、中火、小火烹调。三是娴于烧炖，浓淡相宜。除爆、炒、熘、炸、烩、煮、烤、焐等技法各有千秋外，尤以烧、炖及熏、蒸菜品而闻名。四是注重天然，以食养身。徽菜继承了祖国医食同源的传统，讲究食补，这是徽菜的一大特色\n 。<br/><br/><strong>浙菜：</strong>浙江菜简称浙菜，是浙江地方风味菜系。 \n浙江是江南的鱼米之乡。浙菜发展到现代，是精品迭出，日臻完善，自成一统，有“有佳肴美点三千种”之盛誉。归纳起来，浙菜有如下几大特征：一是用料广博，配伍严谨。主料注重时令和品种，配料、调料的选择旨在突出主料、增益鲜香、去除腥腻；二是刀工精细，形状别致；三是火候调味，最重适度；四是清鲜嫩爽，滋、味兼得；五是浙菜三支，风韵各具。<br/><br/>浙江菜主要由杭州、宁波、绍兴三支地方风味菜组成，携手联袂，并驾齐驱。杭州素有“天堂”之称。杭州菜制作精细，清秀隽美，擅长爆、炒、烩、炸等烹调技法，具清鲜、爽嫩、精致、醇和等特点。宁波地方厨师尤善制海鲜，技法以炖、烤、蒸著称，口味鲜咸适度，菜品讲究鲜嫩爽滑，注重本味，用鱼干制品烹调菜肴更有独到之处。绍兴菜品香酥绵糯，汤浓味醇，富有水乡古城之淳朴风格。<br/><br/><strong>湘菜：</strong>湖南菜又称湘菜，由于湖南民丰物博，向称鱼米之乡。 湖南菜以腴滑肥润为主，多将辣椒当主菜食用，不仅有北方的咸，也有南方的甜，更有本地特色之辣与酸。香、嫩、清、脆是其特色，所用材料以新鲜、价廉物美为原则。<br/><br/>湖南菜特别讲究原料的入味，技法多样，有烧、炒、蒸、熏等方法，尤以“蒸”菜见长。最为精湛的是煨，原汁原味。且刀功精妙，形味兼美，菜肴千姿百态，变化无穷。<br/><br/>湖南菜的特殊料有豆豉、茶油、辣油、辣酱、花椒、茴香、桂皮等，使湖南菜增色不少。<br/><br/>湖南菜以辛辣著称。特别值得一提的是湖南的辣椒。湖南人对辣椒“宠爱有加”，几乎吃什么都放辣椒。湖南的辣椒也特别辣。<br/><br/><strong>苏菜：</strong>江苏菜简称苏菜，以苏州和扬州菜为代表，是中国著名的八大菜系之一。 江苏的历代名厨造就了苏菜风格的传统佳肴，而古有“帝王洲”之称的南京、“天堂”美誉的苏州及被史家叹为“富甲天下”的扬州，则是名厨美馔的摇篮。江苏菜系正是以这三方风味为主汇合而成的。<br/><br/>概括起来，江苏菜有如下几个特点：一是选料严谨，制作精细，因材施艺，按时治肴；二是擅长炖、焖、煨、焐、蒸、烧、炒等烹饪方法，且精于泥煨、叉烤；三是口味清鲜，咸甜得宜，浓而不腻，淡而不薄；四是注重调汤，保持原汁。其中南京刀工细腻，火工纯熟，菜肴滋味醇，兼有四方之美，适应八方口味，尤以鲜香酥嫩取胜；苏州菜口味趋甜，以烹制四季佳蔬、江河湖鲜见长；扬州菜史称淮扬风味，刀工精细，火候精微，色调清新，造型别致，突出主料，强调本味，清淡可口，适应面宽，尤以擅长制汤而著称。</p><p><strong>闽菜：</strong>福建菜俗称闽菜，以福州菜为代表，素以制作细巧、色调美观、调味清鲜著称。 \n福建菜以海鲜类为主，口味方面则咸、甜、酸、辣具备，咸的调味品有虾酱、虾油、豉油等；酸的有白醋、乔头等；甜的有红糖、冰糖等；辣的有胡椒、芥末等；香的有红糖、五香粉、八角、桂皮等。福建菜对清汤的调制特别讲究，一般都以油鸡、火腿、蹄膀为用料。方法是先用小温火将油鸡、火腿、蹄膀等熬出汤汁，并过滤；另将生鸡骨斩碎，加水和盐调和，放入汤内，继续用小温火边烧边搅匀（又称吊汤），然后再过滤一次，便成为莹洁鲜美的清汤，用来调制菜肴，对色、香、味均有帮助。<br/><br/>福建菜也有煎、炸、炻（如煮）、烤、炖、拌、醉、卤、扒、糟、煨、扣、溜、炒、熏、焖、扛、腌、炝等，其中最具特色的是糟，有扛糟、炝糟、爆糟、炸糟之分。<br/><br/><strong>粤菜：</strong>广东菜简称粤菜，是中国著名八大菜系之一。由广州、潮州、东江客家菜三种地方菜构成。而三支 \n地方菜又有各自不同的特色。 广州菜是粤菜的主要组成部分，以味美色鲜、菜式 丰盛而赢得“食在广州”的美誉。广州菜有三大特点： \n一是鸟兽虫鱼均为原料，烹调成形态各异的野味佳肴； 二是即开刀、即烹和即席烹制，独具一格，吃起来新鲜 \n火热；三是夏秋清淡、冬春香浓，深受大众的喜爱。<br/><br/>潮州菜是在广东菜中占有重要的位置。潮菜主要以 海味、河鲜和畜禽为原料，擅烹以蔬果为原料的素菜， 制作精炒，加工多样。可分为炒、烹、炸、焖、炖、烧 、烤、焗、卤、熏、扣、泡、滚、拌，刀工 讲究，汤菜功夫尤深，其中以清炖、红烧、汤泡最具特 色。<br/><br/>东江菜又称客家菜，用料以肉类为主，原汁原味， 讲求酥、软、香、浓。注重火功，以炖、烤、煲、焗见称，尤以砂锅菜见长。做法上仍保留一些奇 巧的烹饪技艺，具有古代中原的风貌。<br/><br/><strong>鲁菜：</strong>山东菜简称鲁菜，是中国著名的八大菜系之一，也是黄河流域烹饪文化的代表。 山东菜可分为济南风味菜、胶东风味菜、孔府菜和其他地区风味菜，并以济南菜为典型，煎炒烹炸、烧烩蒸扒、煮氽熏拌、溜炝酱腌等有50多种烹饪方法。<br/><br/>济南菜以清香、脆嫩、味厚而纯正著称，特别精于制汤，清浊分明，堪称一绝。胶东风味亦称福山风味，包括烟台、青岛等胶东沿海地方风味菜。该菜精于 \n海味，善做海鲜，珍馔佳品，肴多海味，且少用佐料提味。此外，胶东菜在花色冷拼的拼制和花色热菜的烹制中，独具特色。孔府菜做工精细，烹调技法全面， \n尤以烧、炒、煨、炸、扒见长，而且制作过程复杂。以煨、炒、扒等技法烹制的菜肴，往往要经过三四道程序方能完成。“美食不如美器”，孔府历来十分讲究 \n盛器，银、铜等名质餐具俱备。此外，孔府菜的命名也极为讲究，寓意深远。<br/><br/><br/><strong>川菜：</strong>四川菜简称川菜，是中国著名的八大菜系之一，历史悠久，风味独特，驰名中外。 随着生产的发展和经济的繁荣，川菜在原有的基础上，吸收南北菜肴之长及官、商家宴菜品的优点，形成了北菜川烹、南菜川味的特点，享有“食在中国，味在 四川”的美誉。<br/><br/>川菜讲究色、香、味、形，在“味”字上下功夫，以味的多、广、厚著称。川菜口味的组成，主要有“麻、 辣、咸、甜、酸、苦、香”7种味道，巧妙搭配，灵活\n 多变，创制出麻辣、酸辣、红油、白油等几十种各具特 色的复合味，味别之多，调制之妙，堪称中外菜肴之首， 从而赢得了“一菜一格，百菜百味”的称誉。<br/><br/>川菜在烹调方法上，善于根据原料、气候和食者的要求，具体掌握，灵活运用。38种川菜烹调方法中，现在流行的仍有炒、煎、炸、烧、腌、卤、煸、泡等3 0多种。在烹调方法中，特别以小煎小炒、干烧干煸见 长。川菜与四川风景名胜一样闻名于世，扬名天下。<br/></p>', 'admin', 3, 0, '80', 0),
(76, '新疆 麻扎村', '2017-09-18', '<p>麻扎村这个神秘的古老村落，给人带来了无限的诱惑，它的文化，它的历史，它的宁静，让人仿佛置身于世外桃源。这里的居民们日出而作，日落而息，恬淡的生活气息俯拾即是，溪边那穿着鲜亮的民族服饰洗衣的妇女，如时光隧道的券顶窑房下那散步的蒙纱老妪......<br/><br/>麻扎”是阿拉伯文的音译，意为“圣地圣徒墓”，主要指伊斯兰教显贵的陵墓。麻扎村坐落在火焰山南麓吐峪沟大峡谷南沟谷，西距中国地势最低、气候最炎热的新疆吐鲁番市约47公里，东距吐鲁番鄯善县城约46公里，有着1700多年的历史，是迄今新疆保存最好、最古老的维吾尔族村落。村子掩映在白杨和桑树之中，一条不宽的河从北向南穿村而过，沿河两边，建有零散的维吾尔族<a href="http://www.goodzhen.com/" target="_blank" class="keylink">民居</a>。宏大的清真寺与杂乱的居室并立于沟谷，完整地保留了古老的维吾尔族传统和民俗风情。<br/><br/>在麻扎古老的村落中，有新疆最古老的原生态葡萄园，一棵葡萄三亩地的“葡萄王”比比皆是。村中居民们继承了两千多年来用黄粘土建造房屋的传统习惯，一些建筑还遗留着佛教文化和伊斯兰教文化交相融合的印记，是至今国内一座保存完好的生土建筑群，堪称“中国第一土庄”，当地流传着这样的俗语：“土房土房，土坯砌房，不用木材不用砖墙，冬暖夏凉干净舒爽。”整个村子都是土黄色土黄的山、土黄的屋，有独立成房的，也有沿山势连成片的，远远望去，似乎空无一人。家家户户由弯曲和深浅不一的小巷相连，即使从屋顶走也可达到串门的目的。古老<a href="http://www.goodzhen.com/" target="_blank" class="keylink">民居</a>的门窗都很古朴，但又蕴藏了深厚的文化。门框上刻有各种纹样的木雕门铛，有花卉形状、几何形状和果实形状。窗框窗格上的纹样也是多种多样，反映房屋主人的职业、爱好或地位。<br/><br/>在麻扎村，有座著名的麻扎—艾苏哈卜·凯赫夫麻扎，是新疆古老、最著名的两个麻扎之一，也是世界伊斯兰教七大圣地之一，距今已有1＂0多年的历史了。相传，公元7世纪，穆罕默德创立了伊斯兰教，其弟子叶木乃哈等5人来中国传教，一路东行来到：吐鲁番，遇到一位携犬的牧羊人，成为第一个信仰伊斯兰教的中国人。随后便结伴同行来到吐峪沟，在一山洞中修行，6人一犬最终成圣，后人将他们葬于此洞。洞内现存六个土坟和一块不规则的石头石头的形状有点像犬，后人便将它代表与圣贤同行的牧羊犬。因此，这里又有“七圣贤墓”之称，堪称中国第一大伊斯兰教圣地，被称为“中国的麦加”麻扎村也因此得名。<br/><br/>另外，在麻扎村内的清真寺是麻扎村人共同设计参与建造和施工的集体作品，它们既是宗教文化的产物，也是历史文化的积淀，科考价值很高。<br/><br/>在艾苏哈卜·凯赫夫麻扎的西边不远处，有一个千佛洞，是佛教传入中国最重要的驿站之一，是新疆著名的三大佛教石窟之一，也是吐鲁番地区现存于高昌时期最早、最大、最具有代表中的石窟群。洞内的石窟壁画是研究我国佛教文化、佛教美术史、建筑史的历史依据和实物资料，具有不可估量的历史和艺术价值。现存有编号洞窟4）个，其中9个洞窟壁画保存较好。<br/><br/>漫步在村子的小路上，看不到现代化的交通工具，只有毛y车慢悠悠地在小路上穿行；一条从坎儿井引出的小河穿行在村子的中央，河边绿树成荫，孩子们在那里嬉戏打闹。平时村子很安静，每逢节日，他们穿上自己最漂亮的衣服，齐聚街头，跳着维吾尔族最古老的“麦西来甫”舞蹈，使用古老的维吾尔语交际，相互表达节日的问候。<br/><br/><strong>旅游TIPS：</strong><br/><br/>新疆维吾尔自治区吐鲁番地区鄯善县吐峪沟乡麻扎村。可从吐鲁番市区包车前往麻扎村。 麻扎村30元。千佛洞30元。鄯善县旅游局0995-8389687。农家乐0995-8360082<br/><br/>麻扎村是一座反映新疆东部伊斯兰文化背景下村落格局形态的典型代表，对研究伊斯兰文化和干旱少雨的沙漠绿洲文化的形成发展有着重要的实物资料意义。</p>', 'add', 1, 26, '81', 3);
INSERT INTO `tp_message` (`id`, `title`, `time`, `content`, `author`, `cid`, `hit`, `top_img_id`, `collect`) VALUES
(82, '姑田游大龙', '2017-09-19', '<p><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505780631604123.jpg" title="1505780631604123.jpg" alt="20150310093915929.jpg"/></span></p><p><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">福建连城姑田游大龙获得吉尼斯世界记录，游大龙始于明代，至今已有四百多年的历史，游大龙是世界上最长的游龙，姑田的龙每年制作，擎游之后，每年烧掉。有一整套的仪式、祭拜、在指定地点集中焚烧、争先恐后把龙架扛回家（奔跑）……有特殊的寓意。烧龙的环节宛如凤凰涅槃，有送龙升天的意思。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">每年元宵节下午，在锣鼓喧天中，数千名当地中青年壮汉抬着三百余节的大龙在彩旗队、灯箱队的引导下，蜿蜒于中堡、上堡等村落山野田间，穿行与大街小巷，腾挪起伏，活灵活现。沿途村民和海内外游客趋之若鹜，踩着泥泞的田野相随，游龙队伍达公里长。家家门前燃松明，点香烛，摆果茶，放炮竹，迎接“龙游大地，喜到人间”。也是百姓祈求风调雨顺、政通人和的一种</span><a href="http://www.goodzhen.com/html/minsu/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">民俗</a><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">活动。</span></p><p><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505780863136019.jpg" title="1505780863136019.jpg" alt="20150310093857958.jpg"/></span></p><p><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505780882117515.jpg" title="1505780882117515.jpg" alt="20150310093858652.jpg"/></span></p><p><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505780894949281.jpg" title="1505780894949281.jpg" alt="20150310093858668.jpg"/></span></p><p><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505780914312668.jpg" title="1505780914312668.jpg" alt="20150310093915401.jpg"/></span></p>', 'admin', 2, 1, '87', 0),
(83, '束河古镇正月十五棒棒会', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505780946111241.jpg" title="1505780946111241.jpg" alt="20150307054325395.jpg"/></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">每年的农历正月十五，是纳西族传统节日棒棒节。棒棒会实际上是为春耕作准备的一次农具交易的盛会。邻近各县的农民，乃至大理、保山、西藏一带的农民和商人，都携带本地特产到集会上出售， 然后购买所需物品。交流会上，不仅农具、果 树苗木、花卉盆景种类齐全，而且小孩玩具及日用杂货都能买到。</span></p>', 'admin', 2, 55, '88', 2),
(84, '吕梁谢土', '2017-09-19', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">谢土在中国古代有部分地方流传过这一活动，时至今日这种</span>民俗<span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">活动大部分已经消失了，如今山西省吕梁市农村还遗存着这一古老的</span>民俗<span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">活动。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">谢土其实是古代房屋盖成后酬谢土神的一种祭祀形式。民间相信所有的土地都是由七十二位土神掌管着、要动土，必先得到土家的认可，如果没有事先的到土神的认可事后一定要谢土，感谢土神并希望土神能赐福于主人。谢土活动一般由风水师主办，风水师会事先选一黄道吉日，然后设坛诵经，此前主人事先要做一些准备，如五方厚土，五色神石，五方之水，还有朱砂，木碳，海马，海驼，柏板，石燕，枊条，枣枝，书本，车辐，文房四宝，新砖新瓦，七色彩纸，五色彩线，五色彩布，五谷杂粮，新针七个，红布七尺，宾铁铜剑，桃弓枊箭，精药茗香，黄表香烛，鸡蛋油灯，此外还有新蒸好的一框供品等等。然后举办一系列镇宅仪式，用五谷洒遍家宅，门前贴上灵符，这样仪式才算完成。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">谢土是中国千年的风俗，是人们一种向往美好生活的追求和信念。也是人们在与自然抗争中为自己燃起的一盏心灯。</span></p>', 'admin', 2, 1, '89', 0),
(85, '成都灯会', '2017-09-19', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">成都春节灯会会期一个月。灯会期间，公园内有民间曲艺、杂技、杂耍表演；有各种地方风味小吃；还有铸糖人的担子，卖风车的草把子，每届灯会游人都在百万人次以上。成都灯会是在元宵赏灯习俗的基础上发展起来的一种传统</span><a href="http://www.goodzhen.com/html/minsu/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">民俗</a><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">活动。南宋诗人陆游在《丁酉上元》诗中描述成都灯会盛况是“突兀球场锦绣峰，游人仕女拥千重；鼓吹连天沸午门，灯山万炬动黄昏”。</span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505781076360132.jpg" title="1505781076360132.jpg" alt="20150305030627419.jpg"/></span></p>', 'admin', 2, 5, '90', 0),
(86, '安昌腊月风情节', '2017-09-19', '<p style="text-align: left;"><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);"></span></p><p style="text-align: left;"><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">其实只要有时间有机会，去安昌</span><a href="http://www.goodzhen.com/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">古镇</a><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">体验年味是一件很有趣味的事情。 古韵深深，情缘深深，安昌每年都举办古镇腊月风情节。古镇除了在春节期间有除旧、迎新、守岁等年事文化外，水乡婚礼、划龙舟、水上拔河等群众竞赛，越剧、皮影戏、莲花落等民间演出也会天天上演。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">素有“碧水贯街千万居，彩虹跨河十七桥”美誉的安昌古镇，是绍兴四大古镇之一。现存三里长的古街市，骑楼、古台门、石板弄堂、乌篷小船等古镇风情应有尽有。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">腊月风情节，一年举办一回，以“越地风情古镇，绍兴师爷故里”为主题的。走在安昌古街上，你将看到裹粽子、搡年糕、扯白糖、打棕绳、老裁缝等传统</span><a href="http://www.goodzhen.com/html/minsu/" target="_blank" class="keylink" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(49, 48, 33); line-height: 22px; width: 26px; height: 22px; background: rgb(255, 255, 255); border: 1px dotted rgb(170, 170, 170); text-align: center; white-space: normal;">民俗</a><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">。如果你饶有兴致，还可以试试身手，亲身体验一番。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(102, 102, 102); font-family: 宋体, sans-serif; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">安昌最富特色的，要数灌腊肠了。满街腊肠，有的晒在屋檐下，有的挂在河沿边，在冬日暖阳照射下，熠熠发光，年味十足。光是看看，就会诱得人“垂涎三尺”。</span></p>', 'admin', 2, 1, '91', 0),
(87, '黄姚古镇提灯节', '2017-09-19', '<p><span style="font-size: 14px;"><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; background-color: rgb(255, 255, 255);"></span></span></p><p><span style="font-size: 14px;"><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; background-color: rgb(255, 255, 255);">黄姚</span>古镇<span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; background-color: rgb(255, 255, 255);">的提灯会是从乾隆年间开始的，至今已有200多年的历史。每年的农历正月初一至初三，在黄姚宝珠观开坛打醮，连续三日三夜，请来戏班子唱戏，在古戏台唱三日三夜。正月初二晚举行欢庆的重头戏：舞鱼龙提灯晚会。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; background-color: rgb(255, 255, 255);">表演队伍在游行前进中向观众展示光对阵、唢呐、提灯、五花阵、扮饰、万事、扮龙、舞板凳龙、舞狮、舞鱼、舞龟蚌以及表演光对阵、五花阵、提灯等近20个节目。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; background-color: rgb(255, 255, 255);">关于提灯会有一个传说：相传在远古时期海洋中的龙和神鱼对黄姚的秀丽山水情有独钟，经常腾云驾雾来到黄姚游玩，并为之施洒甘露。当地百姓为了感谢龙、鱼的恩赐，同时祈求来年继续赐福，便在每年正月初二晚，自发地组织起舞鱼龙盛会，以庆贺过去一年的平安丰收，同时祈祷新的一年里风调雨顺、年年有余，生活幸福吉祥。</span></span></p><p><span style="font-size: 14px;"><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20170919/1505781186541554.jpg" title="1505781186541554.jpg" alt="20150303103535779.jpg"/></span></span></p><p><span style="font-size: 14px;"><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; background-color: rgb(255, 255, 255);"><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">长长的提灯队伍像一条流动的彩色的河，色彩鲜艳的鱼灯笼都是当地人手工制作的。</span></span></span></p>', 'admin', 2, 0, '92', 0),
(88, '彝族插花节', '2017-09-19', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">在每年的农历二月初八，在云南的大姚县等彝族少数民族地方的男男女女、老老少少都会吹奏芦笙，围成一圈，唱歌跳舞，其中也有情侣，他们采来各种鲜花，编织成花棚和牌坊，花团锦族象征着一年里吉祥如意，和顺安康。这就是云南彝族的插花节。</span></p>', 'admin', 2, 0, '93', 0),
(89, '洛带水龙节', '2017-09-19', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">洛带刘家龙舞直接从中国古代龙舞发展而来，历史悠久，家族内部传承，且一直没有中断，因此，保存了较多的中国古代舞龙最原始的程序和古朴的仪式。水龙九节，夏日伏旱，水龙则大显身手，是为祈雨。舞龙者皆赤裸上身，只穿一条短裤，上下腾挪;观赏者用水枪、水盆泼射龙，前后追堵。客家人似水为财，泼得越湿，人越旺财，舞者和观者攻防进退，煞是刺激，刘家龙也成为客家文化中最具吸引力的一项活动。</span></p>', 'admin', 2, 0, '94', 0),
(90, '洛带火龙节', '2017-09-19', '<p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);"></span></p><p><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">洛带</span>古镇<span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">的客家龙舞以“刘家龙”最负盛名，其名得于参加舞龙的都是江西籍客家人中的刘氏家族人。刘氏家族人在洛带镇已经居住了14代300余年之久，刘家吉庆节日在</span>古镇<span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">舞龙，也已有三百多年的历史了。</span><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><br style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 12px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(68, 68, 68); font-family: 宋体, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">刘家龙舞直接从中国古代龙舞发展而来，历史悠久，家族内部传承，且一直没有中断，因此，保存了较多的中国古代舞龙最原始的程序和古朴的仪式。火龙为七节，原正月十五烧火龙，后来为配合龙泉区的桃花节改在了3月18日。舞龙者皆赤裸上身，只穿一条短裤，上下腾挪;观赏者用烟花喷烧龙，烟花是财运的象征，烧得越红，则吉财越旺。火龙节自重新兴起后在洛带古镇已举行了7届。</span></p>', 'admin', 2, 0, '95', 0),
(92, '芙蓉镇的米豆腐', '2017-09-19', '<div class="content" style="padding: 0px; margin: 0px; font-family: 宋体, sans-serif; font-size: 14px; color: rgb(68, 68, 68); white-space: normal; background-color: rgb(255, 255, 255);"><p></p><p>湖南土家族、苗族地区少数民族的一种传统风味小吃米豆腐，在夏日非常盛行，以芙蓉镇所产的米豆腐最为有名,芙蓉镇的米豆腐其味更是清香爽滑、酸辣可口，放上了香油和辣椒，更是味道十足，香、辣、鲜、嫩。</p><br style="padding: 0px; margin: 0px; font-size: 12px;"/>芙蓉镇的米豆腐色泽润绿明亮，口感清香、软滑细嫩。在0 ℃ ～25℃温度下存放3 天仍能保持新鲜不变质。米豆腐既可冷食也可熟食，风味独特，倍受消费者的青睐。<br style="padding: 0px; margin: 0px; font-size: 12px;"/><br style="padding: 0px; margin: 0px; font-size: 12px;"/>米豆腐其制法和食用方法、味道均与旧时的包谷豆腐相似。将米洗净，浸泡一天，和水磨成米浆。将米浆放入锅内烧热，一边加适量的食用碱（旧时用油桐果壳烧灰熬成的土碱），一边用力搅拌，直至煮熟，再放于盆内，使之冷却，即成。食用时，用细线将成因的米豆腐划成1～2cm3的小颗粒，泡入清水中，再备上各种佐料（辣椒末、香葱、姜末、西红柿酱、味精、酱油等），用热水温烫，盛于碗中加佐料即可。<br style="padding: 0px; margin: 0px; font-size: 12px;"/><br style="padding: 0px; margin: 0px; font-size: 12px;"/>作豆腐的原料一般为大米、大豆、石灰。大米和大豆都用石磨磨细，将磨好的米浆或豆浆倒入铁锅，开始用大火，煮到半熟时用小火，边煮边搅，煮熟为止。当煮熟的米浆变成糊状，趁热装入预先准备好的盛器内。等冷却后，色泽金黄，柔嫩滑口的米豆腐也就做成了。为什么是黄色，这是石灰捣的鬼，它一作凝固，二为调色。<br style="padding: 0px; margin: 0px; font-size: 12px;"/><br style="padding: 0px; margin: 0px; font-size: 12px;"/>有的米豆腐佐料中还会有萝卜丁，黄豆，海带丝酸菜、生姜、油炸花生米。其实佐料也大有学问，比如辣椒，就分油辣椒和酸辣椒。因个人的喜好不同，有喜油的，有喜素的，贪心的两者都要。油辣椒一般都是自家做的，从市场上买回辣椒面或自家将晒好的干辣椒切碎，将锅中烧的滚烫热油，倒入盛有辣椒和其他配料的容器中，“哧啦”一声，辣椒就靠着油的热度炸好了。酸辣椒，是放在坛子里腌制的，去掉了辣的烈性，多了酸的软感。</div><p><br/></p>', 'admin', 3, 0, '97', 0),
(93, '福建和平', '2017-09-19', '<p></p><p><br/></p><p>和平古镇位于邵武南三十公里处，是国家级历史文化名镇，目前还没有进行商业开发，基本保持了中国岭南古镇的原汁原味，在那里可以找到与丽江、周庄、阳朔完全不同的感觉， 古镇从唐朝走过来。地处邵南要冲，却是邵武历史上的第一古镇，历来是邵南地区的政治、经济、文化中心。早在唐天成元年，和平即已形成街市，称&quot;旧市街&quot;、&quot;旧墟街&quot;。想当年和平是邵武沟通闽西南和江西的交通枢纽，兼之此地特产丰富，故商贾云集，附近府县和福州及江西等地均有大量客商前来进行茶叶、笋干、纸张、粮食等贸易。昔日&quot;五天一墟&quot;的繁荣景象当然无比壮观，偏安一隅的和平小镇便在这交易集市中平实耐久地生存着。&quot;旧市街&quot;历时千余年长盛不衰，作为贸易集散地的青石古街区，也得以延续并保持至今。虽经时世变迁，青石小路已成为历史的见证，安祥的横贯在小镇的南北。</p><p><img title="1505807388218730.jpg" alt="20110728123735524.jpg" src="/ueditor/php/upload/image/20170919/1505807388218730.jpg"/>高墙黛瓦马头檐，一座座布满精美石刻的牌坊式门楼上镌刻着大夫第、郎官第、和平书院的字样，一条“岁进士巷”记述着从宋朝以来陆续从和平古镇走出的133名进士，让任何一个人走进这座外表普通的古镇都不能小瞧。<br/>和平古镇不算大，城堡式布局，城墙石头垒成，高不盈丈，布满了青苔和蒿草，谯楼有两座，每座三层，挑梁飞檐，气韵非凡。一条青石板铺就的旧市街贯穿南北，600米长的街道随形就势形成“九曲十三弯”，宛如一条腾空欲飞的青龙，古市街又联通着四通八达的鹅卵石铺成的小巷， “巷深苔藓盛，天小白云稀”，每一条古巷都有一个名字，每个巷名都有一个由来。小巷曲曲折折，一拐弯就会有一座“大夫第”呈现在你面前，两拐三拐就可以看见镇外金黄的稻田，这里智慧的乡民曾经创造了稻田养鱼、田埂种豆的农业耕作技术，这种标准的生态农业模式成就了古镇百姓自给自足的田园生活， “禾坪”演变为和平，和平古镇由此而来。</p>', 'dxy', 1, 0, '98', 0),
(94, '福建培田', '2017-09-19', '<p></p><p>培田村位于福建省闽西山区连城县，目前中国保存较为完整的明清时期客家古民居建筑群，有着八百多年的历史。村中心是一条千米长的古街,贯穿全村,旁列古祠、民居、商铺,是培田的主要商业集市。相传,盛时街上商铺数十间,客枝、轿行、赌庄、布店等等无所不包。 曲折古街,巷道,互为连通,把错落的明清古建筑有机连为一体,&quot;虽是人工,宛若天成&quot;。</p><p>三十幢明清时建的高堂华屋,20座古祠,5处书院,1条千米古街,2座跨街牌坊,组成一个连片成群、规模宏大、布局讲究、设计精美的古民居建筑群村落。这便是&quot;客家祖地&quot;闽西山区连城一个古老的客家山村——培田。</p><p><img title="1505807450975530.jpg" alt="20110322050851882.jpg" src="/ueditor/php/upload/image/20170919/1505807450975530.jpg"/></p><div>培田号称“客家祖地”，客家古民居建筑与永定客家土楼风格迥异。如果说土楼是古堡,封闭而坚固,那么培田民居就是庄园,豪放而优雅。培田民居是继永定土楼、梅州围拢屋之后发现的又一保存完好的客家民居建筑瑰宝。</div><div>培田村数百年前的建村者,极为讲究村落的理水。每一座古建筑都布有暗沟,用来排泄家家户户的天井雨水、生活污水。天井将民居屋面流下的雨水汇聚一处,顺沟而出,流人石砌水池,满足&quot;四水归堂,财源攘滚而来&quot;的聚财心理。排水路径讲究宜暗藏,不宜显露；宜弯曲而去,不宜直泻而出,乃因&quot;水为气之母,逆则聚而不散；水又属财,曲则留而不去也&quot;。有的民居还在厅堂下设有陶制暗水管,放养乌龟在管内爬动,起着排污清沟作用,别出心裁。<br/></div><p><br/></p>', 'dxy', 1, 0, '100', 0),
(95, '福建永定', '2017-09-19', '<p></p><p>永定县位于福建省西南部的龙岩地区，西南界广东省梅州市大埔县。总面积2216 3平方千米。人口46万人（2006年）。通行闽西客家方言。除了客家土楼之外，王寿山被确定为国家级森林公园。这里分布着历史悠久、风格独</p><div><img title="1505807493722934.jpg" alt="20110601101748654.jpg" src="/ueditor/php/upload/image/20170919/1505807493722934.jpg"/>永定县位于福建省西南部的龙岩地区，西南界广东省梅州市大埔县。总面积2216.3平方千米。人口46万人（2006年）。通行闽西客家方言。除了客家土楼之外，王寿山被确定为国家级森林公园。这里分布着历史悠久、风格独特的客家民居建筑群，它们被统称为“永定土楼”。永定旅游资源特色独具，有闻名海内外的世界文化遗产景区土楼、如诗如画的龙湖、王寿山等自然、人文景观，“东楼西湖”旅游格局初步形成。“福建土楼”申报“世遗”工作被列入国家计划，东华山和王寿山被列为国家级森林公园，龙湖开发 永定县西陂天后宫有序进行。永定是省重点旅游县和对外开放旅游经济区。县境内山峦起伏、雄奇峻秀，溪河穿山切岭，山清水秀，温泉喷溢，气候温和，人杰地灵，造就了众多的自然景观、人文景观和革命胜地。主要有被誉为“世界上独一无二的山村民居筑”的永定客家土楼，因其奇特的建筑艺术和蕴藏的丰富客家文化内涵而备受世人瞩目；棉花滩水电站的建设，将把汀江宽展成万顷碧波的龙湖，昔日的群山变成宁静的绿岛，水光山色，曲径通幽，拾石径登临湖边千米高山——王寿山顶峰，怪石悬崖，云雾缭绕，恍若仙境。此外，永定还有茫荡洋、仙湖洞、燕子岩、东华山等探险、科考、佛教圣地旅游资源，有毛泽东、周恩来、朱德、张鼎丞等老一辈革命家革命活动遗址等大批“红色”旅游资源，以及大鼓凉伞、作大福、闹古事、山歌等丰富多彩的客家民俗民风。</div><p><br/></p>', 'dxy', 1, 1, '101', 1),
(96, '福建长汀', '2017-09-19', '<p></p><p>长汀又名汀州，古汀州城是历史上客家人聚居具代表性的城市。但凡描述长汀，人们总爱引用新西兰女作家路易艾黎的话：“中国有两个最美丽的小城，一个是福建的长汀，一个是湖南的凤凰。”这句话听得多了，就成了标签似的名言。悠久的历史给长汀留下了丰富的文化遗产，如巍峨耸立的唐代城楼三元阁、唐代大历四年（公元769年）修建的古城墙、独特罕见的唐宋“双阴塔”古井；还有革命遗址：长汀福建省苏维埃政府旧址、中央红军医院旧址福音医院、周恩来旧居——中华基督教堂和刘少奇旧居——水东街204号。所有这些，足以使人领略汀州古城的历史风采，无愧于“中国两个最美丽的山城之一”。长汀是国家历史文化名城、客家首府，著名的八闽汀州故地和福建古代文明发祥地之一。长汀从盛唐到清末均是州、路、府的治所，亦是客家人的主要聚居地，有“客家大本营”和“客家首府”之称，每年都有大批港、澳、台及东南亚各国的客家人来汀寻根。而绕城而过的汀江更被喻为客家人的母亲河。穿过三元阁宽厚的门洞时，忽然之间,高楼大厦和车马喧哗在瞬间消失,取而代之的是展现在眼前的古朴小巷，柳暗花明之间,竟已踏入长汀的老城区。徜徉在老街和古建筑之间,很容易就忘记了今昔何昔。走进这些民居，仿佛走入了一段历史,走入了一件件中国古代艺术作品中。从陈氏宗祠到赖氏坦园宗祠,再到紫云公祠,从门牌到屋里每一处的雕梁画栋，无不精美绝伦,仿佛在诉说着它们当年的辉煌。曾听人说福建客家民居十分重视门楼建筑,有“千斤门楼两屋”的说法。</p><p><img title="1505807552968817.jpg" alt="20140812125241680.jpg" src="/ueditor/php/upload/image/20170919/1505807552968817.jpg"/><br/>世界客家母亲缘广场及世界客家首府博物馆占地146亩，项目总投资1.58亿元。其中，世界客家母亲缘广场主要将设公祭区、候拜区、名人雕塑区、歌舞表演区、游客接待中心、地下停车场等；世界客家首府博物馆主要将承担收藏、陈列展示、研究交流、传承教育、展览服务等五大功能。目前世界客家母亲缘广场已完成2万多平方米沙石铺设，将力争今年8月底完成主体建设；横穿其中的梅林大桥水下桩基灌注已完成，预计年内可完成主体工程。此外，世界客家首府博物馆也在加快建设，将于明年福建省苏维埃政府成立80周年纪念日前完工。</p><p>汆大肠！很多人爱吃.《店头街往下100米，七星饭店》 这店的肉皮很出名酒醋很香<br/>三元阁有个矮炮大排档《三元阁左边第二排》《玉润商行隔壁就是了》这的汆大肠是现汆的，味道也很好~这的便饭也不错的<br/>干蒸大肠/干蒸猪肉/干蒸满圆都蛮好味道的~<br/>炖大肠吃过没有？在上新桥的路上，任屋加油站旁边有个饭店有，味道很是好~好吃啊~<br/>汆牛肉！非常的有营养是吧！《儿童乐园中门旁边，无名饭店》<br/>汆豆腐角！便宜美味又营养！《三园阁左边第一排进去》二合一，豆腐角跟汆牛肉或者汆猪肉各半<br/>烧麦！《食品厂附近》开了个小店</p><p><img title="1505807561669230.jpg" alt="20140812125550126.jpg" src="/ueditor/php/upload/image/20170919/1505807561669230.jpg"/><br/>豆腐圆！《店头街往下10米靠右》不过现在没有了，老板被房东T了<br/>笼床锡！在原纺织厂后面小巷子！现紫云花园后面！<br/>炖货《很多人叫它炖罐》！个人认为最好的还是在《水东街庙子旁边的有间炖货专卖店》都是真材实料！味道好极了！<br/>很多人喜欢去汀州镇旁边吃，我个人不太喜欢~<br/>煮粉干！非常的有味道！《扬成武纪念馆对面-有间饭店》老板水水亲自煮的，够味！山泉水制作哦~<br/>其实水水最出名的是泡猪腰，不过我自己不太喜欢吃内脏所以就不怎么介绍;[page]<br/>芋子饺！很多人喜欢吃《汀洲医院旁边》<br/>汆鸡麻/汆鸡皮/汆鱼/汆鱼皮！个人认为水东街嘞的好几家都差不多太平桥往水东街走的话右边后面这几家生意比较好~<br/>煎薯饼！水东桥头的不错！水东桥新增了个烧烤滩～味道还行～最有生意的烧烤滩在太平桥头~<br/>煎饺！水东街尾巴头的福州佬做得最好！<br/>三园阁蓝力波的汆猪肉加烧大块不错！店头街往下100米，七星饭店的汆猪肉加肉皮也还可以！<br/>但最好吃的汆猪肉还是棉纺厂旁边在路边摆摊的，要蹲着吃才有味道！《加文西》<br/>刨《炸》肉圆据说是在桥下坝哩，五通桥边的摆摊的！<br/>包粽子！店头街往下200米靠左，五通街入口，味道很正！还有炸春卷《可定做，加肉5毛钱一条》<br/>吃便饭的话可以去横甘岭的香烛店的隔壁吃《老板叫五金》，便宜好吃多~;<br/>说到吃便饭当然少不了西客站里面的那一鱼多吃了~味道确实不错，又便宜~<br/>吃米线的话还是营背天桥底下的云南过桥米线比较正，龙潭路口的没法吃，药味太浓 ，三元阁有家我还没去吃过，不知道味道怎么样~<br/>营背有家肥婆清汤粉里面的豆腐角很好吃，很有盐味，才一块钱一碗。<br/>泡扁肉！〈宏泉鞋城〈即龙洲超市〉旁边小巷子！地道的长汀口味;<br/>介绍了这么多的小吃，附送几道大菜！水东街庙子里〈注！晚上才有〉的王矮子的盐酒鸡很好吃！烧关花跟荷叶猪蹄都很好吃~<br/>陶园大酒店的糖醋蝴蝶鱼味道好极了！想想都留口水！<br/>三元阁的白头公狭!很干净!今天吃了个三元阁的棕子也很不错!跟五通街路口的有比! 五通街路口晚上有个卤货摊子，卤猪耳朵味道很好，不会甜！《水东桥头的那家太甜》<br/>最近老去吃夜市，觉得原纪念碑旧址那家不错，很便宜，就是卤货太差！去了不要点卤货！<br/>新增《桥下坝路口的那家做鸭粉的有做韭菜饺子》味道不错～店头街路口现有炸芋子包，还可以;<br/>路口的三间饭店都是差不多的，基本上都是泡猪腰/拌面，到是中间那家炒点小菜的味道还是蛮好的，最好吃就是他的卤牛肉巴~<br/>原九曲山庄现在叫皇庭九曲山庄，装修非常考究，都是用竹做的，里面的腊狗/腊鸭非常的有特色 ;<br/>还有独家火锅菜，新疆毛肚/天山竹笋/重庆鹅肠/天府豆皮~等等等等~又是个游泳/烧烤/郊游的好去处;<br/>金碧花园左边店面有两家的潮州粥褒，个人认为前面这家的味道比较好，喜欢那的海虾粥，很是美味~</p><p><br/></p>', 'dxy', 1, 0, '102', 0),
(97, '福建芷溪', '2017-09-19', '<p></p><p>芷溪镇位于福建省连城县南部，东邻庙前镇，南接丰图村与上杭县蛟洋乡毗连，西倚上杭县南阳镇，北靠新泉镇，距连城县城52km，距龙岩市区65公里，距厦门265公里。芷溪因古时村边溪流两岸长满芷草而得名。<br/>芷溪群山环抱，芷水蜿蜒，气候宜人，四季如春。有诗云：“历史文化一名村，桃源芷水映金峰。”<br/>芷溪居民重视宗祠建筑，缘于客家人慎终追远、敬祖睦宗的传统美德。<br/>芷溪宗祠文化之黄氏家庙<br/>明清以来，芷溪先民先后建了74座古宗祠，138幢古民居，这些规模宏大的明清时期建筑，被客家研究人士誉为“客家大宅门”。这些古建筑除一部分是专门作祭祖联宗用外，其他都是祠居合一的复合型建筑，普遍采用客家地区“九厅十八井”结构布局建造，门庐威严，庭院舒畅，雕梁画栋，飞檐翘角，雄伟壮观，气势恢宏，美仑美奂。这些古建筑中，比较有代表性的有庚福公祠、龟山公祠、杨辉公祠、峻亭公祠、澄川公祠、翠畴公祠、孝友祠、孔怀堂公祠、华岳公祠、隐轩公祠、耀南公祠、渔溪公屋、培兰堂、余庆堂、永裕堂、万斯堂等。<br/>　　芷溪花灯最有特色，原由苏州引进，迄今已有三百年历史。</p>', 'dxy', 1, 1, '103', 0),
(98, '广东赤坎', '2017-09-19', '<p></p><p>赤坎古镇位于广东江门市所辖的开平市中部，是一个有着浓郁的岭南文化特色的古镇，全镇沿潭江而建，南岸是乡村，北岸是市镇，清一色的骑楼，庞大的洋楼群。千年商埠赤坎古渡口寻迹位于湛江市晨光小学至井街口。1至4号码头西起晨光小学至大通街与平安街交接处，全长180米，是赤坎埠最古老的渡口码头，故称古老渡街。在赤坎古镇闲逛浪荡是一种比较适合的游玩方式，在老街上体验停留于上个世纪80年代的生活，别有一番情调。</p><p><img title="1505807667134491.jpg" alt="20140624012743718.jpg" src="/ueditor/php/upload/image/20170919/1505807667134491.jpg"/></p><p>赤坎古镇闻名的古祠庙有水仙庙、白马庙、关帝庙、双宗庙、康公庙、妈祖庙等。</p><p><img title="1505807677111830.jpg" alt="20140624012748197.jpg" src="/ueditor/php/upload/image/20170919/1505807677111830.jpg"/></p><p><br/></p>', 'dxy', 1, 0, '104', 0),
(99, '广东黄埔', '2017-09-19', '<p></p><p>“黄埔”一名往往和黄埔军校、黄埔开发区和黄埔港联系在一起。其实它起源于汪洋中一个叫黄圃的孤岛，从秦时起各姓在岛上聚族而居，始有村落。明初因陆地面积不断增大，改名为黄浦，明末又改为黄埔，并沿用至今。从名字的更迭中不难看出黄埔村地理环境的变化。</p><p><img title="1505807719594096.jpg" alt="20140625085650672.jpg" src="/ueditor/php/upload/image/20170919/1505807719594096.jpg"/><br/>　　在广东乃至整个中国，再也没有一个村庄能象黄埔一样在历史上发挥过如此重要的作用了。黄埔村位于广州海珠岛东部，濒临珠江。康熙二十四年(1685年)，清政府在这里的酱园码头设粤海关黄埔挂号口、税馆、夷务馆，从此商船云集，黄埔村逐渐成为一个几千人的港口。</p><p><img title="1505807724106977.jpg" alt="20140625085654860.jpg" src="/ueditor/php/upload/image/20170919/1505807724106977.jpg"/><br/>　　1757年，因实行“一口通商”，广州成为中国唯一的对外通商口岸。在此后的八十多年里，广州一直处于中国对外贸易的中心地位，黄埔村也因此成为世界贸易大港而走向繁华的颠峰。<br/>　　1842年，清政府与鸦片战争交战国签订《南京条约》，开放广州、厦门、上海、宁波、福州五处通商口岸。广州的龙头地位逐渐被上海取代，酱园码头也因日渐淤浅，于同治年间(1862-1874年)迁址长洲岛,近两百年辉煌的历史就此终结。</p><p><img title="1505807732737822.jpg" alt="20140625090536306.jpg" src="/ueditor/php/upload/image/20170919/1505807732737822.jpg"/></p>', 'dxy', 1, 1, '105', 0),
(100, '广东坑贝', '2017-09-19', '<p></p><p>增城坑背村位于中新镇坑背村（广汕公路旁），建于明末清初。是一个鲜为人知的古村落，村内的民居建于明末清初，至今保存完整。在村内各个独立房屋形制基本相同，为硬山顶，碌灰筒瓦，锅耳封火山墙，施彩绘，面阔均三间，宽度为10.49米，排、列之间有纵横青云巷。</p><p><img title="1505807799298812.jpg" alt="20140630112922659.jpg" src="/ueditor/php/upload/image/20170919/1505807799298812.jpg"/></p><p>在村子中间建有“式谷毛氏祠”，作为整个建筑群的核心大祠堂。该祠深三进，通宽10.98米，通深30.07米。头门面阔三间10.98米，深三间7.93米，硬山顶，锅耳封火山墙，龙船形正脊，碌灰筒瓦，滴水剪边，花岗石檐柱，覆盆柱础，条石墙裙，额为石雕。二进深达9.67米，形式略同，三进深为8.35米。左后角建有碉堡楼。坑背村保留了清代建筑风格，布局有序，对研究岭南地区古代建筑艺术和乡村民情风俗提供了宝贵的实物资料，是广州市历史文化保护区。‘</p><p><img title="1505807786613633.jpg" alt="20140630112917654.jpg" src="/ueditor/php/upload/image/20170919/1505807786613633.jpg"/></p><p>古村落依山面水而建，清浅的溪流在村前穿过，一幢幢古色古香、富有浓郁岭南风韵的祠堂、书房、村屋、碉楼和树林，依次排列在村里，诉说一段沉沉的历史。像一幅水墨丹青画卷，坑背村展现着它的古趣和淳朴，纵然历史变迁、沧海桑田，人们仍能从一个个的局部来感受到她灵光闪现的魅力。老屋都是砖木结构，青瓦白墙，木格花窗，深具岭南风味。</p><p><img title="1505807790609416.jpg" alt="20140630112921275.jpg" src="/ueditor/php/upload/image/20170919/1505807790609416.jpg"/><br/></p><p><br/></p>', 'dxy', 1, 0, '106', 0),
(101, '广东沙湾', '2017-09-19', '<p></p><p>沙湾古镇是一个具有800年历史的文明古镇，悠久的历史孕育了灿烂的文化。凭借着得天独厚的文化、历史优势，合理的城镇布局，快捷的交通网络，优越的投资环境，精心打造着一个适宜人们居住和创业发展的环境。</p><div> 2005年，800多年历史的番禺沙湾镇成为广东首个“中国历史文化名镇”。沙湾镇悠久的历史可以追溯到南宋初年，当时这里已形成繁华的村落，并孕育了璀璨的文化。</div><div>　　镇上的“广东音乐、沙湾飘色、沙坑友狮、沙湾兰花”四大文化品牌享有盛名。遍布镇内的留耕堂古祠、三善鳌山古庙群、安宁西古街、升平人瑞古巷、宝墨古园等远近闻名。</div><p><br/></p><p><img title="1505807831104283.jpg" alt="20120419095348630.jpg" src="/ueditor/php/upload/image/20170919/1505807831104283.jpg"/>　　特别值得一提的是，沙湾是广东音乐的发祥地，著名的作曲家、演奏家何柳堂及其堂侄何少霞、何与年被誉为“广东音乐何氏三杰”，广东音乐四大名曲中的《赛龙夺锦》、《雨打芭蕉》、《饿马摇铃》均是他们的代表作。他们经常在三稔厅以乐会友。</p><p><img title="1505807836509152.jpg" alt="20120419101323385.jpg" src="/ueditor/php/upload/image/20170919/1505807836509152.jpg"/></p><div>　　此外，集力学、美术、冶炼术于一体的传统民间艺术“沙湾飘色”，造型奇特，每逢喜庆，与舞狮、舞龙、舞鳌鱼等民俗表演并驾齐驱，享誉海内外。沙湾姜撞奶、牛乳、薄饼等美食有口皆碑，千万别错过。</div><div>沙湾何氏大宗祠（即留耕堂），始建于元朝正年（公元1275年），距今700多年。最大规模扩建重修是清代康熙六十年（公元1721年），总占地面积3千多平方米，是一座由青砖、石、木、牡蛎构成的古建筑。清代最后一次重修也比广州陈家祠</div><div>还早170年。是番禺区年代古、规模大、艺术精、格调高的何姓族人的祖祠。其特点：以柱多而闻名，一共112条木、石柱，它汇集了元、明、清各个时代不同风格 的建筑艺术，凝聚了劳动人民的血汗和智慧，加上高超的砖、木、石雕手工艺，</div><div>闪烁着东方古代建筑艺术的光彩。自1986年来重新修葺，恢复原貌。1989年列入广东省级文物重点保护单位，至今一直对外开放，让游人参观。 留耕堂鸟瞰牌坊正面“诗书世泽”，背面“三凤流芳”，是明朝理学家陈献章（陈白沙）书写。</div><div>“留耕堂”、“大宗伯”木牌匾分别由陈白沙及明代广东省中书参知政事郑允成书写。</div><p><br/></p>', 'dxy', 1, 1, '107', 0),
(102, '广东自力', '2017-09-19', '<p></p><p>自力村碉楼群位于广东省开平市塘口镇，是世界文化遗产地之一。该村自然环境优美，水塘、荷塘、稻田、草地散落其间，与众多的碉楼、居庐相映成趣，美不胜收，形成一幅阳春烟景田园诗意般的农耕水墨画，独具岭南乡村气息的洋式城堡村落。</p><p><img title="1505807886275489.jpg" alt="20140620115725289.jpg" src="/ueditor/php/upload/image/20170919/1505807886275489.jpg"/></p><p>　　自力村碉楼和居庐群建筑精美，保存完好，布局和谐，错落有致，四周良田万顷，稻香阵阵，踏着田间小道，穿过绿树修竹直入村内，顿生世外桃源之感。</p><p><img title="1505807892135636.jpg" alt="20140621120411639.jpg" src="/ueditor/php/upload/image/20170919/1505807892135636.jpg"/></p><p><br/></p><div>自力村有15座风格各异、造型精美、内涵丰富的碉楼，是开平碉楼兴盛时期的杰出代表。自力村碉楼多建于20世纪二三十年代，是当地侨胞为保护家乡亲人的生命财产安全而兴建的。</div><p>村内碉楼群以其建筑精美，布局和谐，错落有致，成为开平碉楼兴盛时期的代表。自力村内现有63户人家，175人，但华侨、港澳问胞却有248人，其主要分布于美国、加拿大、英国、马来西亚、香港、澳门等国家和地区，侨汇是这村的主要生活来源之一。</p><p><img title="1505807942741437.jpg" alt="20140621120423120.jpg" src="/ueditor/php/upload/image/20170919/1505807942741437.jpg"/></p><p><br/></p>', 'dxy', 1, 0, '108', 0),
(103, '广西黄姚', '2017-09-19', '<p></p><p>黄姚古镇，位于广西贺州昭平县东北部，与昭平县城及贺州市均相距40公里左右（直距），距桂林200公里。黄姚是有着近千年历史的古镇，发祥于宋朝年间，兴建于明朝万历年间，鼎盛于清朝乾隆年间。由于镇上以黄、姚两姓居多，故名黄姚。现已被列为省级风景名胜区。</p><p><img title="1505807983235814.jpg" alt="20140613115800237.jpg" src="/ueditor/php/upload/image/20170919/1505807983235814.jpg"/></p><div>　黄姚地处漓江下游，素有“诗境家园”之称。镇内有“六多”，山水岩洞多、亭台楼阁多、寺观庙祠多、祠堂多、古树多、楹联匾额多。有山必有水，有水必有桥，有桥必有亭，有亭必有联， 有联必有匾，构成古镇独特的风景。街道全部用黑色石板镶嵌而成，镇内建筑按九宫八卦阵式布局。房屋多为两层的砖瓦结构，建筑精美，工艺高超。　</div><p>　　全镇居民600多户，八条街道，房屋多数保持明清风格，由于黄姚所处特殊的地理位置，四面皆山，易守难攻，而且交通不便，所以村镇处于半封闭状态，使得古老的民居、众多的文物得以保存。抗日时期，这里被定为敌后根据地，大批爱国人士从桂林到黄姚，这些文物、故居至今仍被完好保存着，如韩愈、刘宗标墨迹，中共广西省工委旧址纪念馆、钱兴烈士塑像、何香凝、高士其、千家驹等文化名人寓所，以及许多诗联碑刻。</p><p><img title="1505807989121341.jpg" alt="20140615122921567.jpg" src="/ueditor/php/upload/image/20170919/1505807989121341.jpg"/></p><p>　　黄姚为典型的喀斯特地貌，奇峰耸立，溶洞幽深，清溪环绕，古树参天。自然景观有八大景二十四小景，保存有寺观庙祠20多座，亭台楼阁10多处，多为明清建筑，著名的有文明阁、宝珠观、兴宇庙、狮子庙、古戏台、吴家祠、郭家祠、佐龙寺、见龙寺、见龙寺、带龙桥、护龙桥、天然亭等。</p><p><img title="1505807994353376.jpg" alt="20140615122930807.jpg" src="/ueditor/php/upload/image/20170919/1505807994353376.jpg"/></p><div>　　1、交通：从广州往黄姚古镇没有直达的长途汽车，需由广州到八步（即贺州）转程。从广州往贺州的班车在环市西路省站乘坐，每天有五个班次，从下午开始发车，票价80元。从桂林到黄姚的班车在桂林汽车总站乘坐，每天8:00到17:30，每半小时一班车。从贺州到黄姚包出租车需110元；从黄姚回贺州，好运的话截回程出租车仅需60元。</div><div>　　贺州往黄姚有两条路：一条是从八步先到钟山，路程为40公里，车行约50分钟，票价4--5元，再乘从钟山往巩桥的班车，经过同古等地，在到达巩桥之前6公里处便是黄姚，这一段路程为50公里，班车行约2.5小时，票价8元，这一路路况较好，推荐来回都走这条线路。</div><div>　另一条路是从八步搭到巩桥的班车，路程为66公里，票价8元。巩桥到黄姚有很多电三轮车，约5公里路程，每人一元就可到达。　　离开黄姚的班车也是以上两条线路。 黄姚直接到八步的班车最早班是6：30，9元/人。车速慢，有时甚至不开，不推荐；从巩桥去钟山的班车最早的班次是7：50经过，在县政府门口或路边就可以等到，最好提前15-20分钟，这个班次比较稳定。黄姚到钟山每天有3班车：8：30，11：30，3：00。</div><div>　2、住宿：市场旅社是黄姚最高建筑，在6楼天台可以看到古镇全貌，也是早晨看日出的绝好位置，住宿费为每天10元。所以建议大家在这里住宿。店主人叫做古有源，前黄姚中心小学校长。另外，在黄姚还有其他的旅社，价格也基本上差不多，就是地理位置不如市旅社那样优越。</div><div>　　3、餐饮：黄姚豆豉很有名，可作调味。黄姚豆腐是当地的一大特色菜，有豆腐和豆腐酿，两种都应该尝尝。黄姚还有新鲜的平菇、糍菇，蔬菜也很鲜甜，市场还有草鱼和鲤鱼。黄姚的餐馆不多，所有的菜都是炒菜，想要吃广东的煲仔菜是找不到的。另外，广场对面有一家粥店，有鸡、田鸡、塘虱等粥品，一锅10元，晚上也照常营业。街上有个叫做权二的店，每天白天供应好吃的米粉和云吞，一元一大碗。<br/>　　黄姚也卖油茶，家家都有吃不完的红薯，户户都晒红薯干，在黄姚，红薯你一定不会少吃。不过如果想找可口可乐就比较难了，店主人都说夏天卖完了就不再进货了，怕冬天卖不出去。</div><div>　　4、门票：30元。</div><div>　5、其他：黄姚风景如画，是摄影爱好者的好去处。现在黄姚正在开发中，从桂林连接黄姚的公路预计会在2003年2月完成。 　<br/></div><p><br/></p>', 'dxy', 1, 0, '109', 0),
(104, '广西兴安', '2017-09-19', '<p></p><p>兴安县位于广西的东北部，地处“湘桂走廊”要冲，古为“粤楚咽喉”之地，是湘江、漓江的主要发源地，湘江北去、漓水南流构成兴安奇特的地理环境.</p><p><img title="1505808033133609.jpg" alt="20131211101720761.jpg" src="/ueditor/php/upload/image/20170919/1505808033133609.jpg"/></p><p>兴安县的主要景区有：同为世界之奇观的千古灵渠；有“一山分四季、十里不同天”的华南第一峰猫儿山；有“湘南第一洞乳洞岩；有集山水[视频]湖光之美的现代休闲世界元大桂林乐满地，兴安旅游已成为桂林大旅游圈中，中外旅客的神往之地.</p><p><img title="1505808036698793.jpg" alt="20131211101722341.jpg" src="/ueditor/php/upload/image/20170919/1505808036698793.jpg"/></p><p>公元前221年秦王嬴政灭六国，为巩固皇权, 他发兵五十万分五路大举进攻岭南的百越之地。因山路崎岖, 粮草不济而久攻不下。于是秦始皇命令史禄凿灵渠以通粮道。建成后的渠道以灵巧著称，因而得名。灵渠对维护国家统一、促进中原与岭南经济文化交流做出了重要贡献。桂林也因灵渠的开凿而成为“南连海域，北达中原”的岭南重镇。</p><p><img title="1505808040106878.jpg" alt="20131216100104509.jpg" src="/ueditor/php/upload/image/20170919/1505808040106878.jpg"/><br/>　　灵渠全长34公里，分为南渠和北渠，其中南渠长30公里，大部分利用的是天然河道，人工只开挖了5公里；北渠的长度虽然只有4公里，但它却是连通湘江和漓江的航道。</p><div>兴安灵渠是我国古代著名的水利工程之一，同时也是世界上最古老的人工运河之一。灵渠的陡门是现代船闸的前身，是世界上最早的通航设施。灵渠的伟大之处在于它沟通了漓江和湘江，使长江与珠江得以通航</div><p><br/></p>', 'dxy', 1, 0, '110', 0),
(105, '广西兴坪', '2017-09-19', '<p></p><p>兴坪古镇在桂林漓江边上，位于阳朔县东北部，距县城25公里。<br/>公元265年，阳朔地方分属尚安县，熙平县管辖，在漓江的中上游，熙平县治设在今兴坪镇狮子崴，公元590年，熙平县治由兴坪镇狮子崴迁到阳朔镇，此处仅作圩集，“熙平”经年深日久便渐讹传为“兴坪”。兴坪景区内奇峰叠翠，景景相接。有弧形直立的朝板山，像古代朝臣手中所持的朝板；如圆丘相叠的罗汉山，好似正在挺肚憨笑；远无望去，僧尼山如同一僧一尼正面对面斗嘴；溜圆扭卷的螺蛳山恰似一只巨大的螺蛳立于江畔……兴坪的水柔的像丝绒，绿的像碧茶，如一面镜子载浮着群山倒影，似诗似画。虚虚实实的山水景象融会成一曲大自然的和谐旋律。叶剑英元帅游漓江后也写下“春风漓水客舟轻，夹岸奇峰列送迎。马跃华山人睇镜，果然佳胜在兴坪”的诗句。漓江的景点精华大部分在兴坪境内。</p><p><img title="1505808100869887.jpg" alt="20140618105757451.jpg" src="/ueditor/php/upload/image/20170919/1505808100869887.jpg"/></p><p>兴坪集镇早在三国吴甘露元年（公元265年），即已为熙平县治，管辖阳朔一带，隋代开皇十年（公元590年）废熙平县改置阳朔县，兴坪遂为阳朔县所辖。境内不仅有九马画山、螺蛳山、莲花岩、天水寨、灵宝阁等八大景区、24个风景点让人叹为观止，还有明代建筑腾蛟庵1998年7月，时任美国总统克林顿浏览漓江并到兴坪渔村访问，更激起了不少中外游客慕名前往，追寻总统足迹。兴坪的奇山异水，先后留下了周恩来、邓小平、叶剑英、陈毅、杨尚昆、江泽民、李鹏、乔石等党和国家领导人的足迹。“马跃华山人睇镜，果然佳胜在兴坪”就是叶剑英元帅对兴坪古镇的高度概括。</p><p><img title="1505808129936801.jpg" alt="20140618105826968.jpg" src="/ueditor/php/upload/image/20170919/1505808129936801.jpg"/></p><p></p><p><img title="1505808105174063.jpg" alt="20140618105822157.jpg" src="/ueditor/php/upload/image/20170919/1505808105174063.jpg"/></p><p><br/></p>', 'dxy', 1, 1, '111', 1),
(106, '广西阳朔', '2017-09-19', '<p></p><p>“桂林山水甲天下，阳朔堪称甲桂林”，高度概括了阳朔的自然风光在世界上所占有的重要位置。“山青、水秀、峰奇、洞巧”，为天下四绝。? 阳朔百里山川，处处奇山秀水，自然景观与人文景观交相辉映，被誉为“天上人间旅游乡”。</p><p><img title="1505808187628048.jpg" alt="20120222011036548.jpg" src="/ueditor/php/upload/image/20170919/1505808187628048.jpg"/></p><p>20世纪80年代，西方一本被背包旅行者奉为“圣经”的《孤独行星》里面介绍了阳朔西街独特的风情。渐渐地，这里便成了国外背包旅行者的聚集地，一些迷恋西街风土人情的老外干脆在这里“安营扎寨”，开起了旅馆、饭馆。如今在地球村--西街，你能看到的外国人比中国人还要多。这条长仅600多米的小街，本是最传统的南国小镇街道，石板路面，灰砖骑楼；可它又是最洋气的：几乎所有的招牌都是英文。</p><div>阳朔县城旧八景：东岭朝霞，西山晚照，市井双月，黉宫万云， 寺僧钟，白沙渔火，马山岚气，龙洞仙泉。阳朔县城新八景：东岭朝霞，双峰锁江，古榕藏猫，碧莲叠 ， 寺僧钟，白沙渔火，南山古道，朔城佳境。<br/></div><p><br/></p>', 'dxy', 1, 0, '112', 0),
(107, '广西杨美', '2017-09-19', '<p></p><p>在广西的古镇中有“北有阳朔，南有杨美”之说。杨美古镇是一座保存得相当完整的古镇，位于左江下游，距南宁市约30多公里，水陆交通方便。古镇建于宋代，当时是方圆近百公里的商品集散地。明代著名的旅行家徐霞客曾从南宁乘船到过这里，盛赞杨美镇的沿江风光。</p><p>　　杨美古镇有八大景点，分别是魁星楼、清代一条街、明清古建筑群、梁烈亚（辛亥革命志士）故居等景点，每当夕阳西下的时候，江面波光粼粼，绿水翠竹映着古镇，使她显得格外优美。</p><p><img title="1505808223308365.jpg" alt="20140616060906916.jpg" src="/ueditor/php/upload/image/20170919/1505808223308365.jpg"/></p><div>杨美古镇旅游攻略：</div><div>　　火车：乘坐南宁——百色8565次列车（早8：30南宁站发车）前往，在扬美站下车；返程时乘同一班次列车（13：26扬美站发车）。单程车票2.5元。</div><div>　　公路：到华强路198号（二运汽车站斜对面）的“南宁——杨美”专线旅游车停靠点乘车，每天上午9：00、10：00、11：00准时发车，单程7元，车程约1.5小时。返回南宁的车很少，一般是下午14：00和16：00各有一班，事先要先问好发车时间。</div><div>　　水路：到南宁邕江码头乘观光船（出发时间早8：30，返程时间15：30）</div><div>　　自驾车：前往扬美的公路路况一般，有旅游牌提示，驾车或骑自行车都可以，但中途有轮渡，按车收费，自驾出行前最好先问好轮渡的时间。</div><div>　　门票：10元</div><div>　　住宿：扬美只有两个旅店，临江街的临江饭店和文生饭店，最低10元/人，最高不超过50元（节假日除外），但条件一般，床位也很少，如果是旅游旺季建议还是当天返回南宁市里。</div><div>　　餐饮：五叠堂酒店，名菜——左江鱼、扬桃排骨汤等。</div><div>　　购物：扬美三宝——梅菜、豆豉和沙糕。</div><div>　　到扬美游玩建议请一位当地的导游，20元，既节省时间，又不会错过景点；这里独有的“牛的”也是别开生面，饶有兴味，一定要体验一下。</div><div>在广西的古镇中有“北有阳朔，南有杨美”之说。杨美古镇是一座保存得相当完整的古镇，位于左江下游，距南宁市约30多公里，水陆交通方便。古镇建于宋代，当时是方圆近百公里的商品集散地。明代著名的旅行家徐霞客曾从南宁乘船到过这里，盛赞杨美镇的沿江风光。</div><div>　　杨美古镇有八大景点，分别是魁星楼、清代一条街、明清古建筑群、梁烈亚（辛亥革命志士）故居等景点，每当夕阳西下的时候，江面波光粼粼，绿水翠竹映着古镇，使她显得格外优美。</div><div>杨美古镇旅游攻略：</div><div>　　火车：乘坐南宁——百色8565次列车（早8：30南宁站发车）前往，在扬美站下车；返程时乘同一班次列车（13：26扬美站发车）。单程车票2.5元。</div><div>　　公路：到华强路198号（二运汽车站斜对面）的“南宁——杨美”专线旅游车停靠点乘车，每天上午9：00、10：00、11：00准时发车，单程7元，车程约1.5小时。返回南宁的车很少，一般是下午14：00和16：00各有一班，事先要先问好发车时间。</div><div>　　水路：到南宁邕江码头乘观光船（出发时间早8：30，返程时间15：30）</div><p>　　自驾车：前往扬美的公路路况一般，有旅游牌提示，驾车或骑自行车都可以，但中途有轮渡，按车收费，自驾出行前最好先问好轮渡的时间。</p><p><img title="1505808230103190.jpg" alt="20140616060907570.jpg" src="/ueditor/php/upload/image/20170919/1505808230103190.jpg"/></p><div>　　门票：10元</div><div>　　住宿：扬美只有两个旅店，临江街的临江饭店和文生饭店，最低10元/人，最高不超过50元（节假日除外），但条件一般，床位也很少，如果是旅游旺季建议还是当天返回南宁市里。</div><div>　　餐饮：五叠堂酒店，名菜——左江鱼、扬桃排骨汤等。</div><div>　　购物：扬美三宝——梅菜、豆豉和沙糕。</div><p>　　到扬美游玩建议请一位当地的导游，20元，既节省时间，又不会错过景点；这里独有的“牛的”也是别开生面，饶有兴味，一定要体验一下。</p><p><img title="1505808234134439.jpg" alt="20140616060910717.jpg" src="/ueditor/php/upload/image/20170919/1505808234134439.jpg"/></p><p><br/></p>', 'dxy', 1, 0, '113', 0),
(108, '贵州隆里', '2017-09-19', '<p></p><p>明清江南建筑风格的隆里古城位于中国南方高原地带贵州东南苗族和侗族聚居的大山深处。这里遍布着错综复杂的街道古巷，随处可见保存完好的明清古建筑。隆里的历史已有六百多年，这里群山环抱，自古就是军事堡垒，为历代兵家必争之地，古城始建于明朝洪武十八年，当时朱元璋在此地设立千户所，建立了一座颇具规模的军事基地，如今古城保存完好，古城的居民大多是明代就在此屯兵的军户后裔，他们一代代也是古城的守护者和记述者。</p><p><img title="1505808286986503.jpg" alt="20151009042710761.jpg" src="/ueditor/php/upload/image/20170919/1505808286986503.jpg"/></p><p>古城内现存典型民居宗祠40余幢，建筑宏伟各异，木质结构风火墙，后院设有后门，户户相连，城内街道明通暗塞，暗通明阻，虚实结合。隆里古城有72姓，72口水井。</p><div>隆里古城的道路、关隘、城墙、瞭望楼等均为古代军事布局，如今古城的四座城门依然有迹可循，四座城门即东方财门、西方水门、南方喜门和北方鬼门。四门都沧桑古朴，历史古韵浓郁。</div><div>此外陶家大院位于古城的东北面，它建造于清朝嘉庆年间，是隆里古城中保存最完好的古民居建筑。古城每年正月十五开始的花脸龙庆春活动很有特色，如果你春节后来到古城玩可别错过这一活动，舞龙的人要&quot;开花脸&quot;,扛龙尾的人打扮很滑稽，一边舞龙一边吧街上的稀泥或者糍粑裹着地上的脏水炒四周的观看者身上抹，观者笑着叫着躲避，却又因为被抹到会有一年的好运而欲迎还拒。</div><p><br/></p>', 'dxy', 1, 0, '114', 0),
(109, '贵州青岩', '2017-09-19', '<p></p><p>青岩古镇历史悠久，自明洪武十一年设青岩屯至今已有六百多年历史了。古镇位于贵州省贵阳市的南郊，距市区约29公里。是贵州省历史文化古镇，青岩的文物点多达百处。在那亘古不变的狮子山下，古镇胜迹随处可见。交错密布于镇内的明清古建筑，计有九寺、八庙、五阁、三洞、二祠、一宫、一院等共37处，其中8座石碑坊现存3坊。这些古建筑都是设计精巧、工艺精湛的佳作，如慈云寺的石雕、寿佛寺的木雕艺术均为贵阳市仅见的精品。青岩镇的镇容布局沿袭明、清格局，至今仍存完好的朝门、腰门以及陈旧古老的石柜台和木柜台，总给人以悠悠古韵的美感。新建的古镇商业街更使古镇遗风韵味得以弘扬。</p><p><img title="1505808334597797.jpg" alt="20121010061421123.jpg" src="/ueditor/php/upload/image/20170919/1505808334597797.jpg"/></p><p><br/></p><p><img title="1505808344709249.jpg" alt="20121010061424343.jpg" src="/ueditor/php/upload/image/20170919/1505808344709249.jpg"/></p>', 'dxy', 1, 0, '115', 0),
(110, '贵州云山屯', '2017-09-19', '<p></p><p>一个屯堡村寨,因保存着较为完好的古屯堡、民代一条街,以及屯堡文化、民清建筑艺术和屯堡宗教文化被誉为一座“现存屯堡文化地面博物馆”,位于贵州省西部的安顺市东面七眼桥镇的云鹫山峡谷中。</p><div>·<img title="1505808406108612.jpg" alt="20140610111418715.jpg" src="/ueditor/php/upload/image/20170919/1505808406108612.jpg"/>以贵阳市为起点,在火车站、体育馆及客车站均可乘坐到安顺市的汽车,票价15元,需两小时。到安顺后在体育馆车站乘坐至七眼桥镇的中巴车行程20公里,票价2元左右,七眼桥镇有拖拉机到云鹫山前的一个寨子,票价1元左右,至此可步行到云山屯。</div><div>云山屯是自然村落不需门票,上云鹫山进寺庙门票1元。在寺庙住宿厢房每人每晚10元,中餐或晚餐每人每餐10元,早餐5元。</div><div>屯堡的建筑大多采用的是以四合院为主的建筑风格。屯堡的四合院自成一体，融合了江南四合院的特点和华东四合院的布局。其中最突出的特点是全封闭。居民建筑分朝门、正房、厢房。朝门成雄伟大“八”字形特点。</div><div><img title="1505808410134095.jpg" alt="20151014065051326.jpg" src="/ueditor/php/upload/image/20170919/1505808410134095.jpg"/>云山屯晃悠了一遍,它的忙碌,它的恬静,它现在的样子已经粗粗印在胶片上,可是在这恬静的乡村照片后弥漫的东西让我们一遍又一遍地从屯前逛到屯尾,从艳阳的午后逛到夕阳西下的黄昏。一位土生土长的老人,搬出几张小椅子,喝着茶,抽着旱烟跟我们慢慢儿摆起了龙门阵:云山屯有一千多年了,我们看到的那一间挨着一间的老屋明代就有,有的原来是药铺,有的是戏楼。原来屯后只有一道屯墙,后来人多了住不下就向后扩,所以又修了一道墙,屯里最繁华的时候有一千多户人家,后来有不少人家到外面做生意,赚到了钱就搬出去,也有的家小孩考起学校在外面工作,老人也跟着出去了,现在屯里还有80来户人家。云山屯穿长衣服的人少,因为是客家,也不跳地戏,只是跳花灯,土改的时候云山屯的人都积极参加,跳花灯到处去宣传,他自己是演老太太的所以绰号叫“老太”。现在有电视的人家多了,平日里晚上人们都看电视,只是到过年过节才跳花灯。<br/></div><p><img title="1505808415114326.jpg" alt="20151014065052627.jpg" src="/ueditor/php/upload/image/20170919/1505808415114326.jpg"/></p>', 'dxy', 1, 0, '116', 0),
(111, '贵州肇兴', '2017-09-19', '<p></p><p>肇兴位于贵州省黔东南苗族侗族自治州黎平县东南部，海拔410米，向西68公里到黎平县城，向北52公里到从江县城，向东102公里到广西壮族自治区三江侗族自治县，是黔东南侗族地区最大的侗族村寨之一。占地18万平方米，有800多户人家，居民4000多人。寨中房屋为干栏式吊脚楼，一条东西走向的主要街道两旁建有学校、乡政府和民居，全是青瓦木楼。就侗族西部而言，肇兴侗寨是赫赫有名的，有“千家肇洞”和“侗乡第一寨 ”之美誉。</p><p><img title="1505808453101111.jpg" alt="20131128101335840.jpg" src="/ueditor/php/upload/image/20170919/1505808453101111.jpg"/></p><div>肇兴以鼓楼群最为著名，其鼓楼在全国侗寨中绝无仅有，被誉为&quot;鼓楼之乡&quot;。寨中五团，共建有鼓楼五座，花桥五座、戏台五座。五座鼓楼的外观、高低、大小、风格各异，蔚为大观。</div><p>黎平县地区以山地为主，肇兴侗寨则处于一狭长谷地，侗族建筑密集，形成罕见的布局风格。当地耕种的梯田多沿山势分布，常为云雾缭绕。到了春天，油菜花开时节，景色十分诱人。</p><p><img title="1505808460417881.jpg" alt="20151008112859549.jpg" src="/ueditor/php/upload/image/20170919/1505808460417881.jpg"/></p><p><br/></p>', 'dxy', 1, 0, '117', 0),
(112, '重庆丰都', '2017-09-19', '<p></p><p>重庆 &quot;鬼城&quot;丰都古为&quot;巴子别都&quot;,东汉和帝永元二年置县，距今已有近2000年的历史,素以&quot;鬼国京都&quot;、&quot;阴曹地府&quot;闻名于世，据魏晋时《度人往》记载，丰都坐落在六天青河旁，有三宫九府，宫阙楼观贵似天庭，鬼帝坐镇在此，统亿万鬼神。是传说中人类亡灵的归宿之地，集儒、佛、道民间文化于一体的民俗文化艺术宝库，被誉为&quot;中国神曲之乡&quot;、&quot; 人类灵魂之都&quot;。</p><p><img title="1505808502605887.jpg" alt="20140918114101351.jpg" src="/ueditor/php/upload/image/20170919/1505808502605887.jpg"/><br/> &quot;鬼国京都&quot;--名山风景区，位于重庆下游北岸172公里岸边，水陆交通便利，古称&quot;平都山&quot;、&quot;丰都山&quot;，系道教七十二洞天福地之一，因苏东坡畅游丰都所题&quot;平都天下古名山&quot;而改称&quot;名山&quot;。名山风景区主要由全国著名风景名胜古迹点名山、全国最大动态人文景观鬼国神宫和国家级森林公园双桂山组成。先后被评为国家首批4A级旅游区、全国首批15家保护消费者合法权益示范单位、中国企业诚信经营示范单位、国家建设部全国景区联合会副会长单位、中国风景名胜区协会理事会员单位、中国文联全国书画创作基地、中国作协丰都名山创作基地、重庆市旅游协会副会长单位、重庆市最佳景区、重庆市市级生态环境保护示范景区。获得了ISO9000国际质量认证和ISO14000国际环境管理体系认证。</p><p><img title="1505808508413019.jpg" alt="20140918114105660.jpg" src="/ueditor/php/upload/image/20170919/1505808508413019.jpg"/><br/> 名山风景区自对外开放以来，共接待海内外游客1000余万人次，历代党和国家领导人光临名山风景区；国外知名人士美国前国务卿基辛格博士、舒尔茨，日本前首相海部俊树、中曾根康弘、村山富士，澳大利亚总督海登等莅临景区观光游览。</p><p>丰都自助游攻略：<br/>从朝天门坐车出发。经过长寿—涪陵—丰都。差不多三个多小时就到了，重庆到涪陵这一段为全高速路，涪陵到丰都为二级公路，路费差不多是60块钱左右吧，你还可以直接坐船到丰都，在船上还可以悠闲的赏赏一路的江景，到达丰都后，那就看你们怎么玩了，首先肯定要去玩的就是鬼城名山了噻，那里面的好玩的东西可多了，门票嘛，不是很贵，80块钱，保证你玩得很满意的。到了晚上嘛，去新县城那边住，那边的宾馆一晚上也不贵，那个算得上是星级的宏声宾馆一晚也就170左右，如果你觉得高了，也有便宜的，一晚50块钱，电视、浴室，空调也都有的，保证你住得安心，吃得放心。</p><p><img title="1505808513739974.jpg" alt="20140918114108470.jpg" src="/ueditor/php/upload/image/20170919/1505808513739974.jpg"/><br/>到了第二天嘛，你就去丰都新县城转一圈，现在这新修的新县城呀，差不多跟重庆的主城区一样吧，看看这个三峡移民库区的人民们把他们的家乡建设得这么的漂亮。不难感叹三峡移民真伟大啊！转玩后，你还不得不去一个地方哦，那就是著名的雪玉洞啦，那个地方呀，简直就是人间的天堂，大自然巧夺天工的雕塑呀。最后祝各位到丰都玩得高兴。<br/>丰都鬼城交通指南：<br/>路线一:重庆朝天门码头到丰都码头轮船随时都有(请咨询朝天门售票大厅),六个小时到达丰都 ,停靠丰都老县城,下船即是景区。</p><div>路线二:重庆朝天门长途汽车站每一个小时有发往丰都的班车,票价五十三元.(含保险费)下车为丰都新县城,转乘市内交通(长安小面包车)票价三元到景区。<br/></div><p><br/></p>', 'dxy', 1, 3, '118', 0),
(113, '重庆龚滩', '2017-09-19', '<p></p><p>龚滩古镇位于国道319线旁，是黔江民族风情旅游区的景点之一。龚滩镇地处重庆市酉阳县、彭水县和贵州省沿河县的结合部，乌江、阿蓬江的交汇处，酉阳县城至彭水县城公路穿越龚滩中心集镇，龚滩码头年货物吞吐量在500万吨以上，水陆交通便利，自古以来即是川（渝）、黔、湘、鄂客货中转站，素有“钱龚滩”之美誉。全镇幅员面积龚滩古镇133平方公里，辖8个行政村，57个组，2.1万余人，有耕地面积2.6万余亩，森林面积7.8万余亩。 自重庆沿乌江航道可直达龚滩，彭水至龚滩的公路路面平整，酉阳至龚滩公路改造基本完成。重庆、涪陵、彭水有直达龚滩的快艇，黔江、酉阳已开通到龚滩的旅游专线。景区内交通以乌江航运为主，现有电瓶飞船、舢板船、机动船等运输工具，还有人工抬轿、骑自行车、骑牛等特色交通。</p><p><img title="1505808614169114.jpg" alt="20140914054748569.jpg" src="/ueditor/php/upload/image/20170919/1505808614169114.jpg"/></p><p>龚滩古镇特色：<br/></p><p>古镇现存长约三公里的石板街、150余堵别具一格的封火墙、200多个古朴幽静的四合院、50多座形态各异的吊脚楼、独具地方特色，是国内保存完好且颇具规模的明清建筑群。专家学者考察后认为，龚滩古镇可与世界文化遗产丽江古镇媲美。 　　</p><div>龚滩古镇青石板街青幽如玉，串连全镇，街边是古香古色、临崖高挑的木制吊楼；是访古、猎奇、探险的理想景区。乌江干流龚滩至万木段，全长60余公里，包括龚滩、清泉两个古 龚滩古镇及乌江一级支流阿蓬江龚滩至大河口段，堪称乌江画廊之精华。 　　</div><p>龚滩古镇三教寺是庙非庙——没有一座神像，没有一根香烛，神殿早已经成了民居。相传三寺庙曾经是明末清初著名的巾帼英雄秦良玉的故居。崇祯三年(公元1636年)，秦良玉奉旨率精兵北上收复了华北永平(今唐山市)四城。崇祯皇帝在平台召见秦良玉和五百四川僧兵。这些僧兵婉言谢绝了皇上的赐官封赏，一致要求求皇上恩准和尚可以娶妻为室，可以开戒吃荤。崇祯皇帝慷慨应允。勤王有功的僧兵回到石柱、酉阳等地之后，照常当和尚，但从此可以娶妻，可以吃肉。他们既信奉佛教，也信奉道教,同时对于儒家的学说也非常尊崇。因此，他们所在的寺庙，就称为三教寺。</p><p><img title="1505808621371063.jpg" alt="20140914054748990.jpg" src="/ueditor/php/upload/image/20170919/1505808621371063.jpg"/></p><p><br/></p>', 'dxy', 1, 0, '119', 0),
(114, '重庆灌水', '2017-09-19', '<p></p><p>濯水古镇自古便是重要的驿站和商埠，与酉阳龙潭、龚滩合称“酉阳三大名镇”。古镇位于黔江东南角，距黔江主城18公里，作为重庆旧城老街的典型，濯水古镇除了拥有较多历史遗址以及相关的历史典故，同时也在四千年的漫漫历史长河中积淀了丰富的文化内涵。街巷格局保留较为完整，具有浓郁的渝东南古镇格局，码头文化、商贾文化、场镇文化以及丰富多彩的文化艺术遗存相互交织。</p><p><img title="1505808690110045.jpg" alt="20150202030750634.jpg" src="/ueditor/php/upload/image/20170919/1505808690110045.jpg"/></p><p><br/></p>', 'dxy', 1, 0, '120', 0),
(115, '重庆西坨', '2017-09-19', '<p></p><p>位于重庆市石柱土家族自治县的西沱古镇已有一千九百多年的历史，古镇内的“云梯街”，以独持的风貌，幸存于长江三峡库区175米水位以上。“秦砖汉瓦宋元楼，千载临风赋江流”，古镇现存有许多明清时期古建筑和土家古民居，西沱是长江文化、巴蜀文化、荆楚文化和土家族历史文化的交汇地，西沱也是全国第一批历史文化名镇，为2002年全国十大历史古镇之一。从地理位置上看，西沱处长江黄金水道岸边，斜对岸不足10里，即为闻名海内外的忠县石宝古寨，向南翻过武陵山即为黄水森林公园和千叶草场，往东即是川东门万州，沿长江而上，则有举世闻名的丰都鬼城，往西南则是誉满天下的“乌江画廊”，往东南，则是恩施利川土家族、苗族风情浓郁之地，地理位置之优越，真是上天的多重眷顾。</p><div><img title="1505808741278458.jpg" alt="20140913120426870.jpg" src="/ueditor/php/upload/image/20170919/1505808741278458.jpg"/>从重庆出发，经渝涪高速到涪陵再从长江北岸经丰都到忠县，过忠县长江大桥到沿溪镇，不远即为西沱古镇。云梯街两旁的土家民居，极富巴渝特色。一梯一底木板房，临街建成街铺，屋后建成吊脚楼，灰瓦白墙，被建筑学家称之为“长江沿岸最古老的奇特建筑明珠”。古镇现存众多文物古迹和明清时期古建筑：有明代富丽堂皇的禹王宫、大寺、八角庙、武庙、三楚堂；万寿宫、文昌宫、桂花园等遗址；清代建筑精美的“下盐店”遗构历历在目。</div><div><img title="1505808746469433.jpg" alt="20140913120435929.jpg" src="/ueditor/php/upload/image/20170919/1505808746469433.jpg"/>明代独具匠心的“龙眼桥”点缀着古镇风光。祭祀孔子、关公的“二圣宫”后殿，“余音绕梁”、“副魁”木匾保存至今。更有记录长江水文变化的水文石刻资料：陈家河长江峭壁上刻着“庚午年大水至此”7字清晰可读，对岸岩石上深刻的“大清同治庚午大河水涨”12字历历在目。民国时期的“永成”、“和成”商号遗址，是中共地下党活动的地方，已成为革命历史文物。</div><div>西沱也是一个历史文化底蕴极其丰厚之地。西沱古称“西界沱”，早在宋代即被称为盐镇，古人称之为“千里盐道”，至今街上很多商铺还有石制的贩盐台。目前，云梯街上有依山而凿的石梯1200多梯，传说为镇龙而修，也称镇龙街，总长约500米，相对海拔高度约160米，颇为壮观。西沱隶属石柱县，有重庆民歌代表之称的“太阳出来喜洋洋”即发源于此，重庆首批非物质文化遗产之“啰啰调”就是此歌的前身。那一韵三叹的调子与巴渝民众的豪迈通过歌词与曲调的完美结合而得以表达的酣畅淋漓，不能不使人感慨。而最具特色的，还是西沱人独有的“玩牛”演出。每逢节日期间，当地玩牛手便做牛头面具，由人牵着，串街演唱：“这个牛儿有点怪，长着两脚惹人爱，这个牛儿有点好，长着两脚不吃草，不吃草来惹人爱，快点快点把客人拜，一拜客人身体好、长命百岁都不坏，二拜客人……”直逗的被拜之人与观众都皆大欢喜，所有烦恼之情，都抛九霄云外。</div><div>西沱也是一个美食古镇。地处长江边上，长江鱼就自不必说了。这里的酸醡肉、酸醡鱼独具特色。在秋天，西沱人将鲜肉、干南瓜皮、红苕、海椒等拌上炒好的稻米面或包谷面，加盐和姜、花椒、橙皮等佐料后入坛腌制而成，待冬至后才取出蒸熟食用。经过一个季节的腌制发酵，蒸熟后不仅爽口化渣，且油而不腻，极为开胃。另外，当地人用小麦或高粱而制的西沱砸酒也清香扑鼻，用砸酒来配当地的九大碗办席是这里亘古不边的风俗习惯。</div><p><br/></p>', 'dxy', 1, 1, '121', 0),
(116, '重庆中山', '2017-09-19', '<p></p><p>中山古镇俗称三合场，又名龙洞场，地处重庆市下属江津市南部山区，地处川、渝、黔三省交界处，古镇背山临水，场镇建筑靠水而建，由龙洞、荒中坝、高升桥三条小街连接而成。以前，三合场曾是繁华的水码头，为川黔山区的商品集散地，为国家级风景名胜区四面山的北大门，是重庆十大古镇之一。</p><p><img title="1505808792133338.jpg" alt="20140916125141368.jpg" src="/ueditor/php/upload/image/20170919/1505808792133338.jpg"/></p><p>中山古镇历史悠久，早在四千多年前就有人类居住，南宋《清溪龙洞题名》碑刻记载三合场有可考历史为853年。清康熙33年，这里就设有办事机构，即笋里十二都，有无数现存的岩墓（又称蛮子洞）按史学考证为汉墓，更证明其历史之亘古。“三合场”是由原老场、龙洞场、马桑垭三个场合并而成，解放初期建为三合人民公社，1982年改建中山人民公社，1993年与常乐乡合建中山镇，2001年又与嘉乐乡合建为现在的中山镇，辖11个行政村和一个社区委员会。</p><p><img title="1505808809621895.jpg" alt="20140916125146402.jpg" src="/ueditor/php/upload/image/20170919/1505808809621895.jpg"/></p><p>目前尚存的南宋题刻共82字，记叙李脊用、鱼子仙等泛舟游览一事，境内还有汉代古墓葬枇杷岩墓群。</p><p><img title="1505808814668713.jpg" alt="20140916125150209.jpg" src="/ueditor/php/upload/image/20170919/1505808814668713.jpg"/></p><p><br/></p>', 'dxy', 1, 0, '122', 0),
(118, '山东 东楮岛村', '2017-09-19', '<p></p><p>海草房房盖上那松软的草质，调和了坚硬的石头，又令房顶略具缓缓的弧线身段。她默默地用坚强的臂膀，为她的子民们遮挡风雨，日复一日、年复一年，苍老了容颜。东楮岛村始建于明朝万历年间，位于宁津街道办事处最东端，石岛湾省级旅游度假区的北侧，距石岛港20公里，是一个三面环海的小渔村。以保存有大量海草房而闻名遐迩，这里有着优美的自然环境、浓郁的地方民俗特色文化、众多的民间传说、丰富的海产资源、凝重的饮食文化。</p><p><br/></p><p>整个古村落呈荷花形地势东高西低。全村住房布局大体分成两部分。村南部为新建的红瓦房和楼房的住宅群，木批部为旧有的海草房住宅群，是东楮岛村村民祖辈居住的特色民居。据统计，目前，全村现有海草房4户，共有海草房6孔间，其中，最古老的海草房据传始建年代大约是在清顺治年间，距今已有3W多年历史，百年历史以上的海草房有83户412间，主要分布在村中部分东楮岛村北东南三面环海，西面一路通陆，环境优美，气候宜人，传统名居海草房用大块石头砌成粗犷的墙，石头随方就圆，墙面纹样规则中还显灵活，寓朴于美。三角形大山墙，方形院落。房顶外覆一层厚厚的海草。屋顶大都用一排瓦或水泥压脊，用于抵御大风。这种房子具有“冬暖夏凉、居住舒适、百年不腐”等特点；因成排连片而较好地保留了历史的“原汁原味”，是全国乃至世界难得的生态民居活标本之一。沉淀着浓厚的历史文化、蕴含着丰富的地域特色、承载着淳朴的民俗风情，体现着卓越的古建筑艺术，是国内外不可多得的宝贵资源。海草房顶苫草有的厚达一米以上，脊部两端高于中央，并向山面做切角处理，房脊形成明显的曲线，屋脊浑厚圆润。海草房坚固耐用，所用材料为海苔草，为野生藻类，生长在5·0米海域，春荣秋枯。海草中含有大量的卤和胶质，成：熟的海草被海浪卷上海滩后，人们将其拉上岸晒干苫盖房屋，可以持久耐腐、防漏吸潮，用它盖成的海草房就是世界上独有特色的生态民居了。</p><p><br/></p><p>古老的村落内静悄悄的，走在斑驳的青石小巷，仿佛行走在岁月的长廊里，一排排海草房沉默地矗立着；仿佛饱经沧桑的老人，淡然地看着来来往往的日子和旅人。岁月在这些海草房身上留下了不可磨灭的印记，抚摸着青石墙的拴马石，让人不禁回想起那一段流逝的岁月，注视着山墙上的黑板报和标语，仿佛可以进入那一个狂热的时代。这些历史的痕迹，为后人提供了复原那些遥远岁月的线索，经历了那么多的风风雨雨，海草房就是一部岁月漫漶不了的渔村记忆。</p><p><br/></p><p>东楮岛村还有75公里长的海岸线，5公里长天然优质沙滩，是天然的海水浴场；有…亩的天然赶海滩涂，盛产海参、螃蟹、扇贝、牡蛎等海产品。该村的海岸、沙滩、阳光和岛屿完美海洋组合景观，游客可以在东楮住一天海草房、吃一天渔家饭睡一天农家炕、当一天渔家人 看天民俗情，感受一下海草房质朴古拙的渔家气息，体验一下生活在原生态渔村的感觉。</p><p><br/></p><p>小提示：</p><p><br/></p><p>东楮岛村的杨家葬海礁离岸15公里，海礁高133米。从正面看似笔架摆于海上。西南方向观察像一艘军舰停泊海上。从东北方向看似一顶花轿在海面走动每逢初春大雾刚消时刻，常出现海市蜃楼的景象，有时像动物，有时像植物，有时又像亭台楼阁等，奇形异景，变化无常。</p><p><br/></p><p>旅游TIPS：</p><p><br/></p><p>地理位置：山东省威海市荣成市宁津街道办事处东楮岛村。 从荣成到宁津乘车6元，然后再包车前往东楮岛村。</p><p><br/></p><p>宁津街道办事处电话：0631-7341001</p><p><br/></p>', 'add', 1, 0, '124', 0),
(119, '湖南 凤凰古镇', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505829236879053.jpg" title="1505829236879053.jpg" alt="43188554_4.jpg"/><img src="/ueditor/php/upload/image/20170919/1505829236923720.jpg" title="1505829236923720.jpg" alt="43188554_3.jpg"/></p><p>提起凤凰，人们首先想到的是沈从文故居，很少人知道，在陕西的柞水，也有着一个美丽的凤凰古镇。</p><p><br/></p><p>秦岭南坡，凤凰镇依山傍水，社川河、皂河、水滴沟河三水交汇于此，形成三角洲，凤凰镇便依山傍水建在这肥沃的三角洲上。镇政府所在地的凤镇街距柞水县城45公里。据《柞水县志》记载，凤凰镇始建于唐武德七年(公元624年)，唐宋时名“三岔河口”(社川河、皂河、水滴沟在此交汇)，元代称“社川河乡都”，明成化十五年后(公元1479年)称“社川里”、“上孟里”，清嘉庆年间改名为“凤凰嘴”(因其西南有山名凤凰山而得名)，民国三十年(公元1941年)更名为“凤凰镇”。明景泰三年(公元1452年)至1961年属陕西省镇安县，同年9月划归柞水县。</p><p><br/></p><p>凤凰镇老街弯曲的街道和两边遍布的明清老房是这里的最大特点。在这条东西一千多米长的古镇上至今仍完好保留着60多座明清时期的民居。沿主街有一条石板覆盖的小溪，另有一条与之垂直的小溪穿镇而过，形成十字形水系。老街两旁靠街全是商铺，商铺后面是住宅。临街大门一般用结实耐磨的核桃木或漆木板做成，用土漆油染，明净光亮；门墩为石雕花卉。商铺铺面宽不盈丈，宅深却达三四十米，据说铺面的间数和房屋的纵深度都代表了当时主人的富裕程度。</p><p><br/></p><p>特色美食：当地的特色农家饭，锅盔，米汤、腊肉、豆干、土鸡蛋等等，腊肉和豆干还有豆豉是当地的特产，还有苞谷酒。</p><p><br/></p><p>自驾线路：西安到柞水下高速，到古镇大约43公里</p><p><br/></p><p>乘车路线：火车站乘坐到凤凰镇的旅游大巴</p><p><br/></p>', 'add', 1, 0, '125', 0),
(120, '陕西 后柳古镇', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505829307119636.jpg" title="1505829307119636.jpg" alt="43188554_36.jpg"/><img src="/ueditor/php/upload/image/20170919/1505829307741566.jpg" title="1505829307741566.jpg" alt="43188554_35.jpg"/></p><p>---秦巴胜地，汉水之滨</p><p><br/></p><p>后柳古镇位于石泉县城以南23公里的莲花湖畔，其地势三面环水,呈半岛状。后柳镇古朴厚重，历史久远。地处后柳下街的八亩田，经省考古学家考古发掘，认定为新石器时代遗址，该地曾在上世纪六七十年代出土了大量的石斧、石器，可见早在新石器时代便有先民在这里繁衍生息，创造了灿烂的古代文明。</p><p><br/></p><p>乘车路线：城南客运站直接到石泉的大巴，到达后转当地旅游班车至小镇。</p><p><br/></p><p>自驾路线：线路一：西汉高速：西安--大河坝--两河镇（210国道11公里）--石泉县---后柳镇</p><p><br/></p><p>线路二：西康高速：西安--五里（西康高速195公里）--马领关隧道（316国道77公里）--莲花古渡/汉江三峡（省级公路1公里）--桂苑（省级公路24公里）---后柳镇</p><p><br/></p>', 'add', 1, 0, '126', 0),
(121, '山西 曹家大院', '2017-09-19', '<p></p><p>交通：</p><p>在太谷火车站前乘坐开往祁县的县际班车．到距离曹家大院最近的北洸村口下车，步行约1000米，15分钟便到，沿途有路标，或者在太谷县城南关向南800米左右的聚贤楼乡村公交站乘坐开往果树研究所的中巴，在北洸村下，步行5分钟可达：上述两条中巴线路车次都不算多，有时候需要等待30分钟，因此性价比最高的选择是从太谷城内包车前往，20分钟即可到达。</p><p>如果从祁县方向过来，可在祁县火车站前乘坐县际班车：同样需要步行15分钟。比较明智的做法是乘坐开往东观的中巴，到东观路口下车，然后包租停在此地揽活的小面或夏利，直达曹家大院：</p><p><br/></p><p>游览时间：</p><p>一到一个半小时。</p><p><br/></p><p>下一站：</p><p>乔家大院：两地相距10公里。有三轮摩托在曹家大院的停车场揽客，20多分钟可到乔家大院门口；或者步行约一公里回到108国道上，乘坐过路班车到东观下车，再乘坐三轮车或包车前往东观路口以北2公里的乔家大院。</p><p><br/></p><p>常家庄园：步行一公里到108国道上，乘坐去榆次的过路车，在东阳下车，到马路对面乘坐从榆次开往常家庄园的公交车，终点站即为常家庄园门前的停车场。</p><p><br/></p><p>手记：</p><p><br/></p><p>1、三多堂由于交通不太方便，旅游团队不多，游客稀少，五一、十一长假期间是一年中仅有的两个高峰时段，但在几个晋中大院中仍属于游览环境最宽松的。</p><p><br/></p><p>2、从中路统楼可以登上三多堂的屋顶，俯瞰现存的曹家大院全貌。从大院西边的小门出去，有一片空地，是拍摄三座统楼全景的好地方；</p><p><br/></p><p>3、中路的统楼内有太谷建筑文物图片，不必东奔西走，在此可以一览太谷主要古建筑的风采。</p><p><br/></p><p>4、这里有“山西古典家具博物馆”的牌子，是著名学者、收藏家、文物鉴赏家王世襄的题字，能入了他的法眼，这里的古典家具看来是不错的。</p><p><br/></p>', 'add', 1, 0, '127', 0),
(122, '陕西 米脂县', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505829415666605.jpg" title="1505829415666605.jpg" alt="43188554_25.jpg"/><img src="/ueditor/php/upload/image/20170919/1505829415101090.jpg" title="1505829415101090.jpg" alt="43188554_24.jpg"/></p><p>----貂蝉的故乡</p><p><br/></p><p>米脂古称“银州”，因当地盛产小米，“米汁淅之如脂”而得名。米脂的米不光养育了西夏王朝奠基人李继迁、明末清初农民领袖闯王李自成这样英武盖世的男儿和貂婵这样的倾国倾城的绝世美人，同时也养育了无数革命人士如郭洪涛、马明方、常黎夫和民主人士杜斌丞、李鼎铭等。陕北有句俗语：“米脂婆姨绥德汉”，谁能说清米脂婆姨的俊美聪明，敢爱敢恨与小米没有关系﹖</p><p><br/></p><p>旅游小贴士：</p><p><br/></p><p>米脂气候属中温带干旱气候。主要的气温特点是日夜温差大，春季多风沙，夏季多雨，适合秋季前往，旅行时一定要备御寒衣服和雨具。</p><p><br/></p><p>特色美食：蒸丸子：又名“洋芋擦擦”，米脂传统米酒饭食之一。</p><p><br/></p><p>米酒：色泽黄亮，酸甜适口，口味绵醇，可当饮料。</p><p><br/></p><p>糜子油糕：香甜可口</p><p><br/></p><p>乘车路线：到米脂只能坐汽车，从榆林长途汽车站乘汽车到米脂约需1个多小时，西安到米脂车程约需8小时，延安到米脂车程约需4小时。</p><p><br/></p>', 'add', 1, 0, '128', 0),
(123, '山东 南阳古镇', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505829463107850.jpg" title="1505829463107850.jpg" alt="20120209032010291.jpg"/></p><p>南阳古镇位于微山湖北部的南阳湖中，南阳镇是微山湖中、运河线上最有特色的镇子，距济宁市区约40公里。古镇北依济宁城区，紧靠山东三大都市带，南面是江苏的苏北地带，东面是国际旅游目的地孔孟故里曲阜、邹城，西面是牡丹之乡菏泽，古老的京杭大运河穿镇而过。南阳镇离京福高速三十公里，距济宁机场六十公里，区位优势明显。</p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p>南阳在古代村镇依运河而建，古镇溯源从元朝至顺二年（1331年），在这里建起南阳闸。明隆庆元年（1561年）漕运新渠竣工，南阳成了商埠。其后明清两代，这地方“鱼船、酒船、商船，往来相接，群聚檐檣林立如街市”。北来运大豆、羊皮、煤炭的船只，南来载糖、纸、竹器、丝绸、煤油的船只，都在这里停泊，一时号称“小苏州”。在繁盛时，南阳镇曾有皇宫所、皇粮殿、二爷庙、火神庙、魁星楼、文公祠、禹庙、杨家牌坊、不沾地旗杆等30多处名胜古迹。清政府曾在此设守备及管河主簿，皇帝下江南也曾慕名在镇上逗留。</p><p><br/></p><p>如今南阳镇所统辖的80多个村庄星散在15万亩湖面上，或以莲荷相接，或以苇田相连，或以明水相通，出镇下村，必定要驾小船。运河穿镇而过，河湖串联，水路交错。这种“岛在水中、河在岛上、镇在湖内”的特殊景观在整个运河从南到北独树一帜。</p><p><br/></p>', 'add', 1, 0, '129', 0),
(124, '陕西 青木川古镇', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505829526431178.jpg" title="1505829526431178.jpg" alt="43188554_12.jpg"/><img src="/ueditor/php/upload/image/20170919/1505829526105961.jpg" title="1505829526105961.jpg" alt="43188554_11.jpg"/></p><p>媲美周庄的人间仙镜</p><p><br/></p><p>青木川古镇位于汉中市宁强县西北角，地处陕、甘、川三省交界，有“一脚踏三省”之誉。据考证，青木川发轫于明中叶，成型于清中后期，鼎盛于民国，是羌汉杂居地区，曾先后称“草场坝”“回龙场”“永宁里”“青木川”。青木川曾是入川的要道之一，历经岁月沧桑，现有大量保存完好、风格迥异的古街、古祠、古栈道、古建筑等历史遗迹。一条“回龙场”古街从南向北把小镇拉得悠长，两边的明清民居错落有致地连缀在一起，雕刻精美的窗棂、门楣、瓦当风格典雅，古朴的青石板街道尽显古镇的静谧安详。</p><p><br/></p><p>当然了，这里也是前段时间很火的一部电视剧《一代枭雄》故事里的地方，你可以去曾经繁华的回龙场看看，感受当年何辅堂的气势！</p><p><br/></p><p>特色美食：豆浆稀饭、菜豆腐、腊肉、豆干、魔芋、酸菜、厥菜、热面皮、核桃饼、及农家“七大碗”等。</p><p><br/></p><p><img src="/ueditor/php/upload/image/20170919/1505829541840066.jpg" title="1505829541840066.jpg" alt="43188554_14.jpg"/></p><p><br/></p><p>自驾路线：从西安出发，乘火车可选择开往四川方面的多列火车，到阳平关站下车，车程约10小时;然后转乘阳平关到青木川的长途汽车，车程3小时。</p><p><br/></p><p>乘车路线：自驾车建议走西宝高速公路，具体路线为西安—眉县—太白—南河—马道—石门—汉中东—宁强—七里坝—阳平关—燕子砭—广坪—青木川，全程550公里</p><p><br/></p>', 'add', 1, 1, '130', 0);
INSERT INTO `tp_message` (`id`, `title`, `time`, `content`, `author`, `cid`, `hit`, `top_img_id`, `collect`) VALUES
(125, '陕西 袁家村', '2017-09-19', '<p><img src="/ueditor/php/upload/image/20170919/1505829593116968.jpg" title="1505829593116968.jpg" alt="43188554_29.jpg"/><img src="/ueditor/php/upload/image/20170919/1505829593602961.jpg" title="1505829593602961.jpg" alt="43188554_28.jpg"/></p><p>袁家村座落在举世闻名的唐太宗李世民陵山下，处在西咸半小时经济圈内，312国道、银武高速、陇海铁路近在咫尺、107省道、关中旅游环线、唐昭陵旅游专线从附近经过，交通十分便利，是目前最受欢迎的乡村旅游地。</p><p><br/></p><p>特色美食：当地的特色农家饭、卢记豆腐 、醪 糟 、蜂蜜、花粉 、麦 面 、蝎子酒等。</p><p><br/></p>', 'add', 1, 1, '131', 0),
(126, '陕西 周村', '2017-09-19', '<p></p><p>周村古镇旱码头原名周村古商城，素有“天下第一村”之称，位于山东省中部，属山东省淄博市辖区之一。</p><p>早在明末清初，周村作为商业名镇，与中国南方的佛山、景德镇、朱仙镇齐名，成为无水路相通的全国四大旱码头之一。1904年5月19日（清光绪三十年四月初五）周村正式被清政府批准开辟为商埠，使得周村商业更加繁荣。</p><p>周村旱码头主要由大街、丝市街、银子市街等古街组成，现有保存完好的明清古建筑50000余平方米，古镇内古迹众多、店铺林立、街区纵横，建筑风格迥异，为山东仅有、江北罕见，且至今仍在发挥其商业功能，被中国古建筑委员会的专家誉为“中国活着的古商业建筑博物馆群”，现为省级重点文物保护单位，被列入山东省历史优秀建筑名单，并作为山东省“文化历史与民俗”旅游区的开发重点被列入“山东省旅游发展总体规划”，是以体现鲁中商业文化精髓为主要内容的全省重点民俗旅游项目，是我国江北保存较完好的明清古商业街区，体现了浑厚的古商业文化。</p><p><br/></p>', 'add', 1, 7, '132', 0);
